INSERT INTO `about_us` (`id`, `title`, `detail`, `status`, `created`) VALUES
(1, 'TẦM NHÌN SỨ MỆNH EVAS', '<p>Với hơn 10 năm đồng h&agrave;nh tr&ecirc;n h&agrave;nh tr&igrave;nh đ&agrave;o tạo tiếng Anh, Trung t&acirc;m ngoại ngữ quốc tế &Acirc;u Việt &Uacute;c khẳng định được chất lượng đ&agrave;o tạo v&agrave; c&oacute; được niềm tin tuyệt đối từ phụ huynh học vi&ecirc;n, ch&uacute;ng t&ocirc;i thấu hiểu được kỳ vọng của phụ huynh v&agrave; học vi&ecirc;n <strong>chinh phục tiếng Anh để vươn tới những th&agrave;nh c&ocirc;ng trong tương lai</strong>.</p>\r\n\r\n<p>Sứ mệnh của ch&uacute;ng t&ocirc;i trong h&agrave;nh tr&igrave;nh n&agrave;y l&agrave;&nbsp;<strong>mang đến chương tr&igrave;nh đ&agrave;o tạo tiếng Anh tốt nhất, hiệu quả nhất cho từng người học</strong>. V&agrave; đ&iacute;ch đến kh&ocirc;ng chỉ l&agrave; tốt nghiệp Chứng chỉ Anh văn m&agrave; c&aacute;c em c&ograve;n c&oacute; <strong>đủ bản lĩnh bước l&ecirc;n nấc thang cao hơn - trở th&agrave;nh một phần của m&ocirc;i trường gi&aacute;o dục ti&ecirc;n tiến ở&nbsp;bất kỳ quốc gia n&agrave;o tr&ecirc;n to&agrave;n Thế giới</strong>.</p>\r\n\r\n<p>Để khẳng định chất lượng đ&agrave;o tạo v&agrave; ho&agrave;n th&agrave;nh sứ mệnh đ&oacute;, <strong>Trung t&acirc;m đ&atilde; kh&ocirc;ng ngừng cải tiến, lu&ocirc;n ti&ecirc;n phong trong c&ocirc;ng t&aacute;c đ&agrave;o tạo tiếng anh trong suốt hơn 10 năm qua.</strong> C&aacute;c chương tr&igrave;nh đ&agrave;o tạo tại EVAS từ anh văn Mẫu Gi&aacute;o, Anh Văn Thiếu Nhi, Chứng chỉ quốc tế CAMBRIDGE Starters-Movers-Flyers, Anh Văn Thiếu&nbsp;Ni&ecirc;n (KET, PET), Anh Văn Giao tiếp, c&aacute;c lớp &ocirc;n luyện TOEIC, IELTS, A1, A2, B1, B2 v&agrave; C1...</p>\r\n\r\n<p>Chương tr&igrave;nh được bi&ecirc;n soạn thực tế, hiệu quả, c&ugrave;ng với đội ngũ gi&aacute;o vi&ecirc;n gi&agrave;u t&acirc;m huyết, đam m&ecirc; đ&agrave;o tạo ph&aacute;t triển con người<strong> được c&aacute;c chuy&ecirc;n gia h&agrave;ng đầu của Anh đ&agrave;o tạo h&agrave;ng năm, phương ph&aacute;p dạy trực quan sinh động t&iacute;ch hợp c&aacute;c phần mềm c&ocirc;ng nghệ 4.0.</strong> Bởi<em> lấy hoc sinh l&agrave;m trung t&acirc;m</em> l&agrave; phương ph&aacute;p v&agrave; ti&ecirc;u ch&iacute; dạy của Trung t&acirc;m, v&igrave; thế những buổi học được &aacute;p dụng những hoạt động thuyết tr&igrave;nh, nghi&ecirc;n cứu&nbsp;&yacute; tưởng, dựa tr&ecirc;n những sự kh&aacute;m ph&aacute; c&aacute;c chủ đề xung quanh c&aacute;c em. C&ugrave;ng với những buổi tăng cường hoạt động ngoại kh&oacute;a, gi&uacute;p c&aacute;c em ph&aacute;t triển hơn những&nbsp;kỹ năng phản xạ v&agrave; đưa kiến thức đ&atilde; được học ứng dụng v&agrave;o cuộc sống thực tế gi&uacute;p c&aacute;c em tự tin hơn trong giao tiếp bằng&nbsp;tiếng Anh.</p>\r\n\r\n<p>Mỗi chương tr&igrave;nh đ&agrave;o được Trung t&acirc;m nghi&ecirc;n cứu thật kỹ kh&ocirc;ng chỉ gi&uacute;p c&aacute;c em học tiếng Anh hiệu quả, những kiến thức được t&iacute;ch lũy trong từng b&agrave;i học với mong muốn c&aacute;c em ho&agrave;n&nbsp;thiện hơn từng ng&agrave;y. Đối với chương tr&igrave;nh<strong> tiếng Anh Mẫu gi&aacute;o</strong>, c&aacute;c b&eacute; được học c&aacute;c kỹ năng mềm, c&aacute;c h&agrave;nh vi ứng xử đ&uacute;ng mực gi&uacute;p c&aacute;c b&eacute; ngoan hơn, năng động hơn, th&ocirc;ng minh v&agrave; đặc biệt khơi nguồn cho sự đam m&ecirc; học tiếng Anh của b&eacute;.</p>\r\n\r\n<p>Song song với những kh&oacute;a học giao tiếp d&agrave;nh cho sinh vi&ecirc;n v&agrave; người đi l&agrave;m, chương tr&igrave;nh đ&agrave;o tạo tại EVAS được x&acirc;y dựng to&agrave;n diện li&ecirc;n cấp từ Mầm mon đến Phổ th&ocirc;ng v&agrave; c&aacute;c Chứng chỉ quốc tế. <strong>Chương tr&igrave;nh đ&agrave;o tạo tại EVAS đặc trưng bởi việc dạy v&agrave; học tiếng Anh trong m&ocirc;i trường 100% sử dụng ng&ocirc;n ngữ tiếng anh bởi Gi&aacute;o vi&ecirc;n người Việt v&agrave; Nước ngo&agrave;i theo chứng nhận chuẩn từ Đại học Cambridge.</strong></p>\r\n\r\n<p>Với đội ngũ Giảng vi&ecirc;n Việt Nam v&agrave; Giảng vi&ecirc;n Nước ngo&agrave;i c&oacute; nhiều năm&nbsp;kinh nghiệm trong c&ocirc;ng t&aacute;c đ&agrave;o tạo tiếng Anh v&agrave; từng th&agrave;nh vi&ecirc;n đội ngũ Trung t&acirc;m đ&agrave;o tạo giảng dạy bằng t&acirc;m huyết v&agrave; đam m&ecirc;, tr&aacute;ch nhiệm với từng em học vi&ecirc;n, hạnh ph&uacute;c c&ugrave;ng đồng h&agrave;nh với c&aacute;c em từng ng&agrave;y v&agrave; nh&igrave;n thấy c&aacute;c em tự tin giao tiếp v&agrave; th&iacute;ch th&uacute; học anh văn l&agrave; niềm hạnh ph&uacute;c của ch&uacute;ng t&ocirc;i.</p>\r\n\r\n<p>Tiếp bước về tương lai, bằng những cải tiến kh&ocirc;ng ngừng trong phương ph&aacute;p c&ugrave;ng sự nhiệt huyết cao độ trong đội ngũ, ch&uacute;ng t&ocirc;i tự tin dẫn dắt thế hệ hoc sinh tiến bước về ph&iacute;a trước với tương lai thật tươi s&aacute;ng để <em>&lsquo;tạo ra gi&aacute; trị tốt đẹp cho cộng đồng&rdquo; đ&oacute; cũng l&agrave; niềm tin cốt l&otilde;i đội ngũ EVAS ch&uacute;ng t&ocirc;i.</em></p>\r\n\r\n<p style=\"text-align:center\"><strong>Đến EVAS ĐỂ &ldquo;<span style=\"color:#B22222\">SẴN S&Agrave;NG VƯƠN M&Igrave;NH RA THẾ GIỚI</span>&rdquo;</strong></p>\r\n', 1, '2020-03-28 02:54:09');
INSERT INTO `advisory` (`id`, `title`, `image`, `description`, `status`, `created_at`) VALUES
(1, 'Du Học Singapore', 'du-hoc/dh-1.jpg', 'Đảo quốc sư tử xinh đẹp với nền giáo dục chất lượng, bằng cấp được công nhận toàn cầu.', 1, '2020-03-18 14:53:15'),
(2, 'Du Học Philippines', 'du-hoc/dh-2.jpg', 'Mặc dù Philippines là đất nước đang phát triển, nhưng Hệ thống giáo dục tại Philipines lại có những ưu điểm không hề thua kém với những nước tiến bộ bởi sự ảnh hưởng từ nền Giáo dục Mỹ.', 1, '2020-03-18 14:51:09'),
(3, 'Du Học Australia', 'du-hoc/dh-3.jpg', 'Úc là quốc gia khá trẻ so với Việt Nam nhưng nền giáo dục lại ảnh hưởng rất nhiều từ Giáo dục Anh Quốc.', 1, '2020-03-18 14:51:29'),
(4, 'Du Học Pháp', 'du-hoc/dh-4.jpg', 'Cả thế giới biết đến nước Pháp không chỉ bởi vẻ đẹp của những cảnh quan thiên nhiên, bởi vị thế lớn trong nền kinh tế mà còn được biết đến với nền giáo dục chất lượng và tiên tiến.', 1, '2020-03-18 14:51:44'),
(5, 'Du Học Canada', 'du-hoc/dh-5.jpg', 'Canada từ lâu đã nổi tiếng là một quốc gia có nền giáo dục chất lượng, uy tín hàng đầu thế giới và môi trường sống lý tưởng cho du học sinh.', 1, '2020-03-18 14:51:58'),
(6, 'Du Học Anh Quốc', 'du-hoc/dh-6.jpg', 'Là một quốc gia có lịch sử lâu đời tại Châu Âu, với danh tiếng không thể nào phủ nhận được, nền giáo dục của Vương quốc Anh đã là một trụ cột vững chắc cho nền giáo dục thế giới.', 1, '2020-03-18 14:52:12');
INSERT INTO `advisory_blog` (`id`, `title`, `code`, `category_id`, `description`, `detail`, `status`, `created_at`) VALUES
(1, 'Du học có những lợi ích gì?', 'du-hoc-co-nhung-loi-ich-gi', 1, 'Với chương trình học tập và sinh sống ở nước ngoài trong một khoản thời gian ngắn, chương trình du học hè như một chuyến hành trình du học thu nhỏ giúp các bạn nhỏ làm\r\nquen với nhiều điều hấp dẫn và hơn nữa các bạn còn được tiếp cận với môi trường sẽ có trong cuộc sống du học sau này.', '<p><em><strong>1. Trải nghiệm cuộc sống tự lập</strong></em></p>\r\n\r\n<p>Tham gia v&agrave;o chương tr&igrave;nh du học h&egrave; c&aacute;c bạn sẽ học được c&aacute;ch sống độc lập khi kh&ocirc;ng c&oacute;&nbsp;bố mẹ ở b&ecirc;n. C&aacute;c bạn sẽ tự học c&aacute;ch tự chuẩn bị cho m&igrave;nh h&agrave;nh trang trong chuyến đi v&agrave;i&nbsp;ng&agrave;y, học c&aacute;ch tự chăm s&oacute;c bản th&acirc;n khi kh&ocirc;ng c&oacute; bố mẹ kề b&ecirc;n nhắc nhở.</p>\r\n\r\n<p><em><strong>2.N&acirc;ng cao kỹ năng sống v&agrave; trau dồi tiếng Anh</strong></em></p>\r\n\r\n<p>Một điểm nhấn lớn trong những buổi học được thiết kế ri&ecirc;ng cho chương tr&igrave;nh du học h&egrave;&nbsp;l&agrave; việc c&aacute;c bạn sẽ được l&agrave;m quen với phương ph&aacute;p học tập chủ động c&ugrave;ng lối tư duy ph&acirc;n t&iacute;ch - phản biện của c&aacute;c quốc gia như Anh, Singapore, Philippine,&hellip;V&agrave; đ&acirc;y quả l&agrave; một cơ hội tuyệt vời, để c&aacute;c bạn nhỏ được sống trong m&ocirc;i trường n&oacute;i tiếng Anh của người bản địa, trao đổi c&ugrave;ng thầy c&ocirc; , v&agrave; cả bạn b&egrave; quốc tế.</p>\r\n\r\n<p><em><strong>3.Tiền đề cho kế hoạch du học trong tương lai</strong></em></p>\r\n\r\n<p>Chương tr&igrave;nh học&nbsp;c&ograve;n được kết hợp những hoạt động thuyết tr&igrave;nh, l&agrave;m việc nh&oacute;m, sẽ l&agrave; dịp để c&aacute;c bạn được r&egrave;n luyện khả năng tr&igrave;nh b&agrave;y quan điểm, khả năng linh hoạt trong hoạt động nh&oacute;m, l&agrave;m việc c&ugrave;ng c&aacute;c bạn sinh vi&ecirc;n quốc tế, kỹ năng tương t&aacute;c, khả năng thuyết phục v&agrave; đưa ra quyết định. Những kỹ năng n&agrave;y sẽ gi&uacute;p c&aacute;c bạn trở n&ecirc;n chủ động hơn v&agrave; t&iacute;ch cực hơn trong việc học tập v&agrave; cuộc sống của m&igrave;nh.&nbsp;Đ&acirc;y l&agrave; tiền đề quan trọng v&agrave; cần thiết cho những bạn theo đuổi&nbsp;việc học tập ở nước ngo&agrave;i trong tương lai.</p>\r\n', 1, '2020-03-23 02:25:56'),
(3, 'Cần chuẩn bị gì trước khi du học?', 'can-chuan-bi-gi-truoc-khi-du-hoc', 1, 'Nếu xem việc du học là một hành trình chinh phục ngọn núi thì lộ trình chuẩn bị là khóa huấn luyện toàn diện để chuẩn bị cho chính bạn sự bền bỉ và chuyên sâu để theo đuổi đến cùng. Vì nếu thiếu đi sự trang bị đầy đủ, “vận động viên” có thể bỏ cuộc ngay từ chân núi hoặc chỉ leo được một đoạn ngắn tới triền núi.', '<p><em><strong>Điều kiện cần: học lực, tiếng Anh, t&agrave;i ch&iacute;nh</strong></em></p>\r\n\r\n<p>Nếu cầm tr&ecirc;n tay quyển th&ocirc;ng tin kh&aacute;i qu&aacute;t du học c&aacute;c nước của một c&ocirc;ng ty tư vấn du học, bạn sẽ&nbsp;thấy ngo&agrave;i những điều kiện tập trung sẽ c&oacute; 3&nbsp;điều ti&ecirc;n quyết: học lực, tiếng Anh, khả năng t&agrave;i ch&iacute;nh, đ&acirc;y c&oacute; thể gọi n&ocirc;m na l&agrave; ch&igrave;a kh&oacute;a mở cho c&aacute;nh cửa du học.</p>\r\n\r\n<p><em><strong>Điều kiện đủ: tự lập, ch&iacute;nh chắn v&agrave; &quot;l&yacute; lịch sạch&quot;</strong></em></p>\r\n\r\n<p>C&oacute; những trường hợp hội đủ cả 3 điều kiện cần để chuẩn bị du học nhưng vẫn kh&ocirc;ng thể đạt đến hoặc kh&ocirc;ng thể ho&agrave;n th&agrave;nh mục ti&ecirc;u du học. đ&oacute; l&agrave; khi học sinh chưa sẵn s&agrave;ng về t&acirc;m l&yacute;, chưa đạt được&nbsp;&yacute; thức tự lập hoặc bị vướng một số điểm về l&yacute; lịch (lịch sử di tr&uacute; c&ugrave;ng gia đ&igrave;nh lu&acirc;n tu&acirc;n thủ luật, lịch sử tư ph&aacute;p, lịch sử học tập v&agrave; l&agrave;m việc, v&agrave; vấn đề sức khỏe).&nbsp;</p>\r\n\r\n<p>Việc một m&igrave;nh du học nơi xứ người, ho&agrave;n to&agrave;n kh&aacute;c biệt về ng&ocirc;n ngữ v&agrave; văn h&oacute;a&nbsp;đ&ograve;i hỏi du học sinh cần phải c&oacute; đủ bản lĩnh để tự lo cho bản th&acirc;n, tự biết c&aacute;ch sắp xếp v&agrave; ho&agrave;n th&agrave;nh kế hoạch học tập cũng như việc vượt qua được những kh&oacute;&nbsp;khăn v&agrave; thử th&aacute;ch trong qu&aacute; tr&igrave;nh học tập tại nước ngo&agrave;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 1, '2020-03-23 02:23:57'),
(4, 'Du học Anh! Vì sao nên chọn?', 'du-hoc-anh!-vi-sao-nen-chon', 2, 'Tiến xa hơn trên con đường học vấn tại Vương Quốc Anh...', '<p><strong><span style=\"color:#B22222\"><span style=\"font-size:18px\">V&Igrave; SAO N&Ecirc;N CHỌN DU HỌC ANH?</span></span></strong></p>\r\n\r\n<p><strong><em>Việc chọn du học tại một trường đại học của Vương quốc Anh cũng cho bạn nhiều ưu thế về t&agrave;i ch&iacute;nh, văn h&oacute;a v&agrave; x&atilde; hội. Một cấu tr&uacute;c chương tr&igrave;nh ngắn hơn, chuy&ecirc;n s&acirc;u hơn đồng nghĩa với việc bạn sẽ c&oacute; thể theo đuổi sự nghiệp nhanh ch&oacute;ng hơn v&agrave; cắt giảm tối đa c&aacute;c chi ph&iacute; sinh hoạt của m&igrave;nh trong l&uacute;c bạn đi học. Vương quốc Anh c&ograve;n c&oacute; một loạt c&aacute;c chương tr&igrave;nh được thiết kế nhằm cung cấp cho sinh vi&ecirc;n c&aacute;c khoản giảm gi&aacute; v&agrave; cơ hội tiếp cận dịch vụ vận chuyển c&ocirc;ng cộng rẻ v&agrave; những dụng cụ học tập cần thiết.</em></strong></p>\r\n\r\n<p>Đ&acirc;y l&agrave; năm l&yacute; do ch&iacute;nh để c&aacute;c sinh vi&ecirc;n c&acirc;n nhắc lựa chọn Vương quốc Anh.</p>\r\n\r\n<p><span style=\"color:#B22222\"><em><strong>1. Anh quốc l&agrave; c&aacute;i n&ocirc;i tiếng Anh của Thế giới</strong></em></span></p>\r\n\r\n<p>Anh quốc l&agrave; qu&ecirc; hương của tiếng Anh, l&agrave; nơi m&agrave; từ đ&acirc;y những người Anh đem tiếng Anh c&ugrave;ng nền văn h&oacute;a, khoa học&hellip; đến nhiều nước kh&aacute;c để lập nghiệp. Tại c&aacute;c quốc gia n&agrave;y, ng&agrave;y nay, tiếng Anh trở th&agrave;nh quốc ngữ v&agrave; c&ugrave;ng với người Anh th&igrave; người Mỹ, &Uacute;c, New Zealand, Canada trở th&agrave;nh những người bản xứ n&oacute;i tiếng Anh.</p>\r\n\r\n<p>V&agrave; khi tiếng Anh trở th&agrave;nh ng&ocirc;n ngữ quốc tế th&igrave; kh&ocirc;ng g&igrave; tốt hơn l&agrave; đến đất nước được coi l&agrave; c&aacute;i n&ocirc;i của tiếng Anh n&agrave;y để học tập v&agrave; trải nghi&ecirc;̣m trong m&ocirc;i trường tiếng Anh chuẩn mực nhất.</p>\r\n\r\n<p><em><strong><span style=\"color:#B22222\">2. Chất lượng Gi&aacute;o dục h&agrave;ng đầu Thế giới</span></strong></em></p>\r\n\r\n<p>Vương quốc Anh thỏa m&atilde;n những yếu tố ti&ecirc;n quyết trong c&aacute;c nhu cầu của nhiều sinh vi&ecirc;n v&agrave; sở hữu một v&agrave;i trong số những trường đại học tốt nhất thế giới, bao gồm Cambridge v&agrave; Oxford. Chất lượng đ&agrave;o tạo được kiểm s&aacute;t chặt chẽ bởi Ofsted nơi chuy&ecirc;n đ&aacute;nh gi&aacute;, xếp loại một c&aacute;ch to&agrave;n diện c&aacute;c đơn vị đ&agrave;o tạo.</p>\r\n\r\n<p>Bằng cấp của Anh lu&ocirc;n được c&ocirc;ng nhận v&agrave; đ&aacute;nh gi&aacute; cao tr&ecirc;n to&agrave;n cầu. Với tấm bằng đại học tại Anh Quốc, du học sinh c&oacute; thể học th&ecirc;m l&ecirc;n hoặc xin việc l&agrave;m tại bất cứ nước n&agrave;o tr&ecirc;n thế giới. Những cựu du học sinh Anh quốc cũng l&agrave; những người được giới tuyển dụng ưa chuộng, do họ c&oacute; kiến thức cơ bản tốt, kỷ luật l&agrave;m việc cao v&agrave; kĩ năng mềm đảm bảo cần thiết để hỗ trợ cho c&ocirc;ng việc th&agrave;nh c&ocirc;ng.</p>\r\n\r\n<p><span style=\"color:#B22222\"><strong><em>3. Thời gian học tại Anh ngắn</em></strong></span></p>\r\n\r\n<p>So với nhiều nước kh&aacute;c, thời gian học tại Anh được coi l&agrave; ngắn: bậc cử nh&acirc;n được đ&agrave;o tạo trong 3 năm v&agrave; bậc thạc sỹ được đ&agrave;o tạo trong 01 năm. Điều n&agrave;y gi&uacute;p c&aacute;c du học sinh tiết kiệm thời gian, tiền bạc, ra trường sớm v&agrave; sớm tiếp cận c&aacute;c cơ hội việc l&agrave;m, thu nhập cao.</p>\r\n\r\n<p><span style=\"color:#B22222\"><strong><em>4. Văn h&oacute;a</em></strong></span></p>\r\n\r\n<p>Kh&ocirc;ng nhiều nơi c&oacute; một nền văn h&oacute;a đa dạng như Vương quốc Anh. Với sự pha trộn của nhiều cộng đồng, nhiều chủng tộc v&agrave; một nền lịch sử l&acirc;u đời v&agrave; phong ph&uacute; chắc chắn đ&acirc;y sẽ l&agrave; một nơi tuyệt vời để bạn kh&aacute;m ph&aacute;.</p>\r\n\r\n<p><span style=\"color:#B22222\"><em><strong>5. Vương quốc Anh c&oacute; vị tr&iacute; địa l&yacute; l&yacute; tưởng</strong></em></span></p>\r\n\r\n<p>Vương quốc Anh nằm ở vị tr&iacute; l&yacute; tưởng cho việc thực hiện những chuyến du lịch ngắn đến c&aacute;c quốc gia kh&aacute;c. Từ London, bạn c&oacute; thể đi đến nhiều địa điểm kh&aacute;c ở ch&acirc;u &Acirc;u m&agrave; chỉ mất v&agrave;i giờ, một l&yacute; do khiến th&agrave;nh phố n&agrave;y trở th&agrave;nh một trung t&acirc;m du lịch tuyệt vời.</p>\r\n\r\n<p><span style=\"color:#B22222\"><strong><em>6. Sinh vi&ecirc;n được ở lại 02 năm l&agrave;m việc sau khi tốt nghiệp</em></strong></span></p>\r\n\r\n<p>(Link tham khảo: <a href=\"https://l.facebook.com/l.php?u=http%3A%2F%2Fwww.ukcisa.org.uk%2FInformation%E2%80%93Advice%2FWorking%2FWorking-after-studies%3Ffbclid%3DIwAR1WWYs06SLdJ5eSnh1a3VWuxJkGgomXbMtl4hm8UPHeFjKohlAhI9e2bjg&amp;h=AT22dLsutKYhacscclvfA519TXzaFIIP1O3wHgoXQM3IPxu6BZc8iDTngt-suwbCktvbUTlm8EZ__pMfZmE-9DWqFMvrc-IYj62gkAqmZNzFn5baumK_L6d-1StO36FBa4h_&amp;__tn__=-UK-R&amp;c[0]=AT1YvRSc3W40sLPuCWQIIE3b7D5qTCOYEio7xFT54f1_nJexRM-CImWON6ZEjCZ2w36QIwvPG4Uji_ajDKv0EnVbHv5DjJvofHfQ_iM6IiLiMoaw-ZhY_v55WxYhjbQ0t_WAiA0dZ-dqk0X0iel9ZHs9BbEJ6KuqtkhUh7leNF1q6cbTnFUjhzq_vmbGVG_k-A-L\" rel=\"nofollow noopener\" tabindex=\"0\" target=\"_blank\">www.ukcisa.org.uk/Information&ndash;Advice/Working/Working-after-studies</a>)</p>\r\n\r\n<p>Ch&iacute;nh s&aacute;ch Visa Anh mới được Thủ tướng c&ocirc;ng bố cho ph&eacute;p du học sinh ở lại quốc gia n&agrave;y trong v&ograve;ng 2 năm. Cụ thể, từ năm 2021, sinh vi&ecirc;n quốc tế khi theo học bậc đại học, sau đại học hoặc tiến sĩ ở Anh sẽ cho ph&eacute;p sinh vi&ecirc;n đủ điều kiện l&agrave;m việc hoặc t&igrave;m kiếm việc l&agrave;m, trong bất cứ ng&agrave;nh nghề hoặc vị tr&iacute; n&agrave;o m&agrave; sinh vi&ecirc;n chọn trong v&ograve;ng 2 năm sau khi ho&agrave;n th&agrave;nh kh&oacute;a học. Chiến lược n&agrave;y được cho rằng nhằm th&uacute;c đẩy sự thu h&uacute;t của du học Anh, coi xứ sở sương m&ugrave; l&agrave; điểm đến du học tuyệt vời.</p>\r\n\r\n<p>Sinh vi&ecirc;n c&oacute; 2 năm để t&igrave;m việc l&agrave;m, nếu sở hữu được c&ocirc;ng việc tốt v&agrave; nhận được lời mời từ nh&agrave; tuyển dụng, sinh vi&ecirc;n sẽ dễ d&agrave;ng hơn rất nhiều để c&oacute; working visa v&agrave; cơ hội l&agrave;m việc l&acirc;u d&agrave;i tại Anh.</p>\r\n\r\n<p><em>(Tham khảo nguồn IDP)</em></p>\r\n\r\n<p><span style=\"color:#B22222\"><strong><em>ANH NGỮ QUỐC TẾ &Acirc;U VIỆT &Uacute;C</em></strong></span></p>\r\n', 1, '2021-02-19 11:22:20'),
(5, 'Tại sao nên Du học Anh bậc Cử nhân?', 'tai-sao-nen-du-hoc-anh-bac-cu-nhan', 2, 'Du học tại một trường đại học tại Vương quốc Anh mang lại nhiều lợi thế về cấu trúc khóa học ngắn và chuyên sâu hơn cho phép sinh viên theo dõi và phát triển sự nghiệp một cách nhanh hơn và giảm thiểu chi phí sinh hoạt trong giai đoạn học tập...', '<p><span style=\"font-size:18px\"><strong><em>TẠI SAO N&Ecirc;N DU HỌC ANH BẬC CỬ NH&Acirc;N ?</em></strong></span></p>\r\n\r\n<p><span style=\"color:#B22222\"><strong><em>Tiến xa hơn tr&ecirc;n con đường học vấn tại Vương Quốc Anh...</em></strong></span></p>\r\n\r\n<p><span style=\"color:#B22222\"><strong><em><img alt=\"\" src=\"/upload/images/du-hoc/du%20hoc%20anh.jpg\" style=\"height:467px; width:700px\" /></em></strong></span></p>\r\n\r\n<p>Nhận bằng tại Vương quốc Anh (Anh) l&agrave; một c&aacute;ch tuyệt vời để củng cố v&agrave; ph&aacute;t triển chuy&ecirc;n m&ocirc;n của bạn v&agrave; tạo n&ecirc;n những kết nối l&acirc;u d&agrave;i trong một m&ocirc;i trường năng động v&agrave; đa văn h&oacute;a. C&oacute; một loạt c&aacute;c th&agrave;nh phố, thị trấn v&agrave; l&agrave;ng qu&ecirc; quốc tế ở c&aacute;c v&ugrave;ng của Vương quốc Anh, cụ thể l&agrave; Anh, Scotland, Bắc Ireland v&agrave; xứ Wales, v&agrave; hơn 50.000 kh&oacute;a học trong hơn 25 m&ocirc;n học c&oacute; sẵn.</p>\r\n\r\n<p>Du học tại một trường đại học tại Vương quốc Anh cũng mang lại nhiều lợi thế như cấu tr&uacute;c kh&oacute;a học ngắn hơn v&agrave; chuy&ecirc;n s&acirc;u hơn cho ph&eacute;p sinh vi&ecirc;n theo d&otilde;i v&agrave; ph&aacute;t triển sự nghiệp một c&aacute;ch nhanh hơn v&agrave; giảm thiểu chi ph&iacute; sinh hoạt trong giai đoạn học tập. Theo truyền thống, Vương quốc Anh cung cấp chương tr&igrave;nh đại học 3 năm v&agrave; chương tr&igrave;nh thạc sĩ 1 năm tại c&aacute;c trường đại học. Cơ hội học tập sau đại học chất lượng cao cũng c&oacute; sẵn ở phần lớn c&aacute;c trường đại học tại Vương quốc Anh, với một số trường đại học cung cấp t&agrave;i trợ để gia hạn visa cấp 4.</p>\r\n\r\n<p>Vương quốc Anh cũng c&oacute; sự đa dạng phong ph&uacute; về văn h&oacute;a, ng&ocirc;n ngữ v&agrave; t&iacute;n ngưỡng. C&aacute;c trường đại học c&oacute; một loạt c&aacute;c c&acirc;u lạc bộ v&agrave; x&atilde; hội đảm bảo một cuộc sống x&atilde; hội s&ocirc;i động cho sinh vi&ecirc;n, v&agrave; bạn sẽ được trải nghiệm nhiều nền ẩm thực v&ocirc; c&ugrave;ng đa dạng, hệ thống giao th&ocirc;ng mạnh mẽ, c&aacute;c lễ hội &acirc;m nhạc nổi tiếng v&agrave; c&aacute;c giải v&ocirc; địch thể thao quốc tế trong suốt những năm sinh vi&ecirc;n của bạn.</p>\r\n\r\n<p><span style=\"color:#B22222\"><strong>Tại sao n&ecirc;n du học Anh bậc Cử nh&acirc;n?</strong></span></p>\r\n\r\n<ul>\r\n	<li><strong>Nền gi&aacute;o dục được quốc tế c&ocirc;ng nhận: </strong>Vương quốc Anh l&agrave; nơi c&oacute; một số trường đại học tốt nhất tr&ecirc;n thế giới, bao gồm Cambridge v&agrave; Oxford.</li>\r\n	<li><strong>Được tiếp cận c&aacute;c dịch vụ chăm s&oacute;c sức khỏe:</strong> Dịch vụ y tế quốc gia (NHS) ở Anh cung cấp cho sinh vi&ecirc;n quốc tế quyền truy cập chăm s&oacute;c sức khỏe miễn ph&iacute; tại địa phương khi thanh to&aacute;n Phụ ph&iacute; y tế quốc tế (IHS).</li>\r\n	<li><strong>Nền kinh tế:</strong> Vương quốc Anh l&agrave; nền kinh tế lớn thứ năm tr&ecirc;n thế giới với GDP 2,62 ngh&igrave;n tỷ đ&ocirc; la.</li>\r\n	<li><strong>Sự đa dạng về văn h&oacute;a:</strong> C&oacute; rất &iacute;t nơi c&oacute; nền văn h&oacute;a phong ph&uacute; giống như Vương quốc Anh. Với đa d&acirc;n tộc v&agrave; một lịch sử l&acirc;u d&agrave;i v&agrave; th&uacute; vị, đ&acirc;y l&agrave; một nơi hấp dẫn để bạn kh&aacute;m ph&aacute;.</li>\r\n	<li><strong>Vị tr&iacute; địa l&yacute;:</strong> Vương quốc Anh chỉ c&aacute;ch một v&agrave;i giờ bay đến c&aacute;c điểm đến ch&acirc;u &Acirc;u kh&aacute;c nhau, l&agrave;m cho n&oacute; trở th&agrave;nh một địa điểm trung t&acirc;m du lịch l&yacute; tưởng.</li>\r\n</ul>\r\n\r\n<p><em>(Nguồn IDP)</em></p>\r\n\r\n<p>Hỗ trợ hồ sơ du học bậc Đại học tại Anh Quốc xin vui l&ograve;ng li&ecirc;n hệ:</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"color:#B22222\"><strong><em>&Acirc;U VIỆT &Uacute;C - IDP IELTS - DU HỌC ANH</em></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><a href=\"https://www.facebook.com/auvietucieltsduhoc\"><span style=\"color:#B22222\"><strong><em>https://www.facebook.com/auvietucieltsduhoc</em></strong></span></a></span></p>\r\n', 1, '2021-02-19 11:07:59'),
(6, 'Tại sao nên Du học Anh bậc Thạc Sỹ?', 'tai-sao-nen-du-hoc-anh-bac-thac-sy', 2, 'Sự phong phú và đa dạng của các nền văn hóa là một sức hút rất lớn, với các thành phố quốc tế như London và Glasgow đến các quận lịch sử như Warwickshire và Yorkshire, chỉ cách một chuyến tàu cho dù bạn chọn lấy bằng tại bất cứ nơi nào...', '<p><span style=\"color:#B22222\"><em><strong>Lấy tấm bằng Thạc Sĩ m&agrave; bạn muốn tại Vương quốc Anh...</strong></em></span></p>\r\n\r\n<p><em><strong>Gi&aacute; trị của tấm bằng Thạc sĩ tại Anh quốc c&oacute; gi&aacute; trị cao hơn rất nhiều v&agrave; thời gian học ph&ugrave; hợp sẽ gi&uacute;p bạn bắt kịp sự nghiệp của m&igrave;nh&nbsp;sau Đại học...</strong></em></p>\r\n\r\n<p><span style=\"color:#B22222\"><em><strong><img alt=\"\" src=\"/upload/images/du-hoc/du%20hoc%20thac%20sy%20anh.jpg\" style=\"height:434px; width:650px\" /></strong></em></span></p>\r\n\r\n<p>Bằng thạc sĩ tại Vương quốc Anh (Anh), bao gồm c&aacute;c kh&oacute;a học như MA, MSc, MRes, MBA v&agrave; LLM, thường chỉ k&eacute;o d&agrave;i trong một năm, cho ph&eacute;p sinh vi&ecirc;n sau đại học bắt kịp sự nghiệp nhanh hơn trong khoảng thời gian ngắn hơn so với c&aacute;c quốc gia kh&aacute;c. Vương quốc Anh l&agrave; nơi c&oacute; nhiều trường đại học h&agrave;ng đầu như Đại học Oxford, Đại học Cambridge, Đại học Ho&agrave;ng gia Lu&acirc;n Đ&ocirc;n, Đại học Queen Mary ở Lu&acirc;n Đ&ocirc;n v&agrave; Đại học Edinburgh.</p>\r\n\r\n<p>Sự phong ph&uacute; v&agrave; đa dạng của c&aacute;c nền văn h&oacute;a l&agrave; một sức h&uacute;t rất lớn, với c&aacute;c th&agrave;nh phố quốc tế như London v&agrave; Glasgow đến c&aacute;c quận lịch sử như Warwickshire v&agrave; Yorkshire, chỉ c&aacute;ch một chuyến t&agrave;u cho d&ugrave; bạn chọn lấy bằng tại bất cứ nơi n&agrave;o.</p>\r\n\r\n<p>Dưới đ&acirc;y l&agrave; một số l&yacute; do khiến Vương quốc Anh l&agrave; nơi tuyệt vời để bạn lấy tấm bằng Thạc Sĩ của m&igrave;nh:</p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#000080\"><strong>C&aacute;c trường đại học nổi tiếng quốc tế:</strong></span> Vương quốc Anh l&agrave; một trong ba điểm đến du học h&agrave;ng đầu thế giới v&agrave; l&agrave; nơi c&oacute; nhiều trường đại học h&agrave;ng đầu như Đại học Oxford, Đại học Cambridge, Đại học Ho&agrave;ng gia Lu&acirc;n Đ&ocirc;n v&agrave; Đại học Edinburgh.</li>\r\n	<li><strong><span style=\"color:#000080\">Chất lượng gi&aacute;o dục:</span></strong> C&aacute;c chương tr&igrave;nh của Vương quốc Anh được quốc tế c&ocirc;ng nhận v&agrave; họ nổi tiếng với chất lượng giảng dạy v&agrave; nghi&ecirc;n cứu</li>\r\n	<li><span style=\"color:#000080\"><strong>L&agrave;m việc v&agrave; học tập:</strong></span> C&aacute;c chương tr&igrave;nh thạc sĩ tại Vương quốc Anh bao gồm một kh&oacute;a thực tập cho sinh vi&ecirc;n để c&oacute; được c&aacute;i nh&igrave;n s&acirc;u sắc v&agrave; kết nối v&ocirc; gi&aacute; trong ng&agrave;nh. Sinh vi&ecirc;n cũng được ph&eacute;p l&agrave;m việc với thị thực sinh vi&ecirc;n, với sự linh hoạt hơn được trao cho sinh vi&ecirc;n từ EU.</li>\r\n	<li><span style=\"color:#000080\"><strong>Học bổng:</strong></span> Nhiều chương tr&igrave;nh học bổng d&agrave;nh cho sinh vi&ecirc;n quốc tế từ c&aacute;c trường đại học n&oacute;i ri&ecirc;ng v&agrave; sinh vi&ecirc;n từ c&aacute;c quốc gia Khối thịnh vượng n&oacute;i chung.</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#B22222\"><em><strong>CHỌN TRƯỜNG</strong></em></span></p>\r\n\r\n<p>Dưới đ&acirc;y l&agrave; một số điều quan trọng cần ch&uacute; &yacute; khi chọn trường đại học của bạn:</p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#006400\"><strong><em>Chương tr&igrave;nh học tập xuất sắc</em></strong></span> - Nghi&ecirc;n cứu về đ&aacute;nh gi&aacute; v&agrave; gi&aacute;o sư giảng dạy c&oacute; thể cung cấp c&aacute;i nh&igrave;n s&acirc;u sắc c&oacute; gi&aacute; trị về chương tr&igrave;nh giảng dạy b&ecirc;n cạnh c&aacute;c m&ocirc; tả kh&oacute;a học. Một trải nghiệm tốt c&oacute; thể vượt ra khỏi ranh giới gi&aacute;o dục truyền thống v&agrave; cung cấp hỗ trợ giảng dạy tuyệt vời cho con đường bạn muốn theo đuổi.</li>\r\n	<li><span style=\"color:#006400\"><strong><em>Địa điểm</em></strong></span> - C&oacute; một loạt c&aacute;c th&agrave;nh phố, thị trấn v&agrave; l&agrave;ng qu&ecirc; tr&ecirc;n khắp l&atilde;nh thổ của Vương quốc Anh, cụ thể l&agrave; Anh, Scotland, Bắc Ireland v&agrave; xứ Wales. Loại lối sống bạn muốn sẽ l&agrave; một yếu tố trong việc lựa chọn nơi bạn muốn học tập.</li>\r\n	<li><strong><em><span style=\"color:#006400\">Học ph&iacute; v&agrave; học bổng</span></em></strong> - Sinh vi&ecirc;n quốc tế ngo&agrave;i EU v&agrave; EEA (Khu vực kinh tế ch&acirc;u &Acirc;u) đ&oacute;ng học ph&iacute; đầy đủ tại c&aacute;c trường đại học Vương quốc Anh. Kiểm tra điều kiện nhận học bổng của bạn v&agrave; xem x&eacute;t việc thu&ecirc; một cố vấn c&oacute; kinh nghiệm để hướng dẫn bạn trong qu&aacute; tr&igrave;nh nộp đơn.</li>\r\n	<li><span style=\"color:#006400\"><strong><em>Chi ph&iacute; sinh hoạt</em></strong></span> - Lu&acirc;n Đ&ocirc;n v&agrave; c&aacute;c th&agrave;nh phố lớn kh&aacute;c c&oacute; xu hướng đắt đỏ hơn, nhưng cũng thuận tiện hơn về mặt h&agrave;ng ng&agrave;y của bạn.</li>\r\n	<li><strong><em><span style=\"color:#006400\">Hỗ trợ sinh vi&ecirc;n quốc tế</span></em></strong> - T&igrave;m một trường đại học cung cấp hỗ trợ theo kế hoạch tốt cho sinh vi&ecirc;n quốc tế, v&igrave; họ c&oacute; thể tư vấn về c&aacute;c yếu tố cần thiết như chỗ ở, ph&uacute;c lợi sinh vi&ecirc;n v&agrave; cơ hội việc l&agrave;m sau đại học.</li>\r\n</ul>\r\n\r\n<p><em>(Nguồn IDP)</em></p>\r\n\r\n<p><span style=\"color:#B22222\"><span style=\"font-size:18px\"><strong><em>&Acirc;U VIỆT &Uacute;C - IDP IELTS - DU HỌC ANH</em></strong></span></span></p>\r\n\r\n<p><a href=\"https://www.facebook.com/auvietucieltsduhoc\"><strong><em>https://www.facebook.com/auvietucieltsduhoc</em></strong></a></p>\r\n', 1, '2021-02-24 04:11:47');
