INSERT INTO `education_program` (`id`, `title`, `title1`, `code`, `image`, `description`, `description1`, `detail`, `status`, `sort`, `created_at`) VALUES
(2, 'Anh Văn Mẫu Giáo (EVAS Stars)', 'DÀNH CHO CÁC BÉ TỪ 4 ĐẾN 6 TUỔI', 'anh-van-mau-giao-evas-stars', 'chuong-trinh-dao-tao/daikin-air-conditioning.png', 'Chương trình đặc biệt do trung tâm biên soạn cho các bé trong độ tuổi này, được thiết kế với các chủ đề xung quanh hằng ngày của các bé nên giúp các bé tiếp nhận tiếng Anh một cách dễ dàng. Nhận thấy sự phản xạ và kỹ năng nghe nói, phát âm rất quan trong đối với các bé độ tuổi này. Nên Giáo viên giảng dạy sử dụng 100% tiếng Anh mong muốn đem lại hiệu quả tối ưu từng buổi học cho các bé và khơi dậy niềm đam mê tiếng Anh ở trẻ. ', '<p>Chương tr&igrave;nh đặc biệt do trung t&acirc;m bi&ecirc;n soạn cho c&aacute;c b&eacute; trong độ tuổi n&agrave;y, được thiết kế với c&aacute;c chủ đề xung quanh hằng ng&agrave;y của c&aacute;c b&eacute; n&ecirc;n gi&uacute;p c&aacute;c b&eacute; tiếp nhận tiếng Anh một c&aacute;ch dễ d&agrave;ng. Nhận thấy sự phản xạ v&agrave; kỹ năng nghe n&oacute;i, ph&aacute;t &acirc;m rất quan trong đối với c&aacute;c b&eacute; độ tuổi n&agrave;y. N&ecirc;n Gi&aacute;o vi&ecirc;n giảng dạy sử dụng 100% tiếng Anh mong muốn đem lại hiệu quả tối ưu từng buổi học cho c&aacute;c b&eacute; v&agrave; khơi dậy niềm đam m&ecirc; tiếng Anh ở trẻ.</p>\r\n', '<div class=\"noidung\">\r\n<p>Đ&acirc;y l&agrave; chương tr&igrave;nh đặc biệt, Trung t&acirc;m x&acirc;y dựng tối đa h&oacute;a t&agrave;i năng ng&ocirc;n ngữ của mỗi b&eacute;. Chương tr&igrave;nh được thiết kế với c&aacute;c chủ đề xung quanh hằng ng&agrave;y của c&aacute;c b&eacute; n&ecirc;n gi&uacute;p c&aacute;c b&eacute; tiếp nhận tiếng Anh một c&aacute;ch dễ d&agrave;ng. Nhận thấy sự phản xạ v&agrave; kỹ năng nghe n&oacute;i, ph&aacute;t &acirc;m rất quan trong đối với c&aacute;c b&eacute; độ tuổi n&agrave;y. N&ecirc;n Gi&aacute;o vi&ecirc;n giảng dạy sử dụng 100% tiếng Anh mong muốn đem lại hiệu quả tối ưu từng buổi học cho c&aacute;c b&eacute; v&agrave; khơi dậy niềm đam m&ecirc; tiếng Anh ở trẻ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>LỊCH HỌC V&Agrave; THỜI GIAN HỌC</strong></p>\r\n\r\n<table align=\"center\" border=\"1\" cellspacing=\"0\" style=\"width:626px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:165px\">\r\n			<p><strong>LỊCH HỌC</strong></p>\r\n			</td>\r\n			<td style=\"width:237px\">\r\n			<p><strong>Thứ hai &ndash; tư - s&aacute;u</strong></p>\r\n			</td>\r\n			<td style=\"width:224px\">\r\n			<p><strong>Thứ ba &ndash; năm &ndash; bảy</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:165px\">\r\n			<p><strong>TỐI</strong></p>\r\n			</td>\r\n			<td style=\"width:237px\">\r\n			<p>18&nbsp;&ndash; 19:30</p>\r\n			</td>\r\n			<td style=\"width:224px\">\r\n			<p>18&ndash; 19:30</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:165px\">\r\n			<p><strong>T7&amp;CN</strong></p>\r\n			</td>\r\n			<td colspan=\"2\" style=\"width:461px\">\r\n			<p>8:00 am &ndash; 10:00 am</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<div style=\"clear:both;\">&nbsp;</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>CẤP ĐỘ v&agrave; GI&Aacute;O TR&Igrave;NH</strong></p>\r\n\r\n<table align=\"center\" border=\"1\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:215px\">\r\n			<p><strong>CẤP ĐỘ</strong></p>\r\n			</td>\r\n			<td style=\"width:222px\">\r\n			<p><strong>GI&Aacute;O TR&Igrave;NH</strong></p>\r\n			</td>\r\n			<td style=\"width:183px\">\r\n			<p><strong>THỜI GIAN KH&Oacute;A HỌC</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:215px\">\r\n			<p><strong>TWINKLE STARS</strong></p>\r\n			</td>\r\n			<td style=\"width:222px\">\r\n			<p><strong>MY FIRST</strong></p>\r\n			</td>\r\n			<td style=\"width:183px\">\r\n			<p>36 tuần</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:215px\">\r\n			<p><strong>BRIGHT STARS</strong></p>\r\n			</td>\r\n			<td style=\"width:222px\">\r\n			<p><strong>ABOUT ME</strong></p>\r\n			</td>\r\n			<td style=\"width:183px\">\r\n			<p>36 tuần</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:215px\">\r\n			<p><strong>SUPER STARS</strong></p>\r\n			</td>\r\n			<td style=\"width:222px\">\r\n			<p><strong>ALL ABOUT ME</strong></p>\r\n			</td>\r\n			<td style=\"width:183px\">\r\n			<p>36 tuần</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<div style=\"clear:both;\">&nbsp;</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#B22222\"><strong>Tối ưu của chương tr&igrave;nh:</strong><strong>&nbsp;</strong>&nbsp;</span></p>\r\n\r\n<p><strong>Gi&aacute;o tr&igrave;nh</strong>&nbsp;được Trung t&acirc;m bi&ecirc;n soạn một c&aacute;ch logic đ&uacute;ng tư duy v&agrave; khả năng ph&aacute;t triển của b&eacute; ở độ tuổi 4-6, khả năng ca h&aacute;t, kể chuyện v&agrave; giao tiếp tiếng Anh với chủ đề xung quanh h&agrave;ng ng&agrave;y của b&eacute;.</p>\r\n\r\n<p><strong>100% tiếng Anh giao tiếp c&ugrave;ng b&eacute;</strong>, gi&aacute;o vi&ecirc;n cam kết tối ưu t&agrave;i năng v&agrave; khả năng ng&ocirc;n ngữ của b&eacute;, c&ugrave;ng với cam kết sử dụng, hoạt động b&agrave;i dạy thiết kế trực quan sinh động tr&ecirc;n lớp học với những hoạt động d&atilde; ngoại, xem phim gi&uacute;p c&aacute;c b&eacute; tiếp thu ng&ocirc;n ngữ một c&aacute;c tự nhi&ecirc;n nhất.</p>\r\n\r\n<p><strong>Home-School link</strong>&nbsp;l&agrave; c&ocirc;ng cụ, tương t&aacute;c chặt chẻ giữa phụ huynh v&agrave; Trung t&acirc;m để theo d&otilde;i tiến độ học tập của c&aacute;c b&eacute;. Th&ocirc;ng qua Home-School link n&agrave;y, phụ huynh theo d&otilde;i kiến thức mỗi b&agrave;i học v&agrave; kết quả học tập của b&eacute; sau mỗi buổi học.</p>\r\n\r\n<p><strong>Hỗ trợ phụ đạo </strong>cho c&aacute;c b&eacute; yếu, phụ huynh kh&ocirc;ng c&oacute; thời gian hỗ trợ c&aacute;c b&eacute; tại nh&agrave;, hoặc những buổi vắng học c&oacute; ph&eacute;p, l&agrave; t&acirc;m huyết của Trung t&acirc;m cam kết chất lượng học cho từng b&eacute;.</p>\r\n\r\n<p><strong>T&agrave;i năng của c&aacute;c b&eacute; được vinh danh</strong>&nbsp;qua nhận thưởng v&agrave; giấy khen kết th&uacute;c kh&oacute;a học đối với c&aacute;c bạn th&agrave;nh t&iacute;ch cao trong suốt kh&oacute;a học;</p>\r\n</div>\r\n', 1, 1, '2020-09-16 15:00:01'),
(3, 'Anh Văn Thiếu Nhi<br>(Starters, Movers, Flyers)', 'ANH VĂN THIẾU NHI', 'anh-van-thieu-nhi<br>starters-movers-flyers', 'chuong-trinh-dao-tao/pocari-sweat-website.png', 'Chương trình tiếng Anh thiếu nhi được thiết kế phù hợp cho các em từ 7-12 tuổi, từng bước giúp các em tiếp cận và hoàn toàn tự tin tham gia các kì thi lấy bằng cấp quốc tế Cambridge.', '<p>Chương tr&igrave;nh Anh ngữ chuẩn tiếng Anh được Quốc tế c&ocirc;ng nhận khả năng Nghe-N&oacute;i-Đọc-Viết. Gi&uacute;p c&aacute;c bạn nhỏ tự tin trong bất kỳ kỹ năng n&agrave;o, đặc biệt l&agrave; kỹ năng giao tiếp v&agrave; phản xạ ng&ocirc;n ngữ. Đ&acirc;y l&agrave; một&nbsp;trong những th&agrave;nh c&ocirc;ng trong c&ocirc;ng t&aacute;c đ&agrave;o tạo chương tr&igrave;nh n&agrave;y của Trung t&acirc;m l&agrave; mang đến đam m&ecirc; v&agrave; m&ocirc;n học tiếng Anh được xem l&agrave; m&ocirc;n học y&ecirc;u th&iacute;ch của c&aacute;c em.</p>\r\n', '<div class=\"noidung\">\r\n<p><span style=\"color:#B22222\"><strong>ĐỐI TƯỢNG HỌC VI&Ecirc;N</strong></span></p>\r\n\r\n<p>Chương tr&igrave;nh tiếng Anh thiếu nhi được thiết kế ph&ugrave; hợp cho c&aacute;c em từ 7-12 tuổi. Ở độ tuổi khi c&aacute;c em mới bắt đầu l&agrave;m quen với tiếng Anh n&ecirc;n rất cần một chương tr&igrave;nh ho&agrave;n thiện để gi&uacute;p c&aacute;c em ph&aacute;t triển tối ưu. T&ugrave;y theo từng độ tuổi v&agrave; tr&igrave;nh độ đầu v&agrave;o của c&aacute;c em, ch&uacute;ng t&ocirc;i sẽ hướng dẫn cụ thể để phụ huynh chọn lựa chương tr&igrave;nh ph&ugrave; hợp v&agrave; hiệu quả nhất, từng bước gi&uacute;p c&aacute;c em tiếp cận v&agrave; ho&agrave;n to&agrave;n tự tin tham gia c&aacute;c k&igrave; thi lấy bằng cấp quốc tế Cambridge. Đặc biệt, tham gia k&igrave; thi tiếng Anh của Cambridge l&agrave; c&aacute;ch tốt nhất để qu&yacute; phụ huynh v&agrave; ch&iacute;nh con em m&igrave;nh nhận biết được sự tiến bộ m&agrave; c&aacute;c em đ&atilde; đạt được. Đ&acirc;y ch&iacute;nh l&agrave; một động lực để khuyến kh&iacute;ch c&aacute;c em y&ecirc;u th&iacute;ch v&agrave; học tốt tiếng Anh hơn.</p>\r\n\r\n<p><span style=\"color:#B22222\"><strong>CẤP ĐỘ Đ&Agrave;O TẠO</strong></span></p>\r\n\r\n<p>Kiểm tra tr&igrave;nh độ: trước khi tham gia kh&oacute;a học, c&aacute;c b&eacute; sẽ được gi&aacute;o vi&ecirc;n phỏng vấn nhằm đ&aacute;nh gi&aacute; đ&uacute;ng tr&igrave;nh độ v&agrave; để chọn kh&oacute;a học ph&ugrave; hơp cho b&eacute;.</p>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height:42px; width:124px\">\r\n			<p style=\"text-align:center\"><strong>CẤP ĐỘ</strong></p>\r\n			</td>\r\n			<td style=\"height:42px; width:172px\">\r\n			<p style=\"text-align:center\"><strong>THỜI GIAN </strong></p>\r\n\r\n			<p style=\"text-align:center\"><strong>KH&Oacute;A HỌC</strong></p>\r\n			</td>\r\n			<td style=\"height:42px; width:157px\">\r\n			<p style=\"text-align:center\"><strong>LỊCH HỌC</strong></p>\r\n			</td>\r\n			<td style=\"height:42px; width:162px\">\r\n			<p style=\"text-align:center\"><strong>HỌC PH&Iacute;</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:20px; width:124px\">\r\n			<p style=\"text-align:center\">STARTERS</p>\r\n			</td>\r\n			<td style=\"height:20px; width:172px\">\r\n			<p style=\"text-align:center\">&nbsp;48 tuần</p>\r\n			</td>\r\n			<td rowspan=\"3\" style=\"height:20px; width:157px\">\r\n			<p style=\"text-align:center\">246(6:00-7:30pm)</p>\r\n\r\n			<p style=\"text-align:center\">357(6:00-7:30pm)</p>\r\n\r\n			<p style=\"text-align:center\">T7&amp;CN (8-10am)</p>\r\n\r\n			<p style=\"text-align:center\">T7&amp;CN(5:00-7:00pm)</p>\r\n			</td>\r\n			<td style=\"height:20px; width:162px\">\r\n			<p style=\"text-align:center\">Li&ecirc;n hệ</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:21px; width:124px\">\r\n			<p style=\"text-align:center\">MOVERS</p>\r\n			</td>\r\n			<td style=\"height:21px; width:172px\">\r\n			<p style=\"text-align:center\">48 tuần</p>\r\n			</td>\r\n			<td style=\"height:21px; width:162px\">\r\n			<p style=\"text-align:center\">Li&ecirc;n hệ</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:21px; width:124px\">\r\n			<p style=\"text-align:center\">FLYERS</p>\r\n			</td>\r\n			<td style=\"height:21px; width:172px\">\r\n			<p style=\"text-align:center\">48 tuần</p>\r\n			</td>\r\n			<td style=\"height:21px; width:162px\">\r\n			<p style=\"text-align:center\">Li&ecirc;n hệ</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<div style=\"clear:both\">&nbsp;</div>\r\n\r\n<p>&nbsp;<span style=\"color:#B22222\"><strong>MỤC TI&Ecirc;U&nbsp;Đ&Agrave;O TẠO:</strong></span></p>\r\n\r\n<ul>\r\n	<li>&nbsp; C&aacute;c b&eacute; ph&aacute;t triển 4 kỹ năng nghe, n&oacute;i, đọc v&agrave; viết;</li>\r\n	<li>&nbsp; Giảng dạy tr&ecirc;n phần mền ứng dụng. Nhằm thỏa tr&iacute; t&ograve; m&ograve; của c&aacute;c em v&agrave; tạo buổi học hiệu quả th&ocirc;ng qua h&igrave;nh ảnh, online practice, itools, b&agrave;i h&aacute;t v&agrave; c&aacute;c hoạt động trong lớp;</li>\r\n	<li>&nbsp; Đem lại b&agrave;i học gi&aacute; trị cuộc sống cho c&aacute;c b&eacute; th&ocirc;ng qua mỗi c&acirc;u chuyện trong mỗi đơn vị b&agrave;i học,</li>\r\n	<li>&nbsp; Tự tin m&ocirc; tả sự vật, h&igrave;nh ảnh diễn đạt bằng tiếng Anh (Starters);</li>\r\n	<li>&nbsp; Tự tin diễn đạt bằng tiếng Anh so s&aacute;nh một sự vật, h&igrave;nh ảnh v&agrave; tạo ra một c&acirc;u chuy&ecirc;n th&ocirc;ng qua h&igrave;nh ảnh (Movers-Flyers);</li>\r\n	<li>&nbsp; Tự tin vấn đ&aacute;p ngữ cảnh thực tế;</li>\r\n	<li>&nbsp; Sử dụng tiếng Anh trong nhiều ngữ cảnh bằng những c&acirc;u hội thoại ngắn v&agrave; kể những mẫu chuyện ngắn Thiếu nhi bằng tiếng Anh;</li>\r\n	<li>&nbsp; Kiến thức b&agrave;i học được tổng hợp v&agrave; n&acirc;ng cao kết th&uacute;c mỗi đơn vị b&agrave;i học;</li>\r\n	<li>&nbsp; C&aacute;c mẫu c&acirc;u giao tiếp thực tế v&agrave; s&aacute;t với đề thi Speaking Cambridge Anh Quốc.</li>\r\n	<li>&nbsp; Home- School Link l&agrave; sự phối hợp chặt chẽ Gi&aacute;o vi&ecirc;n v&agrave; Phụ huynh nhằm mang đến hiệu quả tối ưu cho c&aacute;c b&eacute;. (project)</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:7.1pt\"><span style=\"color:#FF0000\"><strong>Tối Ưu</strong></span><span style=\"color:#696969\"><strong> &ndash;</strong> Gi&aacute;o vi&ecirc;n Việt Nam v&agrave; Gi&aacute;o vi&ecirc;n nước ngo&agrave;i đảm nhiệm buổi học, 100% tiếng Anh được sử dụng tr&ecirc;n lớp. Đảm bảo kiến thức buổi học, kỹ năng nghe n&oacute;i lưu lo&aacute;t, chinh phục bằng Cambridge với kết quả cao. Chương tr&igrave;nh <em><u>Home-school link</u></em> l&agrave; sự li&ecirc;n kết chặt chẽ giữa Trung t&acirc;m v&agrave; phụ huynh.</span></p>\r\n\r\n<p><strong>V&igrave; sao phụ huynh n&ecirc;n chọn kh&oacute;a học Anh Văn Thiếu Nhi cho c&aacute;c b&eacute; tại Trung t&acirc;m Ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c:</strong></p>\r\n\r\n<ul>\r\n	<li>Tương t&aacute;c chặt chẻ giữa phụ huynh v&agrave; Trung t&acirc;m để theo d&otilde;i tiến độ học tập của c&aacute;c b&eacute;; Th&ocirc;ng qua h&agrave;ng ng&agrave;y ph&aacute;t bảng điểm th&ocirc;ng b&aacute;o kết quả học tập của b&eacute; tr&ecirc;n lớp để phụ huynh kịp thời nắm bắt tiến tr&igrave;nh học tập của b&eacute; tại Trung t&acirc;m.</li>\r\n	<li>Trung t&acirc;m sẽ hướng dẫn phụ huynh hỗ trợ c&aacute;c b&eacute; luyện tập tiếng Anh tại nh&agrave;;</li>\r\n	<li>Miễn Ph&iacute; phụ đạo cho c&aacute;c b&eacute; yếu, hoặc phụ huynh kh&ocirc;ng c&oacute; thời gian hỗ trợ c&aacute;c b&eacute; tại nh&agrave;;</li>\r\n	<li>Những buổi vắng học c&oacute; ph&eacute;p, Gi&aacute;o vi&ecirc;n hoặc trợ giảng sẽ &ocirc;n b&agrave;i c&aacute;c b&eacute; buổi tiếp sau;</li>\r\n	<li><strong>MIỄN PH&Iacute;</strong>&nbsp;học lại nếu kết quả học tập kh&ocirc;ng đạt y&ecirc;u cầu Trung t&acirc;m;</li>\r\n	<li>Tham gia CLUB tiếng Anh h&agrave;ng tuần, c&aacute;c buổi d&atilde; ngoại, xem phim tiếng Anh thiếu nhi, v&agrave; tham gia c&aacute;c hoạt động tư duy ng&ocirc;n ngữ;</li>\r\n	<li><strong>GIẢM HỌC PH&Iacute;</strong>&nbsp;nếu đ&oacute;ng học ph&iacute; trọn kh&oacute;a học, v&agrave; hỗ trợ học ph&iacute; cho học vi&ecirc;n đang theo học tại Trung t&acirc;m;</li>\r\n	<li><strong>BẢO LƯU</strong>&nbsp;học ph&iacute; nếu thời gian nghỉ học qu&aacute; 2 tuần trở l&ecirc;n;</li>\r\n	<li>Được nhận thưởng v&agrave; giấy khen kết th&uacute;c kh&oacute;a học đối với c&aacute;c bạn th&agrave;nh t&iacute;ch cao trong suốt kh&oacute;a học</li>\r\n	<li>Kết th&uacute;c mỗi kh&oacute;a học Trung t&acirc;m sẽ &ocirc;n củng cố kiến thức để đảm bảo nền tảng vững chắc c&aacute;c em thi kỳ thi Cambridge.</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#B22222\"><strong>Chứng chỉ v&agrave; bằng cấp</strong></span></p>\r\n\r\n<p>Sau khi ho&agrave;n tất mỗi chương tr&igrave;nh học, Trung t&acirc;m mời Hội đồng thi Cambridge tổ chức thi tại Trung t&acirc;m do&nbsp;<strong>Hội đồng Khảo th&iacute; Đại học Cambridge (Cambridge ESOL)</strong>&nbsp;tổ chức&nbsp;<strong>YLE - Cambridge Young Learners English Tests</strong>&nbsp;gồm 3 cấp độ:&nbsp;<strong>Starters</strong>,&nbsp;<strong>Movers</strong>&nbsp;v&agrave;&nbsp;<strong>Flyers</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n</div>\r\n', 1, 2, '2020-05-31 05:45:18'),
(4, 'Anh Văn Thiếu Niên<br>(KET, PET)', 'KET & PET', 'anh-van-thieu-nien<br>ket-pet', 'chuong-trinh-dao-tao/the-sip-app.png', 'Chứng chỉ KET, PET, FCE được Đại học Cambridge xem là thước đo kiến thức chuẩn sử dụng tiếng Anh trên tất cả nước trên thế giới đối với bậc Trung học Cơ Sở và Trung học Phổ Thông...', '<p>Nội dung chương tr&igrave;nh chuẩn tiếng Anh được Quốc tế c&ocirc;ng nhận khả năng Nghe-N&oacute;i-Đọc-Viết. Với sự tự tin khả năng tiếng Anh, đặc biệt l&agrave; kỹ năng n&oacute;i, vững tin trong c&aacute;c kỳ thi tiếng Anh trường học hoặc Quốc tế. H&agrave;nh trang sẵn s&agrave;ng gia nhập v&agrave;o c&ocirc;ng d&acirc;n to&agrave;n cầu được c&aacute;c em trang bị v&agrave; chinh phục trong c&aacute;c cấp độ n&agrave;y.</p>\r\n', '<div class=\"noidung\">\r\n<p><span style=\"color:#B22222\"><strong>1. ĐỐI TƯỢNG HỌC VI&Ecirc;N</strong></span></p>\r\n\r\n<p><strong>&nbsp; &nbsp;L&agrave; chương tr&igrave;nh được thiết kế theo đối tượng học vi&ecirc;n l&agrave; c&aacute;c bạn học sinh trong độ tuổi học THCS v&agrave; THPT.</strong></p>\r\n\r\n<p><span style=\"color:#B22222\"><strong>2. GIỚI THIỆU VỀ&nbsp;CHƯƠNG TR&Igrave;NH</strong></span></p>\r\n\r\n<p>Cambridge English: bao gồm c&aacute;c b&agrave;i kiểm tra tiếng Anh th&uacute; vị v&agrave; sinh động d&agrave;nh cho c&aacute;c em nhằm gi&uacute;p phụ huynh v&agrave; trẻ định hướng được con đường ph&aacute;t triển tiếng Anh của m&igrave;nh. C&aacute;c b&agrave;i kiểm tra được thiết kế bởi tổ chức Cambridge English Language Assessment thuộc đại học Cambridge, vương quốc Anh.</p>\r\n\r\n<p>Chứng chỉ KET, PET, FCE được Đại học Cambridge xem l&agrave; thước đo kiến thức chuẩn sử dụng tiếng Anh tr&ecirc;n tất cả nước tr&ecirc;n thế giới đối với bậc Trung học Cơ Sở v&agrave; Trung học Phổ Th&ocirc;ng.</p>\r\n\r\n<p><em><strong><u>Th&ocirc;ng tin chi tiết kh&oacute;a KET, PET, FCE</u></strong></em></p>\r\n\r\n<p><strong>Key</strong>, c&ograve;n được gọi l&agrave; Key English Test (KET), l&agrave; văn bằng chứng chỉ cấp độ cơ bản cho biết bạn c&oacute; thể sử dụng tiếng Anh để giao tiếp trong c&aacute;c t&igrave;nh huống đơn giản. N&oacute; chứng tỏ bạn đ&atilde; c&oacute; một khởi đầu tốt trong việc học tiếng Anh. Cấp độ n&agrave;y được xếp ở Tr&igrave;nh độ A2 của Khung tham chiếu tr&igrave;nh độ ng&ocirc;n ngữ chung của Việt Nam (VSTEP)</p>\r\n\r\n<ul>\r\n	<li>Chứng tỏ bạn c&oacute; thể giao tiếp bằng tiếng Anh ở tr&igrave;nh độ cơ bản</li>\r\n	<li>Tham dự một kỳ thi chất lượng cao c&ocirc;ng bằng với mọi người</li>\r\n	<li>Lựa chọn địa điểm, thời gian v&agrave; c&aacute;ch thức dự thi cho bạn</li>\r\n	<li>Tự h&agrave;o về chứng chỉ được c&ocirc;ng nhận tr&ecirc;n to&agrave;n cầu</li>\r\n	<li>Nhận được nhiều hỗ trợ gi&uacute;p bạn gặt h&aacute;i th&agrave;nh c&ocirc;ng</li>\r\n</ul>\r\n\r\n<p><strong>Preliminary</strong>, c&ograve;n được gọi l&agrave; Preliminary English Test (PET), l&agrave; văn bằng chứng chỉ tr&igrave;nh độ trung cấp. N&oacute; chứng tỏ bạn c&oacute; khả năng sử dụng c&aacute;c kỹ năng tiếng Anh trong c&ocirc;ng việc, học tập v&agrave; du lịch. Cấp độ n&agrave;yđược xếp ở Tr&igrave;nh độ B1 của Khung tham chiếu tr&igrave;nh độ ng&ocirc;n ngữ chung của Việt Nam (VSTEP)</p>\r\n\r\n<ul>\r\n	<li>Chứng minh bạn nắm vững tiếng Anh thực h&agrave;nh để sử dụng h&agrave;ng ng&agrave;y</li>\r\n	<li>Đạt được c&aacute;c kỹ năng ng&ocirc;n ngữ thực tiễn</li>\r\n	<li>Tham dự một kỳ thi chất lượng cao c&ocirc;ng bằng với mọi người</li>\r\n	<li>Lựa chọn địa điểm, thời gian v&agrave; c&aacute;ch thức dự thi cho bạn</li>\r\n	<li>Tự h&agrave;o về chứng chỉ được c&ocirc;ng nhận tr&ecirc;n to&agrave;n cầu</li>\r\n	<li>Nhận được nhiều hỗ trợ gi&uacute;p bạn gặt h&aacute;i th&agrave;nh c&ocirc;ng</li>\r\n</ul>\r\n\r\n<p><strong>First,</strong>&nbsp;c&ograve;n được gọi l&agrave; First Certificate in English (FCE), l&agrave; văn bằng chứng chỉ tr&igrave;nh độ tr&ecirc;n trung cấp. N&oacute; chứng tỏ bạn c&oacute; thể sử dụng tiếng Anh n&oacute;i v&agrave; viết h&agrave;ng ng&agrave;y trong c&ocirc;ng việc hoặc học tập. Cấp độ n&agrave;yđược xếp ở Tr&igrave;nh độ B2 của Khung tham chiếu tr&igrave;nh độ ng&ocirc;n ngữ chung của Việt Nam (VSTEP)</p>\r\n\r\n<ul>\r\n	<li>Chứng tỏ bạn đ&atilde; sẵn s&agrave;ng cho những th&agrave;nh c&ocirc;ng trong học tập, c&ocirc;ng việc v&agrave; đời sống</li>\r\n	<li>Đạt được c&aacute;c kỹ năng tiếng Anh đời sống phục vụ cho c&ocirc;ng việc v&agrave; học tập</li>\r\n	<li>Tham dự một kỳ thi chất lượng cao c&ocirc;ng bằng với mọi người</li>\r\n	<li>Lựa chọn địa điểm, thời gian v&agrave; c&aacute;ch thức dự thi cho bạn</li>\r\n	<li>Tự h&agrave;o về chứng chỉ được c&ocirc;ng nhận tr&ecirc;n to&agrave;n cầu</li>\r\n	<li>Nhận được nhiều hỗ trợ gi&uacute;p bạn gặt h&aacute;i th&agrave;nh c&ocirc;ng</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#B22222\"><strong>3. THỜI GIAN HỌC</strong></span></p>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height:65px; width:205px\">\r\n			<p style=\"text-align:center\"><strong>LỊCH HỌC</strong></p>\r\n			</td>\r\n			<td style=\"height:65px; width:295px\">\r\n			<p style=\"text-align:center\"><strong>Mon- Wed- Fri (2-4-6)</strong></p>\r\n			</td>\r\n			<td style=\"height:65px; width:227px\">\r\n			<p style=\"text-align:center\"><strong>Tue- Thu- Sat (3-5-7)</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:36px; width:205px\">\r\n			<p style=\"text-align:center\"><strong>TỐI</strong></p>\r\n			</td>\r\n			<td style=\"height:36px; width:295px\">\r\n			<p style=\"text-align:center\">18&ndash; 19:30</p>\r\n			</td>\r\n			<td style=\"height:36px; width:227px\">\r\n			<p style=\"text-align:center\">18&ndash; 19:30</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:53px; width:205px\">\r\n			<p style=\"text-align:center\"><strong>T7&amp;CN</strong></p>\r\n			</td>\r\n			<td colspan=\"2\" style=\"height:53px; width:523px\">\r\n			<p style=\"text-align:center\">8:00 am &ndash; 10:00 am</p>\r\n\r\n			<p style=\"text-align:center\">14:00-16:00</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<div style=\"clear:both\">&nbsp;</div>\r\n\r\n<p><span style=\"color:#B22222\"><strong>4. MỤC TI&Ecirc;U&nbsp;Đ&Agrave;O TẠO:</strong></span></p>\r\n\r\n<ul>\r\n	<li>H&igrave;nh th&agrave;nh v&agrave; r&egrave;n luyện c&aacute;c kỹ năng học tập v&agrave; tư duy Anh ngữ 1 c&aacute;ch logic.</li>\r\n	<li>N&acirc;ng cao kỹ năng nghe &ndash; n&oacute;i, đồng thời cung cấp vốn từ vựng cần thiết, ph&ugrave; hợp với chương tr&igrave;nh phổ th&ocirc;ng.</li>\r\n	<li>Kỹ năng đọc &ndash; viết được ph&aacute;t triển tốt hơn v&agrave; th&agrave;nh thạo hơn trong c&aacute;c c&aacute;ch viết một đoạn văn hay 1 b&agrave;i tiểu luận nhỏ.</li>\r\n	<li>Nắm vững grammar cơ bản trong chương tr&igrave;nh phổ th&ocirc;ng;</li>\r\n	<li>Gi&uacute;p học vi&ecirc;n l&agrave;m quen v&agrave; hiểu được cấu tr&uacute;c b&agrave;i thi;</li>\r\n	<li>X&acirc;y dựng sự tự tin cho trẻ trong c&aacute;c kỳ thi quốc tế;</li>\r\n	<li>Gi&uacute;p trẻ tự tin giao tiếp sử dụng ng&ocirc;n ngữ tiếng Anh trong mọi t&igrave;nh huống th&ocirc;ng qua c&aacute;c b&agrave;i thuyết tr&igrave;nh trước lớp v&agrave; được sự đ&aacute;nh gi&aacute; từ Gi&aacute;o vi&ecirc;n Nước ngo&agrave;i;</li>\r\n	<li>Kỹ năng thuyết tr&igrave;nh, tự tin giao tiếp, diễn giải bằng tiếng Anh trước c&ocirc;ng ch&uacute;ng.</li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><strong><span style=\"background-color:white\"><span style=\"font-family:segoe ui,sans-serif\"><span style=\"color:#FF0000\">Ưu điểm</span><span style=\"color:#696969\">&nbsp;&ndash;</span></span></span></strong><span style=\"color:#696969\"><span style=\"background-color:white\"><span style=\"font-family:segoe ui,sans-serif\">&nbsp;Chương tr&igrave;nh được thiết kế đ&agrave;o tạo Gi&aacute;o vi&ecirc;n Việt Nam v&agrave; Gi&aacute;o vi&ecirc;n nước ngo&agrave;i, 100% tiếng Anh được sử dụng tr&ecirc;n lớp. Đảm bảo kiến thức buổi học, kỹ năng nghe n&oacute;i lưu lo&aacute;t, chinh phục bằng Cambridge với kết quả cao. Kỹ năng thuyết tr&igrave;nh th&ocirc;ng qua sự thể hiện v&agrave; chuẩn bị cho buổi thuyết tr&igrave;nh như poster, powerpoint, reality, đồng thời thực hiện c&aacute;c dự &aacute;n thể hiện sự tự tin, s&aacute;ng tạo, qua đ&oacute; đ&aacute;nh gi&aacute; sự lưu lo&aacute;t tiếng Anh của c&aacute;c em. Để hỗ trợ c&aacute;c em vững v&agrave;ng hơn về Grammar trong chương tr&igrave;nh phổ th&ocirc;ng, EVAS đ&atilde; n&acirc;ng giờ dạy Grammar v&agrave; t&agrave;i liệu được EVAS tổng hợp đồng bộ điểm ngữ ph&aacute;p trong chương tr&igrave;nh phổ th&ocirc;ng của c&aacute;c em.</span></span></span></span></span></li>\r\n</ul>\r\n\r\n<p><strong>V&igrave; sao c&aacute;c em n&ecirc;n học tại Trung t&acirc;m Ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c:</strong></p>\r\n\r\n<ul>\r\n	<li>Tương t&aacute;c chặt chẻ giữa phụ huynh v&agrave; Trung t&acirc;m để theo d&otilde;i tiến độ học tập của c&aacute;c em; Th&ocirc;ng qua những b&agrave;i kiểm tra kết b&agrave;i v&agrave; bảng điểm th&ocirc;ng b&aacute;o kết quả học tập của em tr&ecirc;n lớp để phụ huynh kịp thời nắm bắt tiến tr&igrave;nh học tập của em tại Trung t&acirc;m.</li>\r\n	<li><strong>MIỄN PH&Iacute;</strong>&nbsp; phụ đạo cho c&aacute;c b&eacute; yếu, hoặc phụ huynh kh&ocirc;ng c&oacute; thời gian hỗ trợ c&aacute;c b&eacute; tại nh&agrave;;</li>\r\n	<li>Những buổi vắng học c&oacute; ph&eacute;p, Gi&aacute;o vi&ecirc;n hoặc trợ giảng sẽ &ocirc;n b&agrave;i c&aacute;c b&eacute; buổi tiếp sau;</li>\r\n	<li><strong>MIỄN PH&Iacute;</strong>&nbsp;học lại nếu kết quả học tập kh&ocirc;ng đạt y&ecirc;u cầu Trung t&acirc;m;</li>\r\n	<li>Tham gia CLUB tiếng Anh h&agrave;ng tuần, c&aacute;c buổi d&atilde; ngoại v&agrave; tham gia c&aacute;c hoạt động tư duy ng&ocirc;n ngữ;</li>\r\n	<li><strong>GIẢM HỌC PH&Iacute;</strong>&nbsp;nếu đ&oacute;ng học ph&iacute; trọn kh&oacute;a học, v&agrave; hỗ trợ học ph&iacute; cho học vi&ecirc;n đang theo học tại Trung t&acirc;m;</li>\r\n	<li><strong>BẢO LƯU</strong>&nbsp;học ph&iacute; nếu thời gian nghỉ học qu&aacute; 2 tuần trở l&ecirc;n;</li>\r\n	<li>Được nhận thưởng v&agrave; giấy khen kết th&uacute;c kh&oacute;a học đối với c&aacute;c bạn th&agrave;nh t&iacute;ch cao trong suốt kh&oacute;a học</li>\r\n	<li>Kết th&uacute;c mỗi kh&oacute;a học Trung t&acirc;m sẽ &ocirc;n củng cố kiến thức để đảm bảo nền tảng vững chắc c&aacute;c em thi kỳ thi Cambridge.</li>\r\n</ul>\r\n\r\n<p><strong>Chứng chỉ v&agrave; bằng cấp</strong></p>\r\n\r\n<ul>\r\n	<li>C&aacute;c em sẽ được cấp giấy chứng nhận (<em>Acheivement Certificate</em>) của Trung t&acirc;m ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c sau khi ho&agrave;n th&agrave;nh kh&oacute;a học.</li>\r\n	<li>Sau khi ho&agrave;n tất mỗi chương tr&igrave;nh học, Trung t&acirc;m tiến h&agrave;nh thực hiện thủ tục dự thi cho c&aacute;c em, v&agrave; đưa c&aacute;c em đến<strong>&nbsp;Hội đồng Khảo th&iacute; Đại học Cambridge (Cambridge ESOL)</strong>&nbsp;dự thi v&agrave; đ&aacute;nh gi&aacute; n&acirc;ng lực mỗi cấp độ.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n</div>\r\n', 1, 3, '2020-05-31 05:43:29'),
(5, 'Anh Văn Giao Tiếp', 'PHÙ HỢP VỚI SINH VIÊN & NGƯỜI ĐI LÀM', 'anh-van-giao-tiep', 'chuong-trinh-dao-tao/dalat-hasfarm.png', 'Giúp học viên tự tin giao tiếp, phản xạ, luyện phát âm và mở rộng vốn từ vựng thông qua phương pháp dạy trực quan sinh động phần mềm MEE hoạt động đơn và hoạt động nhóm...', '<p><strong>Chương tr&igrave;nh</strong>&nbsp;được thiết kế đ&uacute;ng đối tượng học vi&ecirc;n mong muốn n&acirc;ng cao kỹ năng giao tiếp c&ugrave;ng <strong>đội</strong>&nbsp;<strong>ngũ&nbsp;gi&aacute;o vi&ecirc;n EVAS</strong>&nbsp;nhiều năm kinh nghiệm trong đ&agrave;o tạo tiếng Anh cho sinh vi&ecirc;n, học sinh Việt Nam, gi&uacute;p c&aacute;c bạn thoải m&aacute;i tự tin v&agrave; chia sẻ, x&oacute;a đi&nbsp;r&agrave;o cản văn h&oacute;a v&agrave; ng&ocirc;n ngữ trong những buổi học.</p>\r\n', '<div class=\"noidung\">\r\n<p>Thấu hiểu được kh&oacute; khăn của người lớn, n&acirc;ng cao kỹ năng giao tiếp, ch&uacute;ng t&ocirc;i lu&ocirc;n đặt vị tr&iacute; của m&igrave;nh l&agrave; người đồng h&agrave;nh c&ugrave;ng c&aacute;c bạn chinh phục tiếng Anh. <strong>Đội ngũ&nbsp;gi&aacute;o vi&ecirc;n EVAS, gi&aacute;o vi&ecirc;n nước ngo&agrave;i</strong>, nhiều năm kinh nghiệm trong đ&agrave;o tạo tiếng Anh cho sinh vi&ecirc;n, học sinh Việt Nam, gi&uacute;p c&aacute;c bạn thoải m&aacute;i tự tin v&agrave; chia sẻ&nbsp;kh&ocirc;ng c&ograve;n r&agrave;o cản văn h&oacute;a v&agrave; ng&ocirc;n ngữ trong những buổi học.</p>\r\n\r\n<p><strong>Phương ph&aacute;p giảng dạy</strong>&nbsp;lấy học vi&ecirc;n l&agrave; trung t&acirc;m, mỗi buổi học được thiết kế trực quan sinh động, tối đa sự tương t&aacute;c người học, gi&uacute;p tự tin giao tiếp v&agrave; thoải m&aacute;i thuyết tr&igrave;nh diễn đạt &yacute; trước đ&aacute;m đ&ocirc;ng.</p>\r\n\r\n<p><strong>Gi&aacute;o tr&igrave;nh</strong>&nbsp;l&agrave; sự t&iacute;ch hợp c&ocirc;ng nghệ 4.0 phần mền MEE, t&igrave;nh huống v&agrave; hoạt động thiết kế từng b&agrave;i, gi&uacute;p người học tương t&aacute;c thoải m&aacute;i, khắc phục điểm yếu thiếu tự tin trong giao tiếp. Trung t&acirc;m tặng mỗi bạn học phần mền bổ trợ chương tr&igrave;nh học ở lớp, được c&agrave;i đặt laptop or điện thoại, ngo&agrave;i giờ học tr&ecirc;n lớp c&aacute;c bạn c&oacute; thể trao dồi tiếng Anh ho&agrave;n to&agrave;n chủ động mọi l&uacute;c mọi nơi.</p>\r\n\r\n<p><strong>Tối đa h&oacute;a m&ocirc;i trường để người học thực h&agrave;nh giao tiếp tiếng Anh, ch&uacute;ng t&ocirc;i hỗ trợ c&aacute;c c&acirc;u lạc bộ nghe n&oacute;i tiếng Anh, Drop-In session giao tiếp mỗi ngo&agrave;i c&ugrave;ng Gi&aacute;o vi&ecirc;n nước ngo&agrave;i, </strong>c&ugrave;ng&nbsp;c&aacute;c buổi ngoại kh&oacute;a v&agrave; tham gia c&aacute;c hoạt động tư duy ng&ocirc;n ngữ. Mục ti&ecirc;u đem đến chất lượng cho từng người học, EVAS <strong>h</strong><strong>ỗ trợ phụ đạo </strong>anh/ chị bận đi c&ocirc;ng t&aacute;c hoặc cần hướng dẫn chi tiết trong chương tr&igrave;nh học.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Chứng chỉ v&agrave; bằng cấp</strong></p>\r\n\r\n<p>Học vi&ecirc;n sẽ được cấp giấy chứng nhận (<em>Achievement Certificate</em>) của Trung t&acirc;m Ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c sau khi ho&agrave;n th&agrave;nh kh&oacute;a học.</p>\r\n\r\n<p>Nền tảng vững chắc để c&aacute;c bạn tự tin chinh phục c&aacute;c bằng cấp quốc tế.</p>\r\n</div>\r\n', 1, 4, '2020-05-31 05:16:08'),
(6, 'Ôn Luyện IELTS', 'HỌC THEO LỘ TRÌNH BÀI BẢN', 'on-luyen-ielts', 'chuong-trinh-dao-tao/hoa-sen-group.png', 'Hệ Thống Trắc Nghiệm Anh Ngữ Quốc Tế (International English Language Testing System) là kỳ thi tiếng Anh dành cho các đối tượng muốn học tập và sinh sống tại các quốc gia sử dụng tiếng Anh là ngôn ngữ chính...', '<p>Việc &ocirc;n luyện để chắc chắn đạt được mục ti&ecirc;u số điểm trong thang đ&aacute;nh gi&aacute; của IELTS sẽ lu&ocirc;n đi k&egrave;m với một lộ tr&igrave;nh r&otilde; r&agrave;ng -&nbsp;cụ thể nhất với sự cam kết đầu ra tuyệt đối từ EVAS.</p>\r\n', '<div class=\"noidung\">\r\n<p><strong><span style=\"color:#B22222\"><em><u>Th&ocirc;ng tin cần biết về IELTS</u></em></span></strong></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Hệ Thống Trắc Nghiệm <a href=\"http://www.elcvn.com/\">Anh Ngữ Quốc Tế</a> (International English Language Testing System) l&agrave; kỳ thi tiếng Anh d&agrave;nh cho c&aacute;c đối tượng muốn học tập v&agrave; sinh sống tại c&aacute;c quốc gia sử dụng tiếng Anh l&agrave; ng&ocirc;n ngữ ch&iacute;nh.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Kỳ thi n&agrave;y được đồng sỡ hữu v&agrave; điều h&agrave;nh bởi 3 tổ chức: University of Cambridge ESOL, British Council v&agrave; tổ chức gi&aacute;o dục IDP của &Uacute;c.<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <a href=\"http://www.elcvn.com/\">IELTS</a> đ&aacute;nh gi&aacute; khả năng giao tiếp bằng tiếng Anh của th&iacute; sinh th&ocirc;ng qua việc kiểm tra cả 4 kỹ năng Nghe, N&oacute;i, Đọc, Viết.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Chứng chỉ IELTS c&oacute; gi&aacute; trị trong 2 năm.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em><u>B&agrave;i thi IELTS c&oacute; hai h&igrave;nh thức:</u></em></p>\r\n\r\n<ul>\r\n	<li>H&igrave;nh thức Academic- d&agrave;nh cho c&aacute;c th&iacute; sinh dự định học đại học v&agrave; thạc sỹ</li>\r\n	<li>H&igrave;nh thức General Training - D&agrave;nh cho c&aacute;c th&iacute; sinh dự định học Trung học, Cao đẳng hoặc tham dự c&aacute;c kh&oacute;a đ&agrave;o tạo kh&ocirc;ng ở bậc đại học, hoặc v&igrave; mục đ&iacute;ch định cư.</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>B&agrave;i thi Nghe 40 ph&uacute;t</li>\r\n	<li>B&agrave;i thi đọc 60 ph&uacute;t</li>\r\n	<li>B&agrave;i thi Viết 60 ph&uacute;t</li>\r\n	<li>Phỏng vấn n&oacute;i 15 ph&uacute;t</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:.25in\"><strong><span style=\"color:#ff0000\">PRE IELTS 1</span>:</strong> cam kết đầu ra&nbsp; 4.0</p>\r\n\r\n<p>Thời lượng: 6 th&aacute;ng <strong>(24 tuần)</strong></p>\r\n\r\n<p>Gi&aacute;o tr&igrave;nh: <strong>Trung t&acirc;m bi&ecirc;n soạn</strong></p>\r\n\r\n<p>Thời lượng GVNN: 30% GVNN &amp; 70% GVVN</p>\r\n\r\n<p>Đối tượng: Học vi&ecirc;n nguyện vọng đạt 4.0, học tiếp nối chương tr&igrave;nh EVAS, kế hoạch du học v&agrave; định cư nước ngo&agrave;i.</p>\r\n\r\n<p style=\"margin-left:.25in\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"color:#ff0000\"><strong>PRE IELTS </strong><strong>2</strong><strong>:</strong></span> cam kết đầu ra&nbsp; 4.5</p>\r\n\r\n<p>Gi&aacute;o tr&igrave;nh: <strong>Get Insight into IELTS skills</strong>, Word lists.</p>\r\n\r\n<p>Thời lượng: 6 th&aacute;ng <strong>(24 tuần)</strong></p>\r\n\r\n<p>Thời lượng GVNN: 30% GVNN &amp; 70% GVVN</p>\r\n\r\n<p>Đối tượng: Học vi&ecirc;n nguyện vọng đạt 4.5, học tiếp nối chương tr&igrave;nh EVAS, kế hoạch du học v&agrave; định cư nước ngo&agrave;i.</p>\r\n\r\n<p style=\"margin-left:.25in\"><strong>&nbsp; &nbsp; <span style=\"color:#ff0000\">&nbsp;IELTS 1:</span></strong><span style=\"color:#ff0000\"> </span>cam kết đầu ra&nbsp; 5.0</p>\r\n\r\n<p>Gi&aacute;o tr&igrave;nh: <strong>Trung t&acirc;m tổng hợp</strong></p>\r\n\r\n<p>Thời lượng: 6 th&aacute;ng <strong>(24 tuần)</strong></p>\r\n\r\n<p>Thời lượng GVNN: 30% GVNN &amp; 70% GVVN</p>\r\n\r\n<p>Đối tượng: Học vi&ecirc;n nguyện vọng đạt 5.0, học tiếp nối chương tr&igrave;nh EVAS, kế hoạch du học v&agrave; định cư nước ngo&agrave;i.</p>\r\n\r\n<p style=\"margin-left:.25in\"><strong>&nbsp; &nbsp; &nbsp;<span style=\"color:#ff0000\">IELTS 2:</span></strong> cam kết đầu ra&nbsp; 5.5</p>\r\n\r\n<p>&nbsp;Gi&aacute;o tr&igrave;nh: <strong>Trung t&acirc;m tổng hợp</strong></p>\r\n\r\n<p>Thời lượng: 6 th&aacute;ng <strong>(24 tuần)</strong></p>\r\n\r\n<p>Thời lượng GVNN: 30% GVNN &amp; 70% GVVN</p>\r\n\r\n<p>Đối tượng: Học vi&ecirc;n nguyện vọng đạt 5.0, học tiếp nối chương tr&igrave;nh EVAS, kế hoạch du học v&agrave; định cư nước ngo&agrave;i.</p>\r\n\r\n<p style=\"margin-left:.25in\"><strong>&nbsp; &nbsp; &nbsp; <span style=\"color:#ff0000\">IELTS 3:</span></strong> cam kết đầu ra&nbsp; 6.0</p>\r\n\r\n<p>Gi&aacute;o tr&igrave;nh: <strong>IELTS EXAM PREPARATION</strong> (Vocab Consoliation - Speaking Feedback)</p>\r\n\r\n<p>Thời lượng: 6 th&aacute;ng <strong>(24 tuần)</strong></p>\r\n\r\n<p>Thời lượng GVNN: 30% GVNN &amp; 70% GVVN</p>\r\n\r\n<p>Đối tượng: Học vi&ecirc;n nguyện vọng đạt 6.0, học tiếp nối chương tr&igrave;nh EVAS, ho&agrave;n th&agrave;nh thủ tục du học nước ngo&agrave;i.</p>\r\n\r\n<p><span style=\"color:#B22222\"><strong>Mục ti&ecirc;u đ&agrave;o tạo:</strong></span></p>\r\n\r\n<ul>\r\n	<li>Nắm vững cấu tr&uacute;c đề thi;</li>\r\n	<li>L&agrave;m quen với tất cả c&aacute;c dạng c&acirc;u hỏi;</li>\r\n	<li>Ph&aacute;t triển chiến thuật l&agrave;m b&agrave;i thi;</li>\r\n	<li>Ho&agrave;n th&agrave;nh c&aacute;c b&agrave;i thi mẫu;</li>\r\n	<li>Tập trung r&egrave;n luyện to&agrave;n diện bốn kỹ năng Nghe - N&oacute;i - Đọc- Viết;</li>\r\n	<li>Mở rộng vốn từ vựng, n&acirc;ng cao kỹ năng viết;</li>\r\n	<li>Lập chiến lược l&agrave;m b&agrave;i hợp l&yacute; đối với c&aacute;c dạng c&acirc;u hỏi kh&aacute;c nhau của b&agrave;i thi Nghe v&agrave; Đọc.</li>\r\n	<li>Ph&aacute;t triển khả năng suy nghĩ độc lập, đưa ra nhiều &yacute; kiến trong b&agrave;i thi N&oacute;i v&agrave; Viết.</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#B22222\"><strong>Tối ưu chương tr&igrave;nh:</strong></span></p>\r\n\r\n<ul>\r\n	<li>Gi&aacute;o vi&ecirc;n nhiều năm kinh nghiệm luyện thi IELTS, cam kết đạt 100% kết kh&oacute;a;</li>\r\n	<li>Đạt số điểm cao, ho&agrave;n th&agrave;nh mục đ&iacute;ch ch&iacute;nh du học;</li>\r\n	<li>Phụ đạo c&aacute;c kỹ năng Writing v&agrave; Speaking cho học vi&ecirc;n suốt lộ tr&igrave;nh &ocirc;n luyện Ielts tại Trung t&acirc;m;</li>\r\n	<li>Học vi&ecirc;n học 3 kh&oacute;a li&ecirc;n tục Trung t&acirc;m đạt kết quả cao được ho&agrave;n lại 50% kh&oacute;a học cuối c&ugrave;ng;</li>\r\n	<li>T&agrave;i liệu lu&ocirc;n được bi&ecirc;n soạn cập nhật thường xuy&ecirc;n với đề thi hiện h&agrave;nh;</li>\r\n	<li>Nh&acirc;n vi&ecirc;n quản l&yacute; lớp hỗ trợ nhiệt t&igrave;nh suốt kh&oacute;a học, tương t&aacute;c phụ huynh v&agrave; học vi&ecirc;n về kết quả v&agrave; tiến độ học tập;</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#B22222\"><strong>Chứng chỉ v&agrave; bằng cấp</strong></span></p>\r\n\r\n<p style=\"margin-left:.5in\">Sau khi ho&agrave;n tất mỗi chương tr&igrave;nh học, Trung t&acirc;m tiến h&agrave;nh thực hiện thủ tục dự thi cho c&aacute;c em tại <strong>Hội đồng Anh(BC)</strong> hoặc <strong>Hội đồng &Uacute;c(IDP)</strong> t&ugrave;y v&agrave;o mục đ&iacute;ch y&ecirc;u cầu của học vi&ecirc;n.</p>\r\n</div>\r\n', 1, 5, '2020-05-03 02:42:52');
INSERT INTO `education_program` (`id`, `title`, `title1`, `code`, `image`, `description`, `description1`, `detail`, `status`, `sort`, `created_at`) VALUES
(7, 'Ôn Luyện TOEIC', 'HỌC THEO LỘ TRÌNH BÀI BẢN', 'on-luyen-toeic', 'chuong-trinh-dao-tao/TOEIC.jpg', 'TOEIC được viết tắt của Test of English for International Communication là chương trình kiểm tra, đánh giá khả năng sử dụng Anh ngữ trong môi trường giao tiếp và làm việc quốc tế...', NULL, '<div class=\"noidung\">\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"color:#B22222\"><strong><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><em><u><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\">Th&ocirc;ng tin chi tiết&nbsp;TOEIC</span></span></u></em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></span></strong></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\"><span style=\"color:#212529\">TOEIC được viết tắt của Test of English for International Communication l&agrave; chương tr&igrave;nh kiểm tra, đ&aacute;nh gi&aacute; khả năng sử dụng Anh ngữ trong m&ocirc;i trường giao tiếp v&agrave; l&agrave;m việc quốc tế. Tại Việt Nam, c&aacute;c c&ocirc;ng ty nước ngo&agrave;i, c&ocirc;ng ty li&ecirc;n doanh v&agrave; cả c&aacute;c doanh nghiệp trong nước cũng đang lấy TOEIC l&agrave;m một ti&ecirc;u chuẩn bắt buộc trong kh&acirc;u tuyển dụng nh&acirc;n sự &ndash; nhất l&agrave; đối với c&aacute;c chức danh quan trọng. Với c&aacute;c c&ocirc;ng ty đa quốc gia v&agrave; c&aacute;c tập đo&agrave;n lớn TOEIC l&agrave; chuẩn để họ đ&aacute;nh gi&aacute; tr&igrave;nh độ tiếng anh của nh&acirc;n vi&ecirc;n. Nếu bạn c&oacute; một chứng chỉ TOEIC với điểm số cao c&ugrave;ng khả năng giao tiếp ho&agrave;n hảo th&igrave; cơ hội được l&agrave;m việc trong những tập đo&agrave;n đa quốc gia, c&aacute;c c&ocirc;ng ty h&agrave;ng đầu kh&ocirc;ng c&ograve;n l&agrave; chuyện xa vời.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\"><span style=\"color:#212529\">Chương tr&igrave;nh TOEIC do Viện Khảo Th&iacute; Gi&aacute;o Dục Hoa Kỳ - ETS thiết kế năm 1979 theo đơn đặt h&agrave;ng của Bộ C&ocirc;ng Nghiệp v&agrave; Ngoại Thương Nhật Bản.</span></span></span></span></span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"color:#B22222\"><strong><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><em><u><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\">Đối tượng cần TOEIC:</span></span></u></em></span></span></span></strong></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\"><span style=\"color:#212529\">-&nbsp;</span></span></span></strong><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\"><span style=\"color:#212529\">Những người sử dụng tiếng Anh trong c&ocirc;ng việc h&agrave;ng ng&agrave;y thuộc c&aacute;c lĩnh vực như: kinh doanh, kh&aacute;ch sạn, bệnh viện, nh&agrave; h&agrave;ng, hội nghị quốc tế v&agrave; sự kiện thể thao.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\"><span style=\"color:#212529\">- Học sinh, sinh vi&ecirc;n c&oacute; nhu cầu thi lấy bằng để ra trường hoặc xin việc. D&agrave;nh cho người đi l&agrave;m c&oacute; nhu cầu thi lấy bằng để ra thăng chức hoặc phuc vụ cho c&ocirc;ng việc kinh doanh.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\"><span style=\"color:#212529\">- Du học c&aacute;c nước y&ecirc;u cầu TOEIC l&agrave; điều kiện nhập học như Đ&agrave;i Loan, Trung Quốc.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><strong><em><u><span style=\"font-size:10.5pt\"><span style=\"font-family:segoe ui,sans-serif\"><span style=\"color:#212529\">Thi TOEIC định kỳ</span></span></span></u></em></strong><span style=\"font-size:10.5pt\"><span style=\"font-family:segoe ui,sans-serif\"><span style=\"color:#212529\">&nbsp;IIG V</span></span></span><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\"><span style=\"color:#212529\">iệt Nam tổ chức thi TOEIC định kỳ cho c&aacute;c c&aacute; nh&acirc;n v&agrave;o c&aacute;c ng&agrave;y từ thứ hai đến s&aacute;ng thứ bảy h&agrave;ng tuần, trong giờ h&agrave;nh ch&iacute;nh tại c&aacute;c văn ph&ograve;ng của TOEIC Việt Nam ở H&agrave; Nội, Đ&agrave; Nẵng, Hồ Ch&iacute; Minh v&agrave; C&acirc;n Thơ.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><em><u><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\"><span style=\"color:#212529\">Hồ Sơ dự thi cần c&oacute;:</span></span></span></u></em></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"color:#212529\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\">03 ảnh thẻ 3x4 chụp kh&ocirc;ng qu&aacute; 03 th&aacute;ng, chụp ảnh kh&ocirc;ng đeo k&iacute;nh.</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"color:#212529\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\">Chứng minh nh&acirc;n d&acirc;n/Hộ chiếu gốc v&agrave; bản ph&ocirc;-t&ocirc; Chứng minh nh&acirc;n d&acirc;n/Hộ chiếu hoặc c&aacute;c loại giấy chứng nhận nh&acirc;n th&acirc;n bổ sung kh&aacute;c (nếu được y&ecirc;u cầu). Thủ tục đăng k&yacute; dự thi kh&ocirc;ng được chấp nhận qua điện thoại, fax hoặc e-mail.</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"color:#212529\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\">Lệ ph&iacute; thi li&ecirc;n hệ bộ phận quản l&yacute;.</span></span></span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\"><span style=\"color:#212529\">Phiếu điểm TOEIC c&oacute; gi&aacute; trị trong thời han 02 năm</span></span></span></strong><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\"><span style=\"color:#212529\">. Trong trường hợp y&ecirc;u cầu cung cấp th&ecirc;m phiếu điểm để nộp&nbsp;<strong>hồ sơ tuyển dụng</strong>&nbsp;v&agrave;o c&aacute;c cơ quan/tổ chức kh&aacute;c nhau, th&iacute; sinh phải đạt đ<strong>iểm TOEIC từ 200 trở l&ecirc;n</strong>. Th&iacute; sinh cần phải xuất tr&igrave;nh CMND/Hộ chiếu gốc, bản ph&ocirc;-t&ocirc; phiếu điểm TOEIC, 01 ảnh 3x4 (giống với ảnh tr&ecirc;n phiếu điểm gốc) cho mỗi phiếu l&agrave;m th&ecirc;m, th&ocirc;ng tin nơi nhận phiếu điểm (bao gồm t&ecirc;n của c&aacute;n bộ nh&acirc;n sự v&agrave; địa chỉ doanh nghiệp) v&agrave; l&agrave;m thủ tục đăng k&yacute; trực tiếp tại văn ph&ograve;ng IIG Việt Nam. IIG Việt Nam sẽ chuyển Phiếu điểm đ&oacute; về địa chỉ C&ocirc;ng ty m&agrave; th&iacute; sinh nộp hồ sơ tuyển dụng sau 05 ng&agrave;y l&agrave;m việc. Lệ ph&iacute; cho mỗi phiếu điểm in li&ecirc;n hệ bộ phận quản l&yacute;.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\"><span style=\"color:#212529\">Nếu muốn y&ecirc;u cầu cung cấp th&ecirc;m phiếu điểm để nộp&nbsp;<strong>hồ sơ du học</strong>, th&iacute; sinh phải đạt&nbsp;<strong>điểm TOEIC từ 500 trở l&ecirc;n</strong>. Th&iacute; sinh cần phải xuất tr&igrave;nh bản ph&ocirc;-t&ocirc; nội dung văn bản y&ecirc;u cầu c&oacute; phiếu điểm TOEIC trong hồ sơ nộp v&agrave;o Trung t&acirc;m (VD: email, tờ rơi, th&ocirc;ng tin quảng c&aacute;o tr&ecirc;n b&aacute;o ch&iacute;...), CMND/Hộ chiếu gốc, bản ph&ocirc;-t&ocirc; phiếu điểm TOEIC, 01 ảnh 3x4 (giống với ảnh tr&ecirc;n phiếu điểm gốc) cho mỗi phiếu l&agrave;m th&ecirc;m v&agrave; l&agrave;m thủ tục đăng k&yacute; trực tiếp tại văn ph&ograve;ng TOEIC Việt Nam. Th&iacute; sinh sẽ phải điền th&ocirc;ng tin v&agrave; k&yacute; nhận v&agrave;o &ldquo;Đơn đề nghị cấp bản sao phiếu điểm TOEIC&rdquo;. Trong đơn, th&iacute; sinh phải ghi mục đ&iacute;ch l&agrave; ho&agrave;n thiện hồ sơ du học, cung cấp đầy đủ th&ocirc;ng tin về Trung t&acirc;m sẽ nộp hồ sơ (t&ecirc;n Trung t&acirc;m, địa chỉ chi tiết) v&agrave; cam đoan sử dụng phiếu điểm đ&uacute;ng mục đ&iacute;ch. Lệ ph&iacute; cho mỗi phiếu điểm in li&ecirc;n hệ bộ phận quản l&yacute;.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\"><span style=\"color:#212529\">Th&iacute; sinh c&oacute; thể y&ecirc;u cầu cung cấp&nbsp;<strong>bằng chứng nhận (Certificate of Achievement)</strong>. Th&iacute; sinh chỉ c&oacute; thể l&agrave;m được 01 bằng chứng nhận cho mỗi lần thi.v&agrave; sẽ nhận bằng sau 10 ng&agrave;y l&agrave;m việc kể từ ng&agrave;y y&ecirc;u cầu. Lệ ph&iacute; nhận bằng chứng nhận li&ecirc;n hệ bộ phận quản l&yacute;.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"color:#B22222\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><strong><u><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\">TOEIC 1&nbsp; - 350-450</span></span></u></strong></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\"><span style=\"color:#212529\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Gi&aacute;o tr&igrave;nh:&nbsp;Format TOEIC 2019<strong> + EVAS GRAMMAR.</strong></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\"><span style=\"color:#212529\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Thời lượng: 4 th&aacute;ng&nbsp;<strong>(16 tuần)</strong></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\"><span style=\"color:#212529\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Đối tượng: Học vi&ecirc;n nguyện vọng đạt 350-400</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><span style=\"color:#B22222\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\"><u>TOEIC 2&nbsp; - 450-550</u></span></span></strong></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\"><span style=\"color:#212529\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Gi&aacute;o tr&igrave;nh:&nbsp;Format TOEIC 2019<strong> + EVAS GRAMMAR.</strong></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\"><span style=\"color:#212529\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Thời lượng: 4 th&aacute;ng&nbsp;<strong>(16 tuần)</strong></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\"><span style=\"color:#212529\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Đối tượng: Học vi&ecirc;n nguyện vọng đạt 450-600</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"color:#B22222\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\"><u>TOEIC 3&nbsp; -&nbsp;550&nbsp;-990</u></span></span></strong></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\"><span style=\"color:#212529\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Gi&aacute;o tr&igrave;nh:&nbsp;<strong>TOEIC EXAM PREPARATION</strong></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\"><span style=\"color:#212529\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Thời lượng: 4 th&aacute;ng&nbsp;<strong>(16 tuần)</strong></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\"><span style=\"color:#212529\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Đối tượng: Học vi&ecirc;n nguyện vọng đạt 600-990</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"color:#B22222\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:18.0pt\">Mục ti&ecirc;u đ&agrave;o tạo chung:</span></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"color:#212529\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\">Nắm vững cấu tr&uacute;c đề thi theo h&igrave;nh thức thi đổi mới 6/2019;</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"color:#212529\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\">L&agrave;m quen với tất cả c&aacute;c dạng c&acirc;u hỏi;</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"color:#212529\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\">Ph&aacute;t triển chiến thuật l&agrave;m b&agrave;i thi;</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"color:#212529\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\">Ho&agrave;n th&agrave;nh c&aacute;c b&agrave;i thi mẫu;</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"color:#212529\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\">Tập trung luyện 2 kỹ năng nghe v&agrave; đọc;</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"color:#212529\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\">R&egrave;n luyện chiến thuật l&agrave;m b&agrave;i v&agrave; tạo cơ hội trải nghiệm m&ocirc;i trường thi thật kỳ thi giữa v&agrave; cuối kh&oacute;a học.</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"color:#212529\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\">Trang bị kiến thức, tư duy ng&ocirc;n ngữ v&agrave; kỹ năng ph&aacute;n đo&aacute;n cần thiết để học vi&ecirc;n tự tin đạt điểm cao;</span></span></span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"color:#B22222\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:18.0pt\">Tối ưu chương tr&igrave;nh:</span></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"color:#212529\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\">Trang bị kiến thức vững chắc kỳ thi TOEIC;</span></span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"color:#212529\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:segoe ui,sans-serif\">Th&iacute; sinh đăng thi v&agrave; nhận kết quả thi tại EVAS, hướng dẫn v&agrave; hỗ trợ&nbsp;hồ sơ đăng k&yacute; dự thi chi tiết nhất;</span></span></span></span></span></span></li>\r\n</ul>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<h2>&nbsp;</h2>\r\n</div>\r\n', 1, 6, '2020-03-17 15:12:19'),
(8, 'Anh Văn Theo Yêu Cầu', 'Học Theo Thời Gian Rảnh Của Bạn', 'anh-van-theo-yeu-cau', 'chuong-trinh-dao-tao/av-yeu-cau.jpg', 'Chương trình được thiết kế phù hợp cho từng học viên hoặc nhóm học. Lớp Anh văn theo yêu cầu với phương pháp dạy và học hoàn toàn mới cho phép người học hoàn toàn chủ động trong việc học...', '<p>Phương ph&aacute;p học tập &nbsp;cho ph&eacute;p học vi&ecirc;n c&oacute; thể đến lớp học bất kỳ thời gian n&agrave;o&nbsp;trong ng&agrave;y, trong tuần v&agrave; ho&agrave;n to&agrave;n chủ động về thời gian lẫn&nbsp;kế hoạch học tập, l&agrave;m việc của ri&ecirc;ng m&igrave;nh.</p>\r\n', '<div class=\"noidung\">\r\n<p><span style=\"font-size:16px\"><span style=\"color:#B22222\"><strong>M&Ocirc; TẢ CHƯƠNG TR&Igrave;NH HỌC</strong></span></span></p>\r\n\r\n<p>Chương tr&igrave;nh được thiết kế ph&ugrave; hợp cho từng học vi&ecirc;n hoặc nh&oacute;m học. Lớp Anh văn theo y&ecirc;u cầu với phương ph&aacute;p dạy v&agrave; học ho&agrave;n to&agrave;n mới cho ph&eacute;p người học ho&agrave;n to&agrave;n chủ động trong việc học. Đ&acirc;y l&agrave; một trong những yếu tố g&oacute;p phần quan trọng quyết định cho hiệu quả học tập của mỗi học vi&ecirc;n, tạo t&acirc;m l&yacute; thoải m&aacute;i v&agrave; h&agrave;o hứng, tr&aacute;nh mệt mỏi mỗi khi đến lớp. Phương ph&aacute;p n&agrave;y cho ph&eacute;p học vi&ecirc;n c&oacute; thể đến lớp học bất kỳ l&uacute;c n&agrave;o trong ng&agrave;y, trong tuần v&agrave; ho&agrave;n to&agrave;n chủ động về thời gian v&agrave; kế hoạch học tập, l&agrave;m việc của ri&ecirc;ng m&igrave;nh.</p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#B22222\"><strong>Tối ưu của kh&oacute;a học:</strong></span></span></p>\r\n\r\n<p>- Được học chương tr&igrave;nh thiết kế đ&uacute;ng y&ecirc;u cầu người học;</p>\r\n\r\n<p>- Đ&uacute;ng khả năng người học;</p>\r\n\r\n<p>- Được gi&aacute;o vi&ecirc;n v&agrave; nh&acirc;n vi&ecirc;n hỗ trợ thực h&agrave;nh v&agrave; phụ đạo ngo&agrave;i giờ học;</p>\r\n\r\n<p>- Kh&oacute;a học được thiết kế với mục đ&iacute;ch gi&uacute;p học vi&ecirc;n đạt mục ti&ecirc;u trong thời gian ngắn nhất, đ&aacute;p ứng c&aacute;c y&ecirc;u cầu đặc biệt của từng học vi&ecirc;n;</p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#B22222\"><strong>Thời gian học:</strong></span></span></p>\r\n\r\n<p>Theo y&ecirc;u cầu học vi&ecirc;n;</p>\r\n\r\n<p><strong>Đội ngủ gi&aacute;o vi&ecirc;n</strong>: Gi&aacute;o vi&ecirc;n nhiệt t&igrave;nh, năng động c&oacute; kinh nghiệm dạy tiếng Anh gi&uacute;p học vi&ecirc;n thoải m&aacute;i v&agrave; tiếp thu ng&ocirc;n ngữ dễ d&agrave;ng v&agrave; tự tin trong giao tiếp. Gi&aacute;o vi&ecirc;n đảm bảo 100% tiếng Anh tr&ecirc;n lớp;</p>\r\n\r\n<p><strong>Chứng chỉ v&agrave; bằng cấp</strong>: Học vi&ecirc;n sẽ được cấp giấy chứng nhận (Acheivement Certificate) của Trung t&acirc;m ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c sau khi ho&agrave;n th&agrave;nh kh&oacute;a học nếu c&oacute; nhu cầu.</p>\r\n\r\n<p>&nbsp;</p>\r\n</div>\r\n', 1, 7, '2020-05-31 05:30:15'),
(9, 'Chứng Chỉ 6 Bậc VSTEP - A1, A2, B1, B2, C1', 'Cam kết đầu ra', 'chung-chi-6-bac-vstep-a1-a2-b1-b2-c1', 'chuong-trinh-dao-tao/VSTEP.jpg', 'Giáo trình được Trung tâm biên soạn một cách logic, cũng cố kiến thức cơ bản để ứng dụng các bài thực hành một cách dễ dàng.', '<p>Gi&aacute;o tr&igrave;nh được Trung t&acirc;m bi&ecirc;n soạn chuy&ecirc;n biệt cho c&aacute;c bạn mục ti&ecirc;u lấy chứng chỉ, đ&aacute;p ứng cho c&ocirc;ng việc, y&ecirc;u cầu đầu ra c&aacute;c trường Đại học, Cao đẳng v&agrave; cơ hội thăng chức trong c&ocirc;ng việc.</p>\r\n', '<div class=\"noidung\">\r\n<p><span style=\"color:#B22222\"><strong>CHƯƠNG TR&Igrave;NH GIẢNG DẠY</strong></span></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Gi&aacute;o tr&igrave;nh được Trung t&acirc;m bi&ecirc;n soạn một c&aacute;ch logic, cũng cố kiến thức cơ bản để ứng dụng c&aacute;c b&agrave;i thực h&agrave;nh một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Gi&aacute;o tr&igrave;nh: <strong>A1-2 &ndash; Short</strong><strong>c</strong><strong>ut to Success.</strong></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Thời lượng: 4 th&aacute;ng <strong>(16 tuần)</strong></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Đối tượng: <strong>Học vi&ecirc;n nguyện vọng lấy chứng chỉ A2</strong><strong>.</strong></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Gi&aacute;o tr&igrave;nh: <strong>B1-B2-C1</strong><strong> &ndash; Short</strong><strong>c</strong><strong>ut to Success.</strong></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Thời lượng: 4 th&aacute;ng <strong>(16 tuần)</strong></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Đối tượng: <strong>Học vi&ecirc;n nguyện vọng lấy chứng chỉ </strong><strong>B1-B2-C1.</strong></p>\r\n\r\n<p><span style=\"color:#B22222\"><strong>MỤC TI&Ecirc;U Đ&Agrave;O TẠO</strong></span></p>\r\n\r\n<ul>\r\n	<li>Cũng cố kiến thức ngữ ph&aacute;p v&agrave; từ vựng căn bản;</li>\r\n	<li>Luyện bộ c&acirc;u hỏi Speaking thực tế b&agrave;i thi;</li>\r\n	<li>Hướng dẫn tự tin viết postcard v&agrave; notes thực tế b&agrave;i thi;</li>\r\n	<li>Nắm vững cấu tr&uacute;c đề thi;</li>\r\n	<li>L&agrave;m quen với tất cả c&aacute;c dạng c&acirc;u hỏi;</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>Ph&aacute;t triển chiến thuật l&agrave;m b&agrave;i thi kỹ năng Nghe v&agrave; Đọc;</li>\r\n	<li>Ho&agrave;n th&agrave;nh c&aacute;c b&agrave;i thi mẫu;</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#B22222\"><strong>TỐI ƯU CHƯƠNG TR&Igrave;NH</strong></span></p>\r\n\r\n<ul>\r\n	<li>Cam kết đạt 100% kết kh&oacute;a;</li>\r\n	<li>Được hỗ trợ nhiệt t&igrave;nh Gi&aacute;o vi&ecirc;n, Nh&acirc;n vi&ecirc;n để học vi&ecirc;n đạt kết quả tối đa;</li>\r\n	<li>Gi&aacute;o tr&igrave;nh đ&uacute;ng mẫu h&igrave;nh thức thi;</li>\r\n	<li>Kiến thức thi s&aacute;t 100% đề thi thật;</li>\r\n	<li>B&agrave;i thực h&agrave;nh s&aacute;t đề thi 60% đề thi thật;</li>\r\n	<li>Phụ đạo c&aacute;c kỹ năng nghe,n&oacute;i cho học vi&ecirc;n suốt lộ tr&igrave;nh &ocirc;n luyện;</li>\r\n	<li>T&agrave;i liệu lu&ocirc;n được bi&ecirc;n soạn cập nhật thường xuy&ecirc;n với đề thi hiện h&agrave;nh;</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#e74c3c\"><strong>Chứng chỉ v&agrave; bằng cấp</strong></span>: thi Hội Đồng c&oacute; gi&aacute; trị v&agrave; tin cậy về chất lượng uy t&iacute;n, Chứng chỉ c&oacute; gi&aacute; trị v&ocirc; thời hạn.</p>\r\n\r\n<p><strong><span style=\"color:#e74c3c\">HỘI ĐỒNG THI</span></strong><strong> - </strong>Trường ĐH Cần Thơ.</p>\r\n</div>\r\n', 1, 8, '2020-05-31 05:52:56');
INSERT INTO `education_video` (`id`, `link`, `description`, `status`, `created_at`) VALUES
(1, 'RkV3eySsBZs', '<p>&Acirc;u Việt &Uacute;c tự h&agrave;o l&agrave; Trung t&acirc;m Anh ngữ Quốc Tế&nbsp;hơn 10 năm&nbsp;tr&ecirc;n h&agrave;nh tr&igrave;nh đ&agrave;o tạo tiếng Anh.Trung t&acirc;m Ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c ra đời với &quot;sứ mệnh v&agrave; niềm tin tuyệt đối&quot; mang chất lượng giảng dạy tốt nhất, hiệu quả nhất gi&uacute;p cho từng hoc vi&ecirc;n đạt được một nền tảng ANH NGỮ vững chắc để thỏa m&atilde;n niềm kh&aacute;t khao, đam m&ecirc; chinh phục những tầm cao mới, v&agrave; cả việc tham gia v&agrave; hội nhập v&agrave;o &quot;Thế giới phẳng&quot; nơi cuộc C&aacute;ch Mạng C&ocirc;ng nghiệp lần thứ 4 đang diễn ra.</p>\r\n\r\n<p>C&ugrave;ng c&aacute;c b&eacute; trau dồi kỹ năng tiếng Anh - C&ocirc;ng&nbsp;cụ hữu hiệu để c&aacute;c bạn trẻ hội nhập v&agrave;o nền Gi&aacute;o dục Thế giới, từ đ&oacute; c&oacute; thể thực hiện ước mơ Du học tại c&aacute;c quốc gia c&oacute; nền gi&aacute;o dục ti&ecirc;n tiến, đồng thời c&oacute; thể gi&uacute;p c&aacute;c bạn tự tin kh&aacute;m ph&aacute; những v&ugrave;ng đất mới, nơi c&oacute; những nền văn h&oacute;a độc đ&aacute;o, đa sắc tộc...</p>\r\n', 1, '2020-05-13 13:22:31');
