INSERT INTO `job` (`id`, `title`, `detail`, `created_at`) VALUES
(1, 'Tuyển dụng', '<div class=\"container\">\r\n<h4><em><span style=\"color:#B22222\"><strong>| Vietnamese English Teachers</strong></span></em></h4>\r\n</div>\r\n\r\n<div class=\"container text-justify\">\r\n<h6><span style=\"font-size:12px\"><strong><em>Job description:</em></strong></span></h6>\r\n\r\n<p>- Covers all classes assigned in the monthly schedule and works cooperatively with the staffs, faculty and administration of the center;<br />\r\n- Teaching schedule: weekday evening and weekend availability</p>\r\n\r\n<h6><strong><em><span style=\"font-size:12px\">Benefits:</span></em></strong></h6>\r\n\r\n<p>- Competitive salary;<br />\r\n- Friendly and supportive work environment;</p>\r\n\r\n<h6><strong><em><span style=\"font-size:12px\">Requirements:</span></em></strong></h6>\r\n\r\n<p>- University degree in English foreign language;<br />\r\n- University degree with IELTS 6.5;</p>\r\n</div>\r\n\r\n<div class=\"container\">\r\n<h4><em><span style=\"color:#B22222\"><strong>| Foreign Teachers</strong></span></em></h4>\r\n</div>\r\n\r\n<div class=\"container text-justify\">\r\n<h6><span style=\"font-size:12px\"><strong><em>Job description:</em></strong></span></h6>\r\n\r\n<p>- Covers all classes assigned in the monthly schedule and works cooperatively with the staffs, faculty and administration of the center;<br />\r\n- Teaching schedule: weekday evening classes (6:00-9:00pm) &ndash; weekend morning classes (8:00-11:00am);</p>\r\n\r\n<h6><em><strong><span style=\"font-size:12px\">Benefits:</span></strong></em></h6>\r\n\r\n<p>- Competitive salary including house allowance and transportation allowance;<br />\r\n- Paid and sponsored working visa, work permit and health care check;<br />\r\n- Offered Lesson plans and instructional materials;<br />\r\n- Friendly and supportive work environment;</p>\r\n\r\n<h6><strong><em><span style=\"font-size:12px\">Requirements:</span></em></strong></h6>\r\n\r\n<p>- Eligible qualifications for work permit application<br />\r\n- University degree, teaching certificates, Criminal Background Check and standard health check, passport and valid visa.</p>\r\n</div>\r\n\r\n<div class=\"container\">\r\n<h4><em><span style=\"color:#B22222\"><strong>| Teaching Assistants</strong></span></em></h4>\r\n</div>\r\n\r\n<div class=\"container text-justify\">\r\n<h6><strong><em><span style=\"font-size:12px\">Job description:</span></em></strong></h6>\r\n\r\n<p>- Assist English Vietnamese teachers and Foreign Teachers;<br />\r\n- Teaching schedule: weekday evening and weekend availability</p>\r\n\r\n<h6><span style=\"font-size:12px\"><strong><em>Benefits:</em></strong></span></h6>\r\n\r\n<p>- Educational and practical work environment;</p>\r\n\r\n<h6><em><strong><span style=\"font-size:12px\">Requirements:</span></strong></em></h6>\r\n\r\n<p>- Students majoring English;<br />\r\n- Students with good English communication;</p>\r\n</div>\r\n\r\n<div class=\"container\">\r\n<h4><em><span style=\"color:#B22222\"><strong>| Training Staffs</strong></span></em></h4>\r\n</div>\r\n\r\n<div class=\"container text-justify\">\r\n<h6><strong><em><span style=\"font-size:12px\">Job description:</span></em></strong></h6>\r\n\r\n<p>- ACovers classes with EVAS programs;<br />\r\n- Consult the appropriate classes to students and students&rsquo; parents;<br />\r\n- Prepare educational materials for teachers;<br />\r\n- Gather feedback from trainees and trainers after each class;<br />\r\n- Working schedule: 48hours per week</p>\r\n\r\n<h6><em><strong><span style=\"font-size:12px\">Benefits:</span></strong></em></h6>\r\n\r\n<p>- Competitive salary;<br />\r\n- Vietnam labor policy;<br />\r\n- Annual outbound tour;<br />\r\n- Friendly and supportive work environment;</p>\r\n\r\n<h6><span style=\"font-size:12px\"><em><strong>Requirements:</strong></em></span></h6>\r\n\r\n<p>- College or University degree in English foreign language;<br />\r\n- University degree with IELTS 6.0;<br />\r\n&nbsp;</p>\r\n</div>\r\n', 2020);
INSERT INTO `news_items` (`id`, `title`, `code`, `description`, `detail`, `image`, `status`, `sort`, `created_at`, `updated`) VALUES
(49, 'BUỔI GIAO LƯU SINH VIÊN ĐH. AN GIANG!!!', 'buoi-giao-luu-sinh-vien-dhan-giang!!!', 'Sáng ngày 14/4/2018 Trung tâm Ngoại ngữ Quốc tế Âu Việt Úc đã có buổi giao lưu các bạn sinh viên ĐH. An Giang.', '<div class=\"noidung\">\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:times new roman,serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times,serif\"><span style=\"color:#1d2129\">S&aacute;ng ng&agrave;y 14/4/2018 Trung t&acirc;m Ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c đ&atilde; c&oacute; buổi giao lưu c&aacute;c bạn sinh vi&ecirc;n ĐH. </span></span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:times,serif\"><span style=\"color:#1d2129\">An Giang.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:times new roman,serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times,serif\"><span style=\"color:#1d2129\"><img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new111.png\" style=\"height:296px; width:527px\" /></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:times new roman,serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times,serif\"><span style=\"color:#1d2129\">Trong buổi giao luu, với sự hướng dẫn nhiệt t&igrave;nh, th&acirc;n thiện của c&ocirc; April Golenflo, c&aacute;c bạn sinh vi&ecirc;n tự tin n&oacute;i tiếng Anh trước nh&oacute;m đ&ocirc;ng;</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:times new roman,serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times,serif\"><span style=\"color:#1d2129\"><img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new112.png\" style=\"height:351px; width:624px\" /></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:times new roman,serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times,serif\"><span style=\"color:#1d2129\">Kh&ocirc;ng chỉ Trung t&acirc;m tổ chức những hoạt động khuyến kh&iacute;ch c&aacute;c bạn mạnh dạng giao tiếp tiếng Anh, buổi giao lưu n&agrave;y gi&uacute;p c&aacute;c bạn quen nhiều bạn mới hơn th&ocirc;ng qua hoạt động Challenge Your English v&agrave; My new Friend;</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:times new roman,serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times,serif\"><span style=\"color:#1d2129\"><img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new113.png\" /></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:times new roman,serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times,serif\"><span style=\"color:#1d2129\">QUAN TRỌNG HƠN CẢ - Trung t&acirc;m đ&atilde; mang đến cơ hội ứng tuyển việc l&agrave;m cho c&aacute;c bạn sinh vi&ecirc;n. Những t&agrave;i năng trẻ mong muốn g&oacute;p kiến thức v&agrave;o c&ocirc;ng t&aacute;c gi&aacute;o dục</span></span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"color:#1d2129\">.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:times new roman,serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"color:#1d2129\"><img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new114.png\" style=\"height:350px; width:624px\" /></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n</div>\r\n', 'tin-tuc/new111.png', 1, 7, '2020-02-18 16:33:06', '2020-02-18 15:33:06'),
(50, 'LỄ PHÁT BẰNG CAMBRIDGE 2017', 'le-phat-bang-cambridge-2017', 'Ngày 8/10/2017 vừa qua, EVAS đã long trọng tổ chức lễ phát bằng Cambridge cho 150 học viên xuất sắc đợt I&II.2017...', '<div class=\"noidung\">\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:times new roman,serif\"><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"font-family:helvetica,sans-serif\"><span style=\"color:#1d2129\">Ngày 8/10/2017 vừa qua, EVAS đã long trọng t&ocirc;̉ chức l&ecirc;̃ ph&aacute;t bằng Cambridge cho 150 học vi&ecirc;n xuất sắc đợt I&amp;II.2017.</span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:times new roman,serif\"><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"font-family:helvetica,sans-serif\"><span style=\"color:#1d2129\"><img alt=\"\" src=\"http://auvietuc.edu.vn//upload/images/tin-tuc/new101.png\" style=\"height:468px; width:624px\" /></span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:9.0pt\"><span style=\"background-color:white\"><span style=\"font-family:helvetica,sans-serif\"><span style=\"color:#666666\">Th&ocirc;ng qua bu&ocirc;̉i l&ecirc;̃, c&aacute;c em kh&ocirc;ng chỉ thể hiện sự tự tin khi tự d&acirc;̃n chương trình và trình di&ecirc;̃n các ti&ecirc;́t mục hoàn toàn bằng ti&ecirc;́ng anh, mà còn được c&ocirc;ng nhận m&ocirc;̣t quá trình luy&ecirc;̣n t&acirc;̣p chăm chỉ vừa qua của các con qua sự hướng d&acirc;̃n t&acirc;̣n tình của đ&ocirc;̣i ngũ EVAS.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:9.0pt\"><span style=\"background-color:white\"><span style=\"font-family:helvetica,sans-serif\"><span style=\"color:#666666\"><img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new102.png\" style=\"height:450px; width:624px\" /></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:12.0pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><span style=\"color:#666666\">C&aacute;c </span></span></span></span><span style=\"font-size:12.0pt\"><span style=\"background-color:white\"><span style=\"font-family:helvetica,sans-serif\"><span style=\"color:#666666\">con khi khoác l&ecirc;n mình chi&ecirc;́c áo tốt nghiệp đ&ecirc;̉ nh&acirc;̣n bằng STARTERS, MOVERS, FLYERS, KET, PET với s&ocirc;́ khi&ecirc;ng r&acirc;́t cao đã khẳng định được ch&acirc;́t lượng của Evas, cũng như sự đ&ocirc;̀ng hành, h&ocirc;̃ trợ từ các quý phụ huynh.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:12.0pt\"><span style=\"background-color:white\"><span style=\"font-family:helvetica,sans-serif\"><span style=\"color:#666666\"><img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new103.png\" style=\"height:415px; width:624px\" /></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n</div>\r\n', 'tin-tuc/blog5.jpg', 1, 8, '2020-02-19 00:00:00', '2020-02-18 15:33:06'),
(51, ' ĐÊM HỘI HALLOWEEN 2017!', 'dem-hoi-halloween-2017!', 'Lại một mùa Halloween nữa đã qua, các em học viên Trung tâm Ngoại ngữ Quốc tế Âu Việt Úc cùng bạn...', '<div class=\"noidung\">\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:times new roman,serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times,serif\"><span style=\"color:#1d2129\">Lại một m&ugrave;a Halloween nữa đ&atilde; qua, c&aacute;c em học vi&ecirc;n Trung t&acirc;m Ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c c&ugrave;ng bạn v&agrave; thầy c&ocirc; đ&atilde; lưu giữ lại những kỹ niệm đẹp th&ocirc;ng qua những bức h&igrave;nh h&oacute;a trang.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:times new roman,serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times,serif\"><span style=\"color:#1d2129\"><img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new91.png\" style=\"height:468px; width:624px\" /></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:times new roman,serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times,serif\"><span style=\"color:#1d2129\"><img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc//new92.png\" style=\"height:451px; width:624px\" /></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\">V&agrave; thầy c&ocirc; cũng t&iacute;ch cực tham gia buổi lễ hội n&agrave;y đấy.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new93.png\" style=\"height:558px; width:624px\" /></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n</div>\r\n', 'tin-tuc/blog3.jpg', 1, 8, '2020-02-20 16:33:06', '2020-02-18 15:33:06'),
(52, 'HỘI TRẠI HÈ 2018!!', 'hoi-trai-he-2018!!', 'Sáng ngày 10/8/2018 Trung tâm Ngoại ngữ Quốc tế Âu Việt Úc đã chức buổi cấm trại...', '<div class=\"noidung\">\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:times new roman,serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times,serif\"><span style=\"color:#1d2129\">S&aacute;ng ng&agrave;y </span></span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"color:#1d2129\">10/8/2018</span></span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:times,serif\"><span style=\"color:#1d2129\"> Trung t&acirc;m Ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c đ&atilde; chức buổi cấm trại cho c&aacute;c em học vi&ecirc;n của Trung t&acirc;m. </span></span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"color:#1d2129\">Buổi trại được tổ chức khu&ocirc;n vi&ecirc;n trường Đại học An Giang, với sự tham gia của 60 b&eacute; đến từ c&aacute;c cấp độ Twinkle Stars &ndash; Bright Stars &ndash; Super Stars &ndash; Starters &ndash; Movers &ndash; Flyers v&agrave; to&agrave;n thể thầy c&ocirc; Trung t&acirc;m.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:times new roman,serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"color:#1d2129\"><img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new81.png\" style=\"height:391px; width:521px\" /></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:times new roman,serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"color:#1d2129\">C&aacute;c em tự tin giao tiếp c&ugrave;ng bạn b&egrave; v&agrave; thầy c&ocirc; c&ugrave;ng với những hoạt động s&aacute;ng tạo vui nhộn, giao tiếp bằng tiếng Anh suốt buổi sinh hoạt.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:times new roman,serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"color:#1d2129\"><img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new82.png\" style=\"height:291px; width:388px\" /></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:times new roman,serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times,serif\"><span style=\"color:#1d2129\">Kh&ocirc;ng chỉ Trung t&acirc;m tổ chức những hoạt động khuyến kh&iacute;ch c&aacute;c bạn mạnh dạng giao tiếp tiếng Anh, buổi </span></span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"color:#1d2129\">cấm trại n&agrave;y c&aacute;c em c&ograve;n được tham gia c&aacute;c tr&ograve; chơi vận động vui nhộn c&ugrave;ng thầy c&ocirc;.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:times new roman,serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"color:#1d2129\"><img alt=\"\" src=\"http://auvietuc.edu.vn//upload/images/tin-tuc/new83.png\" style=\"height:468px; width:624px\" /></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:times new roman,serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"color:#1d2129\">C&ugrave;ng với điệu nhảy nh&iacute; nhỏm của ch&uacute; bee, c&aacute;c em c&oacute; một ng&agrave;y sinh hoạt hoạt kh&oacute;a thật vui, thật hữu dụng.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n</div>\r\n', 'tin-tuc/blog2.jpg', 1, 8, '2020-02-21 16:33:06', '2020-02-18 15:33:06'),
(53, 'Macmillan Training Day', 'macmillan-training-day', 'Vào lúc 8:30, ngày 7 tháng 9 năm 2017 tại Khách sạn Hòa Bình I. Trung tâm Ngoại ngữ Quốc tế Âu Việt Úc...', '<div class=\"noidung\">\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"font-family:times,serif\"><span style=\"color:#1d2129\">V&agrave;o l&uacute;c 8:30, ng&agrave;y 7 th&aacute;ng 9 năm 2017 tại Kh&aacute;ch sạn H&ograve;a B&igrave;nh I. Trung t&acirc;m Ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c phối hợp c&ugrave;ng nh&agrave; xuất bản Macmillan đến từ Anh tổ chức buổi workshop. C&ugrave;ng với sự tham dự&nbsp;&nbsp;</span></span></span></span><span style=\"font-size:10.5pt\"><span style=\"font-family:times,serif\"><span style=\"color:#1d2129\"><span style=\"background-color:white\">Đại diện Nh&agrave; xuất bản Macmillan&nbsp;</span><br />\r\n<span style=\"background-color:white\">- &Ocirc;ng David Kaye&nbsp;<br />\r\nĐại diện Trung t&acirc;m&nbsp;<br />\r\n- Gi&aacute;m đốc Trung t&acirc;m &ndash; B&agrave; La Ngoc Huong&nbsp;<br />\r\n- Gi&aacute;o vi&ecirc;n Trung t&acirc;m &ndash; &Ocirc;ng Grant Robert Hunter&nbsp;</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:10.5pt\"><span style=\"font-family:times,serif\"><span style=\"color:#1d2129\"><span style=\"background-color:white\"><img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new71.png\" style=\"height:338px; width:602px\" /></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"font-family:times,serif\"><span style=\"color:#1d2129\">Buổi hội thảo diễn ra rất th&agrave;nh c&ocirc;ng mang lại cho </span></span></span></span><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"font-family:times,serif\"><span style=\"color:#1d2129\">gi&aacute;o vi&ecirc;n bộ m&ocirc;n Tiếng anh sử dụng gi&aacute;o tr&igrave;nh v&agrave; c&aacute;c phần mềm hỗ trợ giảng dạy bằng c&ocirc;ng nghệ hiện đại.&nbsp;</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new72.png\" style=\"height:338px; width:602px\" /></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"color:#1d2129\">Kết th&uacute;c buổi tập huấn của &Ocirc;ng. </span></span></span><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"font-family:times,serif\"><span style=\"color:#1d2129\">David Kaye</span></span></span></span><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"color:#1d2129\">, thầy c&ocirc; Trung t&acirc;m Ngoại ngữ quốc tế &Acirc;u Việt &Uacute;c đ&atilde; t&iacute;ch lũy kiến thức chuy&ecirc;n m&ocirc;n, hứa hẹn sẽ mang lại phương ph&aacute;p giảng dạy mới lạ v&agrave; chất lượng tối ưu cho người học.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"color:#1d2129\"><img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new73.png\" style=\"height:338px; width:602px\" /></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n</div>\r\n', 'tin-tuc/blog1.jpg', 1, 8, '2020-02-18 16:33:06', '2020-02-18 15:33:06'),
(54, 'NGOẠI KHÓA HOMESTAY CÙNG GIÁO VIÊN BẢN XỨ', 'ngoai-khoa-homestay-cung-giao-vien-ban-xu', 'Nằm trong chuỗi hoạt động ngoại khóa, Trung tâm Ngoại ngữ Quốc tế Âu Việt Úc đã tổ chức cho...', '<div class=\"noidung\">\r\n<p><span style=\"font-size:14.0pt\"><span style=\"font-family:times new roman,serif\">Nằm trong chuỗi hoạt động ngoại kh&oacute;a</span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:times new roman,serif\">, </span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:times new roman,serif\">Trung t&acirc;m </span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:times new roman,serif\">Ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c </span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:times new roman,serif\">đ&atilde; tổ chức cho c&aacute;c b&eacute; học vi&ecirc;n</span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:times new roman,serif\"> tham <strong>gia một ng&agrave;y trải nghiệm sống v&agrave; học tập c&ugrave;ng gi&aacute;o vi&ecirc;n Nước Ngo&agrave;i</strong>.</span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times new roman,serif\"><img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new61.png\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times new roman,serif\">Nhằm tạo ra m&ocirc;i trường học tiếng Anh hiệu quả Evas kh&ocirc;ng ngừng s&aacute;ng tạo c&aacute;c m&ocirc; h&igrave;nh học tập s&ocirc;i động, thực tế v&agrave; khơi dậy đam m&ecirc; ng&ocirc;n ngữ c&aacute;c em nhỏ, gi&uacute;p c&aacute;c em tự tin v&agrave; giao tiếp lưu lo&aacute;t với người bản xứ.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times new roman,serif\"><img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new62.png\" /></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times new roman,serif\">C&aacute;c em c&oacute; một ng&agrave;y thật đặc biệt</span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:times new roman,serif\"> v&agrave; </span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:times new roman,serif\">tuyệt vời sống v&agrave; trải nghiệm c&ugrave;ng thầy Grant Hunter.</span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times new roman,serif\"><img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new63.png\" /></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times new roman,serif\">Mỗi nh&oacute;m được chia 4-5 b&eacute;, c&aacute;c b&eacute; c&ugrave;ng thầy chuẩn bị bữa ăn, qu&eacute;t s&agrave;n rửa ch&eacute;n&hellip;. c&ugrave;ng thầy đ&agrave;m thoại chủ đề thường ng&agrave;y v&agrave; thực h&agrave;nh c&aacute;c b&agrave;i tập nh&oacute;m n&acirc;ng cao kỹ n&acirc;ng tiếng Anh.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times new roman,serif\">Điểm chung của c&aacute;c b&eacute; l&agrave; rất phấn khởi, vui vẻ tham gia từng hoạt động. Sự dạn dĩ giao tiếp c&ugrave;ng bạn b&egrave;, GVNN cũng ng&agrave;y c&agrave;ng n&acirc;ng cao.</span></span></span></span></p>\r\n</div>\r\n', 'tin-tuc/blog.jpg', 1, 6, '2020-02-18 16:33:06', '2020-02-18 15:33:06'),
(55, 'CÂU LẠC BỘ TIẾNG ANH EVAS', 'cau-lac-bo-tieng-anh-evas', 'Câu Lạc Bộ tiếng Anh EVAS được Trung Tâm Ngoại ngữ Quốc tế Âu Việt Úc tổ chức thường xuyên...', '<div class=\"noidung\">\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times new roman,serif\">C&acirc;u Lạc Bộ tiếng Anh EVAS được Trung T&acirc;m Ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c tổ chức thường xuy&ecirc;n v&agrave;o ng&agrave;y cuối tuần. Thấu hiểu được tầm quan trọng của m&ocirc;i trường luyện giao tiếp tiếng Anh, Evas tổ chức Club với sự tham gia Gi&aacute;o vi&ecirc;n Nước ngo&agrave;i nhằm gi&uacute;p học vi&ecirc;n trong v&agrave; ngo&agrave;i Trung t&acirc;m tự tin giao tiếp, x&oacute;a bỏ khoảng c&aacute;ch sợ sợt giao tiếp tiếng Anh đặc biệt với Gi&aacute;o vi&ecirc;n nước ngo&agrave;i.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n<img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new51.png\" style=\"height:369px; width:553px\" />\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times new roman,serif\">C&aacute;c chủ đề h&agrave;ng tuần được thảo luận gắn liền với c&ocirc;ng việc thực tế, c&aacute;c vấn đề x&atilde; hội, văn h&oacute;a, đang được mọi người quan t&acirc;m, mang t&iacute;nh thiết thực cao. C&ugrave;ng với sự tổ chức chuy&ecirc;n nghiệp với sự hỗ trợ v&agrave; gi&uacute;p đỡ&nbsp; nhiệt t&igrave;nh c&aacute;c th&agrave;nh vi&ecirc;n Club, th&agrave;nh vi&ecirc;n mới hoặc c&aacute;c bạn mới tham gia lần đầu sẽ kh&ocirc;ng phải bở ngỡ.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n<img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new52.png\" style=\"height:373px; width:576px\" />\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times new roman,serif\">Để tạo kh&ocirc;ng kh&iacute; s&ocirc;i động Club được thiết kế với c&aacute;c tr&ograve; chơi tuy duy tiếng Anh, kỹ năng giải quyết vấn đề v&agrave; giao tiếp trước đ&aacute;m đ&ocirc;ng. Qua đ&acirc;y học vi&ecirc;n kh&ocirc;ng chỉ cải thiện kỹ năng mền m&agrave; c&ograve;n n&acirc;ng cao cốn từ vựng tiếng Anh, cải thiện ngữ ph&aacute;p, thực h&agrave;nh c&aacute;c t&igrave;nh huống giao tiếp tiếng Anh h&agrave;ng ng&agrave;y v&ocirc; c&ugrave;ng thực tế.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n<img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new53.png\" style=\"height:382px; width:576px\" />\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times new roman,serif\">Nếu c&oacute; cơ hội, c&aacute;c bạn h&atilde;y c&ugrave;ng tham gia Evas CLUB để được giao lưu, học tập từ c&aacute;c giảng vi&ecirc;n nước ngo&agrave;i, chuy&ecirc;n gia đ&agrave;o tạo Việt Nam. Đ&oacute; l&agrave; điều kiện v&ocirc; c&ugrave;ng thuận lợi để n&acirc;ng cao kỹ năng nghe n&oacute;i của m&igrave;nh.</span></span></span></span></p>\r\n</div>\r\n', 'tin-tuc/new51.png', 1, 8, '2020-03-02 02:33:52', '2020-03-02 02:33:52'),
(56, 'Hỗ trợ Sinh Viên Ngày Hội Việc Làm Trường Đại Học An Giang', 'ho-tro-sinh-vien-ngay-hoi-viec-lam-truong-dai-hoc-an-giang', 'Hàng năm Trung tâm Ngoại ngữ Quốc tế Âu Việt Úc tham gia ngày Ngày hội việc làm...', '<div class=\"noidung\">\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:times new roman,serif\"><span style=\"font-size:14.0pt\"><span style=\"color:black\">H&agrave;ng năm Trung t&acirc;m Ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c tham gia ng&agrave;y Ng&agrave;y hội việc l&agrave;m do trường Trường Đại học An Giang đ&atilde; tổ chức. Với mong muốn tạo điều kiện c&aacute;c bạn sinh vi&ecirc;n tiếp cận đến hệ thống đ&agrave;o tạo của Trung t&acirc;m, theo đuổi chuy&ecirc;n s&acirc;u chuy&ecirc;n ng&agrave;nh được đ&agrave;o tạo, khi ra trường đủ điều kiện ứng tuyển v&agrave;o Trung t&acirc;m l&agrave;m việc. Trung t&acirc;m cũng tạo s&acirc;n chơi bổ &iacute;ch gi&uacute;p c&aacute;c em c&oacute; cơ hội giao tiếp người nước ngo&agrave;i v&agrave; khơi dậy đam m&ecirc; tiếng Anh c&aacute;c bạn trẻ.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\">&nbsp;</p>\r\n<img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new41.png\" style=\"height:347px; width:501px\" />\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times new roman,serif\">Đồng nh&agrave; t&agrave;i trợ ch&iacute;nh của chương tr&igrave;nh, đại diện Trường Đại học An Giang trao hoa v&agrave; qu&agrave; lưu niệm cho Trung t&acirc;m.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\">&nbsp;</p>\r\n<img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new42.png\" style=\"height:334px; width:501px\" />\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times new roman,serif\">Buổi phỏng vấn trao học bổng thầy Grant Hunter với sinh vi&ecirc;n Trường Đại học An Giang</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\">&nbsp;</p>\r\n<img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new43.png\" style=\"height:338px; width:602px\" />\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times new roman,serif\">C&aacute;c bạn sinh vi&ecirc;n đang được c&aacute;n bộ nh&acirc;n vi&ecirc;n Trung t&acirc;m tư vấn v&agrave; hướng dẫn lộ tr&igrave;nh học tiếng Anh hiệu quả</span></span></span></span></p>\r\n</div>\r\n', 'tin-tuc/new41.png', 1, 8, '2020-03-02 02:40:06', '2020-03-02 02:40:06'),
(57, 'HỌC VIÊN EVAS ĐÓN GIÁNG SINH ẤM ÁP CÙNG THẦY CÔ', 'hoc-vien-evas-don-giang-sinh-am-ap-cung-thay-co', 'Tối ngày 19 tháng 12 năm 2016, tại Tiny World, Trung tâm Ngoại ngữ Quốc tế Âu Việt Úc...', '<div class=\"noidung\">\r\n<p style=\"text-align:justify\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times new roman,serif\">Tối ng&agrave;y 19 th&aacute;ng 12 năm 2016, tại Tiny World, Trung t&acirc;m Ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c tổ chức Đ&ecirc;m lễ Gi&aacute;ng sinh nhằm đ&oacute;n một m&ugrave;a Gi&aacute;ng sinh an l&agrave;nh v&agrave; ch&agrave;o mừng năm mới 2017 c&ugrave;ng với sự tham dự nhiệt t&igrave;nh v&agrave; cổ vũ s&ocirc;i nổi của đ&ocirc;ng đảo c&aacute;c em học vi&ecirc;n v&agrave; c&aacute;n bộ nh&acirc;n vi&ecirc;n Evas. </span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n<img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new31.png\" style=\"height:339px; width:602px\" />\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times new roman,serif\">Trung t&acirc;m Ngoại ngữ&nbsp; Quốc tế &Acirc;u Việt &Uacute;c l&agrave; một trung t&acirc;m Anh ngữ kh&ocirc;ng chỉ đi đầu về chất lượng đ&agrave;o tạo m&agrave; c&ograve;n l&agrave; đơn vị c&oacute; nhiều đ&oacute;n g&oacute;p t&iacute;ch cực cho cộng đồng, lu&ocirc;n lu&ocirc;n song h&agrave;nh với chương tr&igrave;nh nhằm mang đến c&aacute;c em thiếu nhi giao lưu, r&egrave;n luyện kỹ năng giao tiếp, sử dụng Anh văn đồng thời l&agrave; dịp để mọi người x&iacute;ch lại gần nhau, hiểu nhau hơn .</span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n<img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new32.png\" style=\"height:351px; width:603px\" />\r\n<p style=\"text-align:justify\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times new roman,serif\">C&ugrave;ng với c&aacute;c hoạt động vui tươi tại c&aacute;c gian h&agrave;ng tr&ograve; chơi, c&aacute;c gian h&agrave;ng nghệ thuật cũng kh&ocirc;ng k&eacute;m phần thu h&uacute;t.</span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n<img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new33.png\" style=\"height:339px; width:602px\" />\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:times new roman,serif\">Kết th&uacute;c trong kh&ocirc;ng kh&iacute; rộn r&atilde;, ấm &aacute;p tiếng cười v&agrave; ngập tr&agrave;n niềm vui, c&aacute;c em ch&agrave;o tạm biệt thầy c&ocirc; trong sự tiếc nuối v&agrave; muốn thời gian quay lại để được tận hưởng kh&ocirc;ng kh&iacute; vui nhộn của Lễ Hội Gi&aacute;ng Sinh th&ecirc;m một lần nữa. Evas xin hẹn gặp lại c&aacute;c b&eacute; v&agrave; Qu&iacute; Phụ huynh v&agrave;o Lễ Hội Gi&aacute;ng Sinh năm sau nh&eacute;</span></span></span></span></p>\r\n</div>\r\n', 'tin-tuc/new31.png', 1, 8, '2020-03-02 02:42:06', '2020-03-02 02:42:06'),
(58, 'TRUNG TÂM EVAS KHAI TRƯƠNG CHI NHÁNH 2', 'trung-tam-evas-khai-truong-chi-nhanh-2', 'Tại buổi khai trương, Trung tâm Ngoại ngữ Quốc tế Âu Việt Úc đã vinh dự được tiếp đón quý vị đại biểu...', '<div class=\"noidung\">\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:times new roman,serif\"><span style=\"font-size:14.0pt\"><span style=\"color:#060606\">Tại buổi khai trương, Trung t&acirc;m Ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c đ&atilde; vinh dự được tiếp đ&oacute;n qu&yacute; vị đại biểu l&agrave; đại diện Sở, Ban, Ng&agrave;nh tỉnh An Giang c&ugrave;ng hơn 100 kh&aacute;ch mời l&agrave; gi&aacute;o vi&ecirc;n, phụ huynh, học sinh tr&ecirc;n địa b&agrave;n th&agrave;nh phố đến tham dự chương tr&igrave;nh khai trương ng&agrave;y.</span></span></span></span></span></p>\r\n<img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new21.png\" />\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:times new roman,serif\"><span style=\"font-size:14.0pt\"><span style=\"color:#060606\">Ph&aacute;t biểu trong buổi lễ khai trương, b&agrave; La Ngọc Hương Gi&aacute;m đốc Trung t&acirc;m Ngoại Ngữ Quốc tế &Acirc;u Việt &Uacute;c cho biết: &ldquo;Nhằm mục đ&iacute;ch thực hiện sứ mệnh của Trung t&acirc;m đ&agrave;o tạo nhiều học vi&ecirc;n chuẩn h&oacute;a tiếng Anh, Ch&uacute;ng t&ocirc;i x&uacute;c tiến th&agrave;nh lập Chi nh&aacute;nh tr&ecirc;n c&ugrave;ng địa b&agrave;n th&agrave;nh phố Long Xuy&ecirc;n địa điểm 79 Trần Hưng Đạo, ch&uacute;ng t&ocirc;i rất tự h&agrave;o l&agrave; một trong những Trung t&acirc;m đ&agrave;o tạo tiếng Anh đầu ti&ecirc;n đạt chuẩn về chương tr&igrave;nh đ&agrave;o tạo lẫn cơ sở vật chất v&agrave; đội ngũ giảng dạy tại tỉnh An Giang. Trung t&acirc;m Ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c được hợp t&aacute;c v&agrave; hỗ trợ bởi những người c&oacute; chuy&ecirc;n m&ocirc;n cao, kinh nghiệm l&acirc;u năm trong lĩnh vực giảng dạy v&agrave; nghi&ecirc;n cứu. Đặc biệt họ lu&ocirc;n ấp ủ mong muốn được đ&oacute;ng g&oacute;p sức lực v&agrave; chuy&ecirc;n m&ocirc;n của m&igrave;nh cho việc ph&aacute;t triển gi&aacute;o dục n&oacute;i chung v&agrave; n&acirc;ng cao chất lượng dạy-học lĩnh vực ngoại ngữ, đặc biệt l&agrave; tiếng Anh n&oacute;i ri&ecirc;ng cho qu&ecirc; hương An Giang. Ch&uacute;ng t&ocirc;i cam kết, sẽ đem đến những chương tr&igrave;nh học tiếng Anh chất lượng từ đội ngũ nh&acirc;n vi&ecirc;n đầy nhiệt huyết v&agrave; gi&aacute;o vi&ecirc;n bản xứ gi&agrave;u kinh nghiệm từ đ&oacute; g&oacute;p phần n&acirc;ng cao khả năng sử dụng tiếng Anh cho những người d&acirc;n nơi đ&acirc;y.</span></span></span></span></span></p>\r\n<img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new22.png\" />\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:times new roman,serif\"><span style=\"font-size:14.0pt\"><span style=\"color:#060606\">Lễ cắt băng khai trương đ&atilde; được diễn ra ngay sau đ&oacute; trước sự chứng kiến của tất cả c&aacute;c qu&iacute; vị quan kh&aacute;ch v&agrave; đ&ocirc;ng đảo học vi&ecirc;n, phụ huynh. Cũng trong buổi s&aacute;ng ng&agrave;y khai trương, tất cả nh&acirc;n vi&ecirc;n v&agrave; gi&aacute;o vi&ecirc;n EVAS đ&atilde; tiếp h&agrave;ng trăm lượt phụ huynh c&ugrave;ng học sinh đến t&igrave;m hiểu về chương tr&igrave;nh học, tham quan cơ sở vật chất của EVAS cũng như tham gia thi xếp lớp với c&aacute;c gi&aacute;o vi&ecirc;n nước ngo&agrave;i.</span></span></span></span></span></p>\r\n</div>\r\n', 'tin-tuc/new22.png', 1, 7, '2020-03-02 02:48:20', '2020-03-02 02:48:20'),
(59, ' LỄ PHÁT BẰNG CAMBRIDGE ĐỢT III NĂM 2016', 'le-phat-bang-cambridge-dot-iii-nam-2016', 'Ngày 19 tháng 7 năm 2016, Trung tâm Ngoại ngữ quốc tế Âu Việt Úc long trọng tổ chức lễ vinh danh...', '<div class=\"noidung\">\r\n<p><span style=\"font-size:14.0pt\"><span style=\"color:#333333\">Ng&agrave;y 19 th&aacute;ng 7 năm 2016, Trung t&acirc;m Ngoại ngữ quốc tế &Acirc;u Việt &Uacute;c long trọng tổ chức lễ vinh danh v&agrave; trao bằng Quốc tế Cambrigde hơn 100 học vi&ecirc;n thiếu nhi, bao gồm c&aacute;c cấp độ STARTERS, MOVERS v&agrave; FLYERS, do đơn vị Khảo th&iacute; Cambridge ESOL của Đại học Cambrigde (Anh) cấp. Đ&acirc;y l&agrave; đợt trao chứng chỉ thứ ba trong năm 2017.</span></span></p>\r\n<img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new11.png\" />\r\n<p><span style=\"font-size:14.0pt\"><span style=\"color:#1a1a1a\">Tại buổi lễ, Đại diện của Trung t&acirc;m Ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c &ndash; Gi&aacute;m đốc Trung t&acirc;m b&agrave; La Ngọc Hương v&agrave; c&ugrave;ng c&aacute;c thầy c&ocirc; chuy&ecirc;n gia nước ngo&agrave;i đ&atilde; trao chứng chỉ cho 45 học vi&ecirc;n Starters, 35 học vi&ecirc;n Movers v&agrave; 45 học vi&ecirc;n Flyers. </span></span></p>\r\n<img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new12.png\" />\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><strong><span style=\"font-size:14.0pt\"><span style=\"color:#333333\">Diễn ra c&ugrave;ng với lễ ph&aacute;t bằng c&ograve;n kết hợp ca nhạc, thời trang, m&uacute;a, kịch, nhảy hiện đại, do c&aacute;c học vi&ecirc;n của trung t&acirc;m biểu diễn v&agrave; sử dụng 100% tiếng Anh, Qua đ&oacute;, gi&uacute;p c&aacute;c b&eacute; r&egrave;n luyện, trau dồi khả năng tiếng Anh, r&egrave;n luyện sự tự tin v&agrave; khả năng tr&igrave;nh diễn, n&oacute;i trước đ&aacute;m đ&ocirc;ng.&nbsp;</span></span></strong></span></span></span></p>\r\n<img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new13.png\" />\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"color:#333333\">Để tham dự kỳ thi lấy chứng chỉ Cambridge Starters, Movers v&agrave; Flyers, học vi&ecirc;n Evas chỉ cần ho&agrave;n th&agrave;nh c&aacute;c cấp độ của chương tr&igrave;nh Anh ngữ Thiếu nhi SuperKids. Được thiết kế cho học sinh tiểu học từ 7-12 tuổi với mục ti&ecirc;u x&acirc;y dựng nền tảng Anh ngữ vững chắc, mang đến cho học vi&ecirc;n cơ hội ph&aacute;t triển đều 4 kỹ năng nghe, n&oacute;i, đọc, viết, ph&aacute;t &acirc;m chuẩn cũng như r&egrave;n luyện sự tự tin. Kết quả c&aacute;c kỳ thi chứng chỉ Cambridge n&agrave;y đ&atilde; trở th&agrave;nh nền m&oacute;ng quan trọng đối với học sinh tiểu học tại Việt Nam trong h&agrave;nh tr&igrave;nh chinh phục tri thức thế giới, h&ograve;a nhập cộng đồng quốc tế.</span></span> </span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:calibri,sans-serif\">&nbsp;</span></span></span></p>\r\n</div>\r\n', 'tin-tuc/new12.png', 1, 8, '2020-03-02 02:52:14', '2020-03-02 02:52:14');
INSERT INTO `news_items` (`id`, `title`, `code`, `description`, `detail`, `image`, `status`, `sort`, `created_at`, `updated`) VALUES
(60, 'THI VĂN NGHỆ BE A STAR – THE VOICE OF EVAS', 'thi-van-nghe-be-a-star-–-the-voice-of-evas', 'Vừa qua ngày 3/6/2018, EVAS đã tổ chức thành công Trung tâm tổ chức cuộc thi BE A STAR...', '<div class=\"noidung\"><img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new12_1.png\" />\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"color:black\">Vừa qua ng&agrave;y 3/6/2018, EVAS đ&atilde; tổ chức th&agrave;nh c&ocirc;ng Trung t&acirc;m tổ chức cuộc thi BE A STAR, c&aacute;c con rất giỏi, tự tin buổi diễn tr&ecirc;n s&acirc;n khấu. Qua chương tr&igrave;nh n&agrave;y EVAS rất tự h&agrave;o về c&aacute;c con. D&ugrave; thắng hay kh&ocirc;ng th&igrave; quan trọng nhất l&agrave;: </span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"color:black\">❤️</span><span style=\"color:black\"> C</span><span style=\"color:black\">&aacute;</span><span style=\"color:black\">c con c</span><span style=\"color:black\">&oacute;</span><span style=\"color:black\"> bu</span><span style=\"color:black\">ổ</span><span style=\"color:black\">i t</span><span style=\"color:black\">ố</span><span style=\"color:black\">i th</span><span style=\"color:black\">ưở</span><span style=\"color:black\">ng th</span><span style=\"color:black\">ứ</span><span style=\"color:black\">c c</span><span style=\"color:black\">&aacute;</span><span style=\"color:black\">c ti</span><span style=\"color:black\">ế</span><span style=\"color:black\">t m</span><span style=\"color:black\">ụ</span><span style=\"color:black\">c bu</span><span style=\"color:black\">ổ</span><span style=\"color:black\">i bi</span><span style=\"color:black\">ể</span><span style=\"color:black\">u di</span><span style=\"color:black\">ễ</span><span style=\"color:black\">n 100% ti</span><span style=\"color:black\">ế</span><span style=\"color:black\">ng Anh;</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"color:black\">❤️</span><span style=\"color:black\">Con t</span><span style=\"color:black\">ự</span><span style=\"color:black\"> tin bu</span><span style=\"color:black\">ổ</span><span style=\"color:black\">i di</span><span style=\"color:black\">ễ</span><span style=\"color:black\">n tr</span><span style=\"color:black\">&ecirc;</span><span style=\"color:black\">n s</span><span style=\"color:black\">&acirc;</span><span style=\"color:black\">n kh</span><span style=\"color:black\">ấ</span><span style=\"color:black\">u;</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"color:black\">❤️</span><span style=\"color:black\"> H</span><span style=\"color:black\">&aacute;</span><span style=\"color:black\">t nh</span><span style=\"color:black\">ữ</span><span style=\"color:black\">ng b</span><span style=\"color:black\">&agrave;</span><span style=\"color:black\">i h</span><span style=\"color:black\">&aacute;</span><span style=\"color:black\">t ti</span><span style=\"color:black\">ế</span><span style=\"color:black\">ng Anh thuyết phục kh&aacute;n giả;</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"color:black\">❤️</span><span style=\"color:black\">L</span><span style=\"color:black\">&agrave;</span><span style=\"color:black\"> n</span><span style=\"color:black\">ơ</span><span style=\"color:black\">i c</span><span style=\"color:black\">&aacute;</span><span style=\"color:black\">c con giao l</span><span style=\"color:black\">ư</span><span style=\"color:black\">u c</span><span style=\"color:black\">&aacute;</span><span style=\"color:black\">c anh ch</span><span style=\"color:black\">ị</span><span style=\"color:black\"> t</span><span style=\"color:black\">ự</span><span style=\"color:black\"> tin </span><span style=\"color:black\">đứ</span><span style=\"color:black\">ng s</span><span style=\"color:black\">&acirc;</span><span style=\"color:black\">n kh</span><span style=\"color:black\">ấ</span><span style=\"color:black\">u tr</span><span style=\"color:black\">ả</span><span style=\"color:black\"> l</span><span style=\"color:black\">ờ</span><span style=\"color:black\">i v</span><span style=\"color:black\">ấ</span><span style=\"color:black\">n </span><span style=\"color:black\">đ&aacute;</span><span style=\"color:black\">p gi</span><span style=\"color:black\">&aacute;</span><span style=\"color:black\">m kh</span><span style=\"color:black\">ả</span><span style=\"color:black\">o v</span><span style=\"color:black\">&agrave;</span><span style=\"color:black\"> c</span><span style=\"color:black\">&aacute;</span><span style=\"color:black\">c b</span><span style=\"color:black\">ạ</span><span style=\"color:black\">n t</span><span style=\"color:black\">ự</span><span style=\"color:black\"> tin k</span><span style=\"color:black\">ể</span><span style=\"color:black\"> chuy</span><span style=\"color:black\">ệ</span><span style=\"color:black\">n, di</span><span style=\"color:black\">ễ</span><span style=\"color:black\">n xu</span><span style=\"color:black\">ấ</span><span style=\"color:black\">t v</span><span style=\"color:black\">&agrave;</span><span style=\"color:black\"> l</span><span style=\"color:black\">&agrave;</span><span style=\"color:black\">m MC.</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"color:black\">❤️</span><span style=\"color:black\">V&agrave; đặc việt c&aacute;c con c&oacute; những buổi tập với những kỷ niệm đẹp với c&aacute;c bạn c&ugrave;ng lớp v&agrave; với thầy c&ocirc; EVAS.</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\">&nbsp;</p>\r\n<img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new12_2.png\" />\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"color:black\">Kết hợp với chương tr&igrave;nh BE A STAR l&agrave; buổi thi vấn đ&aacute;p 5 bạn v&agrave;o v&ograve;ng chung kết The Voice of EVAS. </span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"color:black\">V&agrave; giải nhất thuộc về em: Phan Ngọc Nhật H&agrave; &ndash; Linda (IELTS)</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"color:black\">Giải nh&igrave; thuộc về em: Trần &Aacute;nh Ngọc &ndash; Kelly (FLYERS)</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"color:black\">Đồng giải nh&igrave; thuộc về em: Trần Khải Qu&acirc;n &ndash; Will (IELTS)</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\">&nbsp;</p>\r\n<img alt=\"\" src=\"http://auvietuc.edu.vn/storage/app/images/new12_3.png\" />\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"color:black\">Tổng kết chương tr&igrave;nh BE A STAR</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"color:black\">Giải nhất thuộc về em: Brian Movers23</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"color:black\">Đồng Giải nh&igrave; thuộc về em: Justin Starters25 v&agrave; Annie Flyers9&amp;10</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:11.0pt\"><span style=\"color:black\">Đồng Giải ba thuộc về em: Anna Starters22 &ndash; Cherry Movers16 </span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\">&nbsp;</p>\r\n<img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/new12_4.png\" /></div>\r\n', 'tin-tuc/new12_1.png', 1, 8, '2020-03-02 03:01:50', '2020-03-02 03:01:50'),
(61, 'TỔNG KẾT SUMMER CAMP 29.7.2018', 'tong-ket-summer-camp-29-7-2018', 'Gần 400 học viên nhóm tuổi 4-12 tham gia ngày 28&29/7 cùng thầy cô EVAS.', '<div class=\"noidung\">\r\n<p>TỔNG KẾT SUMMER CAMP 29.7.2018<br />\r\nGần 400 học vi&ecirc;n nh&oacute;m tuổi 4-12 tham gia ng&agrave;y 28&amp;29/7 c&ugrave;ng thầy c&ocirc; EVAS.&nbsp;C&aacute;c em chắc chắn sẽ c&oacute; kỷ niệm rất đẹp v&agrave; trải nghiệm ấn tượng kh&ocirc;ng qu&ecirc;n trong m&ugrave;a h&egrave; năm nay c&ugrave;ng thầy c&ocirc; EVAS.&nbsp;Niềm vui nối tiếp niềm vui, cả ng&agrave;y bận rộn c&aacute;c hoạt động gi&aacute;o dục giải tr&iacute;.&nbsp;Mỗi hoạt động rất th&uacute; vị mang đến sự h&agrave;o hứng cho từng bạn nhỏ:<br />\r\nC&ugrave;ng s&aacute;ng tạo &yacute; tưởng trang tr&iacute; trại của m&igrave;nh!<br />\r\nTh&aacute;ch thức v&agrave; kh&aacute;m ph&aacute; những bước nhảy đầu ti&ecirc;n!<br />\r\nKh&aacute;m ph&aacute; những h&igrave;nh ảnh được l&agrave;m từ bong b&oacute;ng!<br />\r\nC&ugrave;ng nhảy v&agrave; h&aacute;t ch&uacute; bee dễ thương chưa k&igrave;a!<br />\r\nC&ugrave;ng t&igrave;m trả lời cho 30 c&acirc;u hỏi th&aacute;ch thức của c&ocirc; April!<br />\r\nChơi vận động c&ugrave;ng thầy c&ocirc; EVAS th&ocirc;i n&agrave;o!<br />\r\n==============================</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/DSC00619.jpg\" style=\"height:375px; width:500px\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/DSC00640.jpg\" style=\"height:375px; width:500px\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/DSC00656.jpg\" style=\"height:375px; width:500px\" /></p>\r\n</div>\r\n', 'tin-tuc/DSC00656.jpg', 1, 8, '2020-03-02 03:09:26', '2020-03-02 03:09:26'),
(62, 'LONGXUYEN TOUR 2018 Nhóm Flyers-KET!!!', 'longxuyen-tour-2018-nhom-flyers-ket!!!', 'Chuyến tham quan Long Xuyên cùng cô April đã đem lại cho các bạn teen có một ngày hè đáng nhớ phải không nào???', '<div class=\"noidung\">\r\n<p>LONGXUYEN TOUR 2018 Nh&oacute;m Flyers-KET!!!<br />\r\nChuyến tham quan Long Xuy&ecirc;n c&ugrave;ng c&ocirc; April đ&atilde; đem lại cho c&aacute;c bạn teen c&oacute; một ng&agrave;y h&egrave; đ&aacute;ng nhớ phải kh&ocirc;ng n&agrave;o??? Nhớ c&acirc;u n&oacute;i &quot; C&ocirc; ơi, biết đi như vầy con kh&ocirc;ng đi đ&acirc;u c&ocirc;. Phải thuyết tr&igrave;nh, phi&ecirc;n dịch, v&agrave; tập l&agrave;m hướng dẫn vi&ecirc;n du lịch nữa.&quot; N&oacute;i vậy đ&oacute;, nhưng cuối ng&agrave;y, vẫn nụ cười rạng rỡ đ&oacute; th&ocirc;i.&nbsp;<br />\r\nC&aacute;c em sau khi được c&ocirc; April th&ocirc;ng tin về c&aacute; sấu th&igrave; tự tin đứng trước nh&oacute;m bạn hướng dẫn về tour Trại c&aacute; sấu;<br />\r\nC&aacute;c gi&uacute;p c&ocirc; thuyết tr&igrave;nh&nbsp;vi&ecirc;n lịch sử về B&aacute;c T&ocirc;n phi&ecirc;n dịch cho c&ocirc; April hiểu phần n&agrave;o về lịch sử Việt Nam;<br />\r\nC&aacute;c em đ&atilde; thuyết tr&igrave;nh văn h&oacute;a &Oacute;c Eo, th&agrave;nh tựu, chiến tranh v&agrave; phong tục sống An Giang tại Viện Bảo T&agrave;ng;<br />\r\nQuan trọng nhất l&agrave;: chỉ một ng&agrave;y th&ocirc;i, nhưng c&aacute;c em học được rất nhiều phải kh&ocirc;ng n&egrave;, kh&ocirc;ng chỉ l&agrave; kỹ năng tiếng Anh, m&agrave; kiến thức lịch sử, th&agrave;nh tựu của qu&ecirc; hương v&agrave; một ng&agrave;y trải nghiệm c&ugrave;ng c&aacute;c bạn!!!<br />\r\n&nbsp;C&ugrave;ng ngắm nh&igrave;n những gi&acirc;y ph&uacute;t học hỏi thật bổ &iacute;ch của em nh&eacute;!</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/DSC01108.jpg\" style=\"height:375px; width:500px\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/DSC00903.jpg\" style=\"height:375px; width:500px\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/DSC00847.jpg\" style=\"height:375px; width:500px\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n</div>\r\n', 'tin-tuc/DSC01108.jpg', 1, 8, '2020-03-02 03:16:35', '2020-03-02 03:16:35'),
(63, 'QUÀ TRI ÂN CHO HỌC VIÊN CÓ KẾT QUẢ TỐT', 'qua-tri-an-cho-hoc-vien-co-ket-qua-tot', 'Với kết quả vượt trội của các em học viên trong năm qua khi tham gia các kỳ thi lấy chứng chỉ Quốc tế...', '<div class=\"noidung\">\r\n<p><img alt=\"\" src=\"/upload/images/tin-tuc/Zalo_ScreenShot_4_12_2018_1544807.png\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Với kết quả vượt trội của c&aacute;c em học vi&ecirc;n trong năm qua khi tham gia c&aacute;c kỳ thi lấy chứng chỉ Quốc tế Cambridge, IELTS, TOEIC,... v&agrave; để đ&aacute;p lại niềm tin m&agrave; Qu&yacute; phụ huynh, học vi&ecirc;n đang theo học tại Trung T&acirc;m Ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c tr&ecirc;n 3 kh&oacute;a li&ecirc;n tục. Hằng năm trung t&acirc;m sẽ c&oacute; phần qu&agrave; đến với từng học vi&ecirc;n nhằm thay cho lời cảm ơn s&acirc;u sắc, ch&acirc;n th&agrave;nh nhất!</p>\r\n\r\n<p>EVAS lu&ocirc;n tạo điều kiện tốt nhất để c&aacute;c bạn c&oacute; thể bay xa</p>\r\n\r\n<p>Để biết th&ecirc;m c&aacute;c chương tr&igrave;nh đ&agrave;o tạo của Trung t&acirc;m li&ecirc;n hệ:<br />\r\nTrụ Sở ch&iacute;nh: Số 815 - 816 H&agrave; Ho&agrave;ng Hổ, TP Long Xuy&ecirc;n, An Giang<br />\r\n- Điện thoại: 02963.941.780 - 941.790<br />\r\nCơ sở 2: 79 Trần Hưng Đạo, P Long Xuy&ecirc;n, An Giang<br />\r\n- Điện thoại: 02963.666.888 - 666.989</p>\r\n</div>\r\n', 'tin-tuc/Zalo_ScreenShot_4_12_2018_1544807.png', 1, 7, '2020-03-02 03:20:19', '2020-03-02 03:20:19'),
(64, 'THÔNG BÁO NGHỈ TẾT DƯƠNG LỊCH 2019', 'thong-bao-nghi-tet-duong-lich-2019', 'Trung tâm Ngoại ngữ Quốc tế Âu Việt Úc trân trọng thông báo: Toàn thể Giáo viên, Nhân viên và Học viên...', '<div class=\"noidung\">\r\n<p><em>Trung t&acirc;m Ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c tr&acirc;n trọng th&ocirc;ng b&aacute;o:</em></p>\r\n\r\n<p><em>To&agrave;n thể Gi&aacute;o vi&ecirc;n, Nh&acirc;n vi&ecirc;n v&agrave; Học vi&ecirc;n đang c&ocirc;ng t&aacute;c v&agrave; học tập tại Trung t&acirc;m&nbsp;sẽ nghỉ lễ Tết Dương Lịch 01 ng&agrave;y: v&agrave;o&nbsp;ng&agrave;y&nbsp;<strong>Thứ Ba : 01/01/2019.</strong></em></p>\r\n\r\n<p><em>Bắt đầu từ ng&agrave;y&nbsp;<strong>Thứ Tư 02/01/2019,</strong>&nbsp;Trung t&acirc;m trở lại hoạt động b&igrave;nh thường.</em></p>\r\n\r\n<p><em>Trong thời gian nghỉ lễ, qu&yacute; kh&aacute;ch h&agrave;ng v&agrave; học vi&ecirc;n c&oacute; bất kỳ y&ecirc;u cầu hoặc cần hỗ trợ, vui l&ograve;ng truy cập website:&nbsp;<a href=\"http://www.gln.edu.vn/\" target=\"_blank\">www.</a><a href=\"http://auvietuc.edu.vn/\" target=\"_blank\">auvietuc.edu.vn</a>&nbsp;hoặc Facebook Trung T&acirc;m Ngoại Ngữ Quốc Tế &Acirc;u Việt &Uacute;c v&agrave; để lại lời nhắn, ch&uacute;ng t&ocirc;i sẽ li&ecirc;n lạc lại với bạn.&nbsp;</em></p>\r\n\r\n<p><em>Xin ch&acirc;n th&agrave;nh cảm ơn v&agrave; ch&uacute;c qu&yacute; kh&aacute;ch h&agrave;ng cũng như c&aacute;c bạn học vi&ecirc;n một kỳ nghỉ ấm &aacute;p v&agrave; năm mới hạnh ph&uacute;c!</em></p>\r\n<img alt=\"\" src=\"/upload/images/tin-tuc/5JX2P7_Zalo_ScreenShot_4_12_2018_1558373.png\" /></div>\r\n', 'tin-tuc/5JX2P7_Zalo_ScreenShot_4_12_2018_1558373.png', 1, 7, '2020-03-02 03:27:38', '2020-03-02 03:27:38'),
(65, 'Lễ Phát Bằng Cambridge 2019', 'le-phat-bang-cambridge-2019', 'Hàng năm chúng tôi liên kết với Hội đồng khảo thí Cambridge thuộc Đại Học Cambridge,...', '<div class=\"noidung\">\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"font-family:helvetica\"><span style=\"color:#1c1e21\">H&agrave;ng năm ch&uacute;ng t&ocirc;i li&ecirc;n kết với Hội đồng khảo th&iacute; Cambridge thuộc Đại Học Cambridge, tổ chức thi chứng chỉ cho học vi&ecirc;n theo học tại Trung t&acirc;m ngoại ngữ quốc tế &Acirc;u Việt &Uacute;c. Ch&uacute;ng t&ocirc;i cũng tự tin tự h&agrave;o rằng, đ&acirc;y l&agrave; nh&acirc;n tố g&oacute;p phần th&agrave;nh c&ocirc;ng trong c&ocirc;ng t&aacute;c đ&agrave;o tạo tiếng Anh, khẳng định gi&aacute; trị của Trung t&acirc;m ngoại ngữ quốc tế &Acirc;u Việt &Uacute;c. H&agrave;ng năm ch&uacute;ng t&ocirc;i tổ chức thi cho khoảng ba trăm học vi&ecirc;n, đ&aacute;nh gi&aacute; ch&iacute;nh x&aacute;c khả năng sử dụng ng&ocirc;n ngữ quốc tế của c&aacute;c em, v&agrave; khẳng định chất lượng đ&agrave;o tạo qua tấm bằng quốc tế c&aacute;c em nhận với kết quả gần như tuyệt đối. </span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"font-family:helvetica\"><span style=\"color:#1c1e21\">Chứng chỉ Cambridge Starters - Movers - Flyers - KET (A2) - PET (B1). Kh&ocirc;ng chỉ l&agrave; một tấm bảng c&oacute; gi&aacute; trị quốc tế do Hội đồng khảo th&iacute; quốc tế thuộc Đại học Cambridge Vương Quốc Anh cấp. Gi&uacute;p cho phụ huynh thấy r&otilde; sự tiến bộ của con/em m&igrave;nh so với mặt bằng chung của thế giới.</span></span></span></span><span style=\"font-size:10.5pt\"><span style=\"font-family:helvetica\"><span style=\"color:#1c1e21\"> <span style=\"background-color:white\">Tăng sự&nbsp;hứng th&uacute; trong việc học tiếng Anh cho c&aacute;c em. Từ đ&oacute; Trung t&acirc;m sẽ định hướng ch&iacute;nh x&aacute;c trong việc đ&agrave;o tạo v&agrave; r&egrave;n luyện tiếng Anh,trang bị h&agrave;nh trang cho c&aacute;c em trong tương lai khi thi đầu v&agrave;o v&agrave;o c&aacute;c trường chuy&ecirc;n v&agrave; t&igrave;m kiếm học bổng cho c&aacute;c kh&oacute;a học ngắn/d&agrave;i hạn ở nước ngo&agrave;i.</span></span></span></span></p>\r\n<img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/Picture4.png\" />\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"font-family:helvetica\"><span style=\"color:#1c1e21\">Buổi lễ ph&aacute;t bằng Cambridge năm 2018. Gần ba trăm th&iacute; sinh, trong đ&oacute; đạt kết quả tuyệt đối cho phần thi N&oacute;i. </span></span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/Picture5.png\" /><span style=\"font-size:11pt\"> </span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"font-family:helvetica\"><span style=\"color:#1c1e21\">Buổi lễ ph&aacute;t bằng Cambridge năm 2017. </span></span></span></span></span></span></p>\r\n\r\n<p><img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/Picture6.png\" style=\"height:532px; width:800px\" /><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"font-family:helvetica\"><span style=\"color:#1c1e21\">Buổi lễ ph&aacute;t bằng Cambridge năm 2016. </span></span></span></span></span></span></p>\r\n</div>\r\n', 'tin-tuc/Picture4.png', 1, 6, '2020-03-02 03:43:38', '2020-03-02 03:43:38'),
(66, 'Open Day 5/9/2019', 'open-day-592019', 'Ngày 5/9/2019 Trung tâm Ngoại ngữ Quốc tế Âu Việt Úc tổ chức ngay tại Trung tâm ngày Open Day...', '<div class=\"noidung\">\r\n<p><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"font-family:helvetica\"><span style=\"color:#1d2129\">Ng&agrave;y 5/9/2019 Trung t&acirc;m Ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c tổ chức ngay tại Trung t&acirc;m ng&agrave;y Open Day để tạo điều kiện cho c&aacute;c em học vi&ecirc;n v&agrave; phụ huynh đăng k&yacute; dễ d&agrave;ng cho c&aacute;c kh&oacute;a học, Khởi động m&ugrave;a tựu trường với lớp học tiếng Anh hiệu quả tại Trung t&acirc;m, c&ugrave;ng thầy c&ocirc; Gi&aacute;o vi&ecirc;n nước ngo&agrave;i th&acirc;n thiện, tận t&igrave;nh, động vi&ecirc;n c&aacute;c bạn giao tiếp, c&ugrave;ng thầy c&ocirc; Việt Nam lu&ocirc;n theo s&aacute;t phụ đạo khi c&aacute;c bạn hỗ trợ.</span></span></span></span><br />\r\n<span style=\"font-size:10.5pt\"><span style=\"font-family:helvetica\"><span style=\"color:#1d2129\"><span style=\"background-color:white\">- Kiểm tra khả năng tiếng Anh với gi&aacute;o vi&ecirc;n nước ngo&agrave;i;</span><br />\r\n<span style=\"background-color:white\">- Tư vấn cải thiện từng kỹ năng tiếng Anh từ gi&aacute;o vi&ecirc;n nước ngo&agrave;i;</span><br />\r\n<span style=\"background-color:white\">- Tham quan lớp học v&agrave; tham khảo gi&aacute;o tr&igrave;nh;</span><br />\r\n<span style=\"background-color:white\">- Kh&aacute;m ph&aacute; c&aacute;c chương tr&igrave;nh ưu đ&atilde;i v&agrave; học ph&iacute; hỗ trợ hấp dẫn cho kh&oacute;a khai giảng th&aacute;ng 9;</span><br />\r\n<span style=\"background-color:white\">- Nhận ngay qu&agrave; tặng từ EVAS;</span><br />\r\n<span style=\"background-color:white\">- C&aacute;c em học vi&ecirc;n trao đổi tiếng Anh v&agrave; thưởng thức snacks v&agrave; drinks c&ugrave;ng thầy Junaid.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n<img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/Picture7.png\" />\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:10.5pt\"><span style=\"font-family:helvetica\">C&aacute;c em học vi&ecirc;n Trung t&acirc;m thảo luận cũng <span style=\"background-color:white\"><span style=\"color:#1d2129\">thầy </span></span>Junaid</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n<img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/Picture8.png\" /> <img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/Picture9.png\" /></div>\r\n', 'tin-tuc/Picture7.png', 1, 6, '2020-03-02 03:47:14', '2020-03-02 03:47:14'),
(67, 'Hội Thảo Quốc Tế Thường Niên 2019', 'hoi-thao-quoc-te-thuong-nien-2019', 'Chúng tôi rất tự hào đã ba năm liên tục chúng tôi thực hiện thành công, hợp tác với các Nhà xuất bản...', '<div class=\"noidung\">\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"font-family:helvetica\"><span style=\"color:#1c1e21\">Ch&uacute;ng t&ocirc;i rất tự h&agrave;o đ&atilde; ba năm li&ecirc;n tục ch&uacute;ng t&ocirc;i thực hiện th&agrave;nh c&ocirc;ng, hợp t&aacute;c với c&aacute;c Nh&agrave; xuất bản h&agrave;ng đầu thế giới về đ&agrave;o tạo kiến thức đổi mới cho nh&acirc;n vi&ecirc;n v&agrave; gi&aacute;o vi&ecirc;n Trung t&acirc;m &Acirc;u Việt &Uacute;c. Đ&acirc;y cũng ch&iacute;nh l&agrave; nh&acirc;n tố quan trọng gi&uacute;p Trung t&acirc;m &Acirc;u Việt &Uacute;c khẳng định chất lượng đ&agrave;o tạo trong những năm qua. Những chuỗi sự kiện ch&uacute;ng t&ocirc;i li&ecirc;n kết với Cambridge, Oxford, Macmillan.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"font-family:helvetica\"><span style=\"color:#1c1e21\">Năm 2017, ch&uacute;ng t&ocirc;i tổ chức th&agrave;nh c&ocirc;ng tại kh&aacute;ch sạn H&ograve;a B&igrave;nh buổi workshop &quot;Macmillan Training Day&quot; do Nh&agrave; xuất bản Macmillan đến từ Anh quốc. Hướng dẫn gi&aacute;o vi&ecirc;n bộ m&ocirc;n Tiếng anh sử dụng gi&aacute;o tr&igrave;nh v&agrave; c&aacute;c phần mềm hỗ trợ giảng dạy bằng c&ocirc;ng nghệ hiện đại. C&aacute;c th&agrave;nh phần tham dự:</span></span></span></span><br />\r\n<span style=\"font-size:10.5pt\"><span style=\"font-family:helvetica\"><span style=\"color:#1c1e21\"><span style=\"background-color:white\">Đại diện Nh&agrave; xuất bản Macmillan</span><br />\r\n<span style=\"background-color:white\">Chuy&ecirc;n gia:</span><br />\r\n<span style=\"background-color:white\">- &Ocirc;ng David Kaye<br />\r\n- B&agrave; Nguyen Bich Phuong<br />\r\nĐại diện Trung t&acirc;m<br />\r\n- Gi&aacute;m đốc Trung t&acirc;m &ndash; B&agrave; La Ngoc Huong<br />\r\n- Giảng vi&ecirc;n Trung t&acirc;m &ndash; &Ocirc;ng Grant Robert Hunter</span></span></span></span></span></span></p>\r\n<img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/Picture1.png\" />\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"font-family:helvetica\"><span style=\"color:#1c1e21\">&Ocirc;ng David Kaye đang thực hiện buổi training</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"font-family:helvetica\"><span style=\"color:#1c1e21\">Năm 2018, ch&uacute;ng t&ocirc;i tổ chức th&agrave;nh c&ocirc;ng buổi hội thảo &quot;Oxford Training Day&quot; do Nh&agrave; xuất bản từ Đại học Oxford đến từ Anh. D&agrave;o tạo gi&aacute;o vi&ecirc;n Trung t&acirc;m &Acirc;u Việt &Uacute;c đa dạng h&oacute;a phương ph&aacute;p giảng dạy, t&iacute;ch hợp kỹ thuật c&ocirc;ng nghệ v&agrave;o lớp học v&agrave; mang đến b&agrave;i dạy hiệu quả cho học sinh Trung t&acirc;m, gi&uacute;p thầy c&ocirc; n&acirc;ng cao kỹ năng giảng dạy cam kết chất lương cho học sinh th&acirc;n y&ecirc;u ch&uacute;ng t&ocirc;i.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"font-family:helvetica\"><span style=\"color:#1c1e21\">C&aacute;c th&agrave;nh phần tham dự:</span></span></span></span><br />\r\n<span style=\"font-size:10.5pt\"><span style=\"font-family:helvetica\"><span style=\"color:#1c1e21\"><span style=\"background-color:white\">Đại diện Nh&agrave; xuất bản Oxford</span><br />\r\n<span style=\"background-color:white\">Chuy&ecirc;n gia:</span><br />\r\n<span style=\"background-color:white\">- &Ocirc;ng. Joon Lee<br />\r\nĐại diện Trung t&acirc;m<br />\r\n- Gi&aacute;m đốc Trung t&acirc;m &ndash; B&agrave; La Ngoc Huong<br />\r\n- Giảng vi&ecirc;n Trung t&acirc;m &ndash; </span></span></span></span><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"font-family:helvetica\"><span style=\"color:red\">&Ocirc;ng Neil </span></span></span></span></p>\r\n<img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/Picture2.png\" /><span style=\"font-size:11pt\"><span style=\"font-family:calibri,sans-serif\"><span style=\"font-size:10.5pt\"><span style=\"background-color:white\"><span style=\"font-family:helvetica\"><span style=\"color:#1c1e21\">Năm 2019, ch&uacute;ng t&ocirc;i tiếp tục li&ecirc;n tổ chức th&agrave;nh c&ocirc;ng buổi hội thảo &quot; Macmillan ELT Upgrade Day Advancing Learning in 2019&quot; do Nh&agrave; xuất bản Macmillan đến từ Anh quốc. Thầy c&ocirc; tham dự ng&agrave;y đ&agrave;o tạo gi&aacute;o dục Macmillan. Ngo&agrave;i việc t&iacute;ch lũy c&aacute;c phương ph&aacute;p giảng dạy, gi&aacute;o vi&ecirc;n ch&uacute;ng t&ocirc;i đ&atilde; được đ&agrave;o tạo c&aacute;ch tạo động lực cho học vi&ecirc;n v&agrave; gi&uacute;p học vi&ecirc;n duy tr&igrave; động lực trong c&aacute;c kh&oacute;a học. Với c&aacute;c chiến lược kh&aacute;c nhau được chia sẻ từ buổi đ&agrave;o tạo, những th&aacute;ch thức m&agrave; ch&uacute;ng t&ocirc;i thể gặp phải để th&uacute;c đẩy người học sẽ bị loại bỏ v&agrave; chắc chắn sẵn s&agrave;ng mang đến những b&agrave;i học sinh động để thu h&uacute;t học vi&ecirc;n v&agrave; sự n&acirc;ng cao tr&igrave;nh độ tiếng Anh cho học vi&ecirc;n. Đ&agrave;o tạo bằng t&acirc;m, ch&uacute;ng t&ocirc;i cố gắng mang lại kết quả học tập tốt cho người học để xứng đ&aacute;ng với những g&igrave; phụ huynh v&agrave; học vi&ecirc;n tin tưởng v&agrave;o Trung t&acirc;m.</span></span></span></span></span></span> <img alt=\"\" src=\"http://auvietuc.edu.vn/upload/images/tin-tuc/Picture3.png\" /></div>\r\n', 'tin-tuc/Picture2.png', 1, 7, '2020-03-02 03:52:50', '2020-03-02 03:52:50'),
(68, 'KHÓA HỌC ONLINE - GIẢI PHÁP HỌC HIỆU QUẢ TẠI NHÀ', 'khoa-hoc-online-giai-phap-hoc-hieu-qua-tai-nha', 'TRÁNH XA CORONA - MANG KIẾN THỨC ĐẾN TẠI NHÀ!', '<p><em><strong>Sẵn S&agrave;ng N&oacute;i Tiếng Anh H&agrave;ng Ng&agrave;y C&ugrave;ng Bạn B&egrave; v&agrave; Thầy c&ocirc; EVAS Chưa N&agrave;o?</strong></em></p>\r\n\r\n<p style=\"text-align:justify\">Với diễn biến của t&igrave;nh h&igrave;nh dịch bệnh hiện nay, c&aacute;c qu&yacute; phụ huynh đặc biệt quan t&acirc;m v&agrave; đặt sức khoẻ của c&aacute;c b&eacute; l&ecirc;n h&agrave;ng đầu. Ch&iacute;nh v&igrave; thế n&ecirc;n Bộ Gi&aacute;o Dục đ&atilde; hạn chế việc tiếp x&uacute;c nơi đ&ocirc;ng người bằng c&aacute;ch cho c&aacute;c b&eacute; tạm ngưng học tập ở trường v&agrave; c&aacute;c cơ sở dạy học. Điều đ&oacute; khiến c&aacute;c thầy, c&aacute;c c&ocirc; ở Trung T&acirc;m Ngoại Ngữ Quốc Tế &Acirc;u Việt &Uacute;c đứng ngồi kh&ocirc;ng y&ecirc;n, phải nh&igrave;n học vi&ecirc;n của m&igrave;nh nghỉ học từng ng&agrave;y v&agrave; phai mờ dần những kiến thức Anh ngữ đ&atilde; học.</p>\r\n\r\n<p style=\"text-align:justify\">Thấu hiểu được nỗi lo v&agrave; mong muốn của c&aacute;c bậc Phụ Huynh về việc giữ an to&agrave;n sức khỏe cho c&aacute;c b&eacute; v&agrave; việc học tập gi&aacute;n đoạn của c&aacute;c bạn trong thời điểm phải nghĩ d&agrave;i hạn chống dịch hiện tại...</p>\r\n\r\n<p style=\"text-align:justify\">V&agrave; cả những mong muốn trong đội ngũ gi&aacute;o vi&ecirc;n tại Trung T&acirc;m về việc tiếp thu kiến thức đạt hiệu quả cao nhất, giảm đi sự bỡ ngỡ cho kỳ nghỉ tr&aacute;nh dịch qu&aacute; d&agrave;i của c&aacute;c em sau khi m&ugrave;a dịch qua đi, c&aacute;c thầy c&ocirc; tại EVAS hiện nay đ&atilde; c&oacute; thể dạy học cho c&aacute;c b&eacute; với lớp học online.</p>\r\n\r\n<p style=\"text-align:justify\">Ở lớp học n&agrave;y, c&aacute;c b&eacute; sẽ được tương t&aacute;c v&agrave; &ocirc;n tập lại những kiến thức tiếng Anh ngay tại nh&agrave; tr&ecirc;n chiếc m&aacute;y t&iacute;nh của m&igrave;nh m&agrave; kh&ocirc;ng cần đến lớp. So với việc tự &ocirc;n tập tại nh&agrave;, c&aacute;c b&eacute; tham gia kh&oacute;a học online sẽ vừa được &ocirc;n lại những kiến thức nền tảng song song với việc tập trung ph&aacute;t triển th&ecirc;m c&aacute;c kỹ năng NGHE - N&Oacute;I- NGỮ &Acirc;M, v&agrave;&nbsp;<em>v&igrave;</em>&nbsp;<em>Anh Văn rất cần sự giao tiếp</em>, ch&iacute;nh v&igrave; thế m&agrave; những kh&oacute;a học online tại EVAS từ thời lượng học k&eacute;o d&agrave;i li&ecirc;n tục trong tuần lẫn những kiến thức, c&aacute;c tr&ograve; chơi lồng gh&eacute;p trong b&agrave;i giảng - sẽ gi&uacute;p c&aacute;c em tăng khả năng tương t&aacute;c tốt hơn với gi&aacute;o vi&ecirc;n v&agrave; c&aacute;c bạn học, gi&uacute;p c&aacute;c em hứng th&uacute; hơn v&agrave; nhớ kiến thức MỖI NG&Agrave;Y.</p>\r\n\r\n<p style=\"text-align:justify\">Chương tr&igrave;nh c&oacute; lớp học của Gi&aacute;o vi&ecirc;n Việt Nam v&agrave; cả Gi&aacute;o vi&ecirc;n Nước Ngo&agrave;i, song song với những kh&oacute;a học online đang được giảng dạy (Cambridge: Starters, Movers, Flyers), chương tr&igrave;nh online tại EVAS được thiết kế mở rộng th&ecirc;m d&agrave;nh ri&ecirc;ng c&aacute;c c&aacute;c cấp độ A2 (KET), B1 (PET) v&agrave; c&aacute;c lớp Anh Văn Giao tiếp.</p>\r\n\r\n<p style=\"text-align:justify\"><em><strong>Với chương tr&igrave;nh được thiết kế ri&ecirc;ng biệt</strong></em>:</p>\r\n\r\n<p style=\"text-align:justify\"><strong><em>+ Thời lượng học li&ecirc;n tục trong tuần.</em></strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong><em>+ Tập trung v&agrave;o kỹ năng NGHE - N&Oacute;I - PH&Aacute;T &Acirc;M.</em></strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong><em>+ Sỉ số &iacute;t chỉ 6 học vi&ecirc;n/ 1 lớp gi&uacute;p tăng hiệu quả học tập.</em></strong></p>\r\n\r\n<p style=\"text-align:justify\">Qu&yacute; Phụ huynh, c&aacute;c bạn c&oacute; thể đến bất kỳ cơ sở &Acirc;u Việt &Uacute;c n&agrave;o để được c&aacute;c Thầy C&ocirc; tự vấn cụ thể về kh&oacute;a học cũng như c&aacute;c thức đăng k&yacute; tham gia kh&oacute;a học v&agrave; thời gian bắt đầu học cụ thể. V&agrave; khi đến Trung T&acirc;m, phụ huynh, c&aacute;c bạn c&oacute; thể mang theo m&aacute;y t&iacute;nh c&aacute; nh&acirc;n của m&igrave;nh để được c&agrave;i đặt chương tr&igrave;nh học v&agrave; được hướng dẫn cụ thể hơn về c&aacute;ch sử dụng nh&eacute;!</p>\r\n\r\n<p style=\"text-align:justify\">H&atilde;y c&ugrave;ng EVAS sẵn s&agrave;ng n&oacute;i tiếng Anh mỗi ng&agrave;y trong m&ugrave;a dịch n&agrave;o!!!</p>\r\n\r\n<p><strong><em>ANH NGỮ &Acirc;U VIỆT &Uacute;C</em></strong></p>\r\n', 'online%2023.png', 1, 3, '2020-03-11 09:55:34', '2020-03-11 09:55:34'),
(70, 'THAY LỜI CẢM ƠN!', 'thay-loi-cam-on!', 'Bằng tất cả tấm lòng, xin chân thành cảm ơn Các Quý Phụ Huynh đã đặt niềm tin vào EVAS!', '<p style=\"text-align: justify;\">EVAS ch&acirc;n th&agrave;nh gửi lời cảm ơn đến rất nhiều Phụ Huynh đ&atilde; d&agrave;nh lời khen cho kh&oacute;a học online hiện tại. V&agrave; thật sự điều EVAS l&agrave;m kh&ocirc;ng phải để nhận những lời c&oacute; c&aacute;nh m&agrave; ở vị tr&iacute; l&agrave; một gi&aacute;o vi&ecirc;n đứng lớp, ch&uacute;ng t&ocirc;i mong muốn nhiều hơn ở việc tiến bộ trong qu&aacute; tr&igrave;nh học tập của c&aacute;c em...</p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/Malie.png\" style=\"height:233px; width:380px\" /></p>\r\n\r\n<p><em>(H&igrave;nh ảnh buổi học do Phụ Huynh gửi cho Trung t&acirc;m)</em></p>\r\n\r\n<p>Hơn ai hết, Qu&yacute; phụ huynh v&agrave; c&aacute;c thầy c&ocirc; lu&ocirc;n hiểu được việc học tại trường v&agrave; tại lớp sẽ l&agrave; phương ph&aacute;p học hiệu quả nhất so với tự học hay l&agrave;m b&agrave;i tập tại nh&agrave;.</p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/MINT.png\" style=\"height:233px; width:380px\" /></p>\r\n\r\n<p style=\"text-align: justify;\">V&agrave; ngo&agrave;i những t&agrave;i liệu, b&agrave;i tập, đường link, chương tr&igrave;nh học từ c&aacute;c nguồn, th&igrave; điều nhiều nhất m&agrave; EVAS chỉ c&oacute; thể l&agrave;m để gi&uacute;p c&aacute;c bạn học vi&ecirc;n nhớ b&agrave;i v&agrave; kh&ocirc;ng bị gi&aacute;n đoạn qu&aacute; tr&igrave;nh học Anh văn trong thời điểm m&agrave; việc y&ecirc;u cầu an to&agrave;n sức khỏe phải được ưu ti&ecirc;n h&agrave;ng đầu của đỉnh điểm m&ugrave;a dịch Covid-19, ch&iacute;nh l&agrave; những buổi học online, bởi ch&uacute;ng t&ocirc;i hiểu anh văn kh&ocirc;ng chỉ l&agrave; đọc viết, m&agrave; c&ograve;n l&agrave; những kỹ năng nghe n&oacute;i, tương t&aacute;c giao tiếp. V&agrave; c&aacute;c buổi học của kh&oacute;a online k&eacute;o d&agrave;i cả 5 buổi li&ecirc;n tiếp trong tuần l&agrave; để c&aacute;c em mỗi ng&agrave;y, mỗi ng&agrave;y được trau dồi c&aacute;c kỹ năng cần thiết n&agrave;y.</p>\r\n\r\n<p style=\"text-align: justify;\"><img alt=\"\" src=\"/upload/images/Tina.png\" style=\"height:233px; width:380px\" /></p>\r\n\r\n<p>Bằng tất cả tấm l&ograve;ng, xin ch&acirc;n th&agrave;nh cảm ơn C&aacute;c Qu&yacute; Phụ Huynh đ&atilde; đặt niềm tin v&agrave;o EVAS!</p>\r\n\r\n<p><strong><em>ANH NGỮ &Acirc;U VIỆT &Uacute;C</em></strong></p>\r\n', 'thank%20you%20note.png', 1, 3, '2020-03-17 04:51:47', '2020-03-17 04:51:47'),
(71, 'HÀNH TRÌNH LẤY CHỨNG CHỈ A2-B1-B2 CHỈ TRONG 01 THÁNG', 'hanh-trinh-lay-chung-chi-a2-b1-b2-chi-trong-01-thang', 'Bạn có tin rằng kết thúc kỳ nghỉ này bạn sẽ lấy chứng chỉ A2-B1-B2 không?', '<p>Thầy c&ocirc; EVAS tự tin trả lời bạn ho&agrave;n to&agrave;n thi th&agrave;nh c&ocirc;ng sau một th&aacute;ng &ocirc;n luyện Online cấp tốc. V&igrave;:</p>\r\n\r\n<p>&nbsp; &nbsp;<strong><em> -&nbsp;Sỉ số lớp &iacute;t - đảm bảo chất lượng tối đa.</em></strong></p>\r\n\r\n<p><strong><em>&nbsp; &nbsp; -&nbsp;Kiến thức được tổng hợp từ căn bản đến nắm vững đề thi.</em></strong></p>\r\n\r\n<p><strong><em>&nbsp; &nbsp; - Hỗ trợ phần mền thực h&agrave;nh đề thi online trị gi&aacute; 499,000vnd.</em></strong></p>\r\n\r\n<p><strong><em>&nbsp; &nbsp; - Thực h&agrave;nh đề thi n&oacute;i trực tiếp c&ugrave;ng thầy c&ocirc; EVAS.</em></strong></p>\r\n\r\n<p><strong><em>&nbsp; &nbsp; - Tự tin viết b&agrave;i chỉ sau 2 buổi học.</em></strong></p>\r\n\r\n<p><strong><em>&nbsp; &nbsp; - Học ph&iacute; được hỗ trợ l&ecirc;n đến 50% (&aacute;p dụng ghi danh trước 31/3).</em></strong></p>\r\n\r\n<p><strong><em>&nbsp; &nbsp; - Cam kết 100% tham gia đầy đủ buổi học, thực h&agrave;nh v&agrave; l&agrave;m b&agrave;i đ&uacute;ng theo hướng dẫn của thầy c&ocirc;.</em></strong></p>\r\n\r\n<p><strong><em><span style=\"font-size:12px\"><a href=\"/upload/images/Vstep.png\"><img alt=\"\" src=\"/upload/images/Vstep.png\" style=\"height:251px; width:380px\" /></a></span></em></strong></p>\r\n\r\n<p>Đ&acirc;y l&agrave; <strong><em>kh&oacute;a học online</em></strong>&nbsp;hỗ trợ hiệu quả d&agrave;nh cho thầy c&ocirc; v&agrave; c&aacute;c bạn sinh vi&ecirc;n tự tin học luyện chứng chỉ A2 - B1 - B2 tại nh&agrave; m&igrave;nh.</p>\r\n\r\n<p><strong><em>ANH NGỮ &Acirc;U VIỆT &Uacute;C</em></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Vstep.png', 1, 3, '2020-03-23 10:33:07', '2020-03-23 10:33:07'),
(74, 'CÂN ĐONG ƯU VÀ NHƯỢC ĐIỂM CỦA KHÓA HỌC ONLINE', 'can-dong-uu-va-nhuoc-diem-cua-khoa-hoc-online', 'Nếu đặt khóa học truyền thống và khóa học online lên cùng một cán cân, thì phần lợi ích sẽ luôn thiên về lớp học truyền thống, tuy nhiên nếu đặt vào tình hình dịch Covid-19 hiện tại, thì đây lại là giải pháp tiết kiệm và tối ưu nhất cho việc học lẫn thời gian và tiền bạc...', '<p><strong><em>Nếu so s&aacute;nh với phương ph&aacute;p học truyền thống, điều đầu ti&ecirc;n khi n&oacute;i đến kh&oacute;a học online l&agrave; việc kh&oacute; đ&aacute;nh gi&aacute; khả năng tiếp thu của học tr&ograve; v&igrave; l&yacute; do v&ocirc; c&ugrave;ng dễ thấy - sự thiếu tương t&aacute;c...</em></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sau khi Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo y&ecirc;u cầu tăng cường việc học qua Internet trong thời gian nghỉ tr&aacute;nh dịch Covid - 19. Đ&atilde; kh&ocirc;ng &iacute;t trường &aacute;p dụng phương ph&aacute;p dạy học online bằng b&agrave;i giảng được quay v&agrave; chuyển l&ecirc;n k&ecirc;nh Youtube, sau đ&oacute; Gi&aacute;o vi&ecirc;n v&agrave; học sinh sẽ thảo luận tr&ecirc;n nh&oacute;m Zalo hoặc Facebook.</p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/Online%20course/hn1.png\" style=\"height:265px; width:380px\" /></p>\r\n\r\n<p><em>(k&ecirc;nh Youtube đ&agrave;i ph&aacute;t th&agrave;nh truyền h&igrave;nh H&agrave; Nội, nội bộ&nbsp;trường THPT Nguyễn Du TPHCM &aacute;p dụng)&nbsp;</em></p>\r\n\r\n<p>V&agrave; ngo&agrave;i lợi &iacute;ch gi&uacute;p c&aacute;c em c&oacute; thể &ocirc;n b&agrave;i trong thời gian kh&ocirc;ng thể đến lớp, th&igrave; khuyết điểm kh&aacute; lớn trong phương ph&aacute;p t&aacute;ch biệt giữa dạy học v&agrave; tương t&aacute;c khiến tạo ra một số bất cập trong qu&aacute; tr&igrave;nh học online như gi&aacute;o vi&ecirc;n ngượng, kh&ocirc;ng đồng bộ hay ph&aacute;t triển th&ecirc;m kiến thức li&ecirc;n quan như khi dạy học th&ocirc;ng thường v&agrave; hầu như b&agrave;i giảng phải g&oacute;i gọn thời lượng lại. V&agrave; về ph&iacute;a học vi&ecirc;n, sẽ ảnh hưởng rất nhiều đến khả năng tập trung v&agrave;o b&agrave;i học.</p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/Online%20course/SL1200_.png\" style=\"height:255px; width:380px\" /></p>\r\n\r\n<p>Tuy nhi&ecirc;n, sẽ thế n&agrave;o nếu như giảm bớt sỉ số lớp, x&acirc;y dựng th&ecirc;m những phương ph&aacute;p học, lồng gh&eacute;p th&ecirc;m nhiều hoạt động c&ugrave;ng kết hợp với phần mềm trực tuyến gi&uacute;p tăng khả năng tương t&aacute;c giữa thầy c&ocirc; v&agrave; học vi&ecirc;n, th&igrave; dường như nhược điểm m&agrave; mọi người hay ngần ngại nhất trước quyết định c&oacute; n&ecirc;n tham gia kh&oacute;a học online, ho&agrave;n to&agrave;n đ&atilde; được giảm tối thiểu hay thậm ch&iacute; được khắc phục.</p>\r\n\r\n<p>Thật vậy, tại EVAS ch&uacute;ng t&ocirc;i &aacute;p dụng phương ph&aacute;p dạy học k&iacute;ch th&iacute;ch sự tương t&aacute;c rất nhiều ở học vi&ecirc;n bằng những chương tr&igrave;nh l&ocirc;ng gh&eacute;p, tạo sự hứng th&uacute; cho học vi&ecirc;n m&agrave; vẫn đảm bảo những kiến thức nền tảng lu&ocirc;n được trau dồi v&agrave; củng cố.</p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/Malie.png\" style=\"height:223px; width:380px\" /></p>\r\n\r\n<p><em>(H&igrave;nh ảnh được Phụ Huynh gửi đến &Acirc;u Việt &Uacute;c sau khi cho b&eacute; tham gia kh&oacute;a học online tại Trường)</em></p>\r\n\r\n<p>Bởi đặc th&ugrave; của việc học một ng&ocirc;n ngữ, học tiếng Anh l&agrave; qu&aacute; tr&igrave;nh k&eacute;o d&agrave;i li&ecirc;n tục song h&agrave;nh c&ugrave;ng sự tương t&aacute;c, ph&aacute;t &acirc;m, r&egrave;n luyện c&aacute;c kỹ năng n&oacute;i - nghe. V&agrave; cũng bởi v&igrave; qu&aacute; tr&igrave;nh n&agrave;y k&eacute;o d&agrave;i n&ecirc;n việc tạo được hứng th&uacute; trong học tập cũng l&agrave; một trong những ti&ecirc;u ch&iacute; hướng đến khi EVAS x&acirc;y dựng v&agrave; lu&ocirc;n ho&agrave;n thiện một chương tr&igrave;nh dạy.</p>\r\n\r\n<p>V&agrave; v&igrave; điều ch&uacute;ng t&ocirc;i kỳ vọng kh&ocirc;ng chỉ đơn thuần mang đến những kh&oacute;a học Anh văn, m&agrave; l&agrave; cả sự đam m&ecirc; theo đuổi, ki&ecirc;n tr&igrave; để đạt được th&agrave;nh tựu cao nhất tr&ecirc;n con đường chinh phục ng&ocirc;n ngữ của từng học vi&ecirc;n v&agrave; cả trong học thuật.</p>\r\n\r\n<p><em><strong>ANH NGỮ &Acirc;U VIỆT &Uacute;C</strong></em></p>\r\n', 'Online%20course/SL1200_.png', 1, 3, '2020-03-24 11:03:18', '2020-03-24 11:03:18'),
(75, 'SẼ THẾ NÀO NẾU ĐẶT KHÓA HỌC ONLINE LÊN CÁN CÂN?', 'se-the-nao-neu-dat-khoa-hoc-online-len-can-can', 'Phòng chống dịch vẫn còn tiếp tục, nhưng không vì vậy mà gián đoạn khả năng phát triển của các em trong giai đoạn này. EVAS sẽ đồng hành cùng các em vững tin với tiếng Anh của mình và luôn tin rằng Việt Nam chúng ta sẽ đại thắng cuộc chiến dịch này!', '<p>Do ảnh hưởng của dịch covid-19, Bộ gi&aacute;o dục quyết định cho nghỉ học tạm thời v&agrave; khuyến kh&iacute;ch ứng dụng c&aacute;c chương tr&igrave;nh giảng dạy online cho c&aacute;c cấp học phổ th&ocirc;ng.</p>\r\n\r\n<p>Ti&ecirc;n phong trong việc triển khai <strong><em>EVAS ONLINE LEARNING</em></strong>, Trung t&acirc;m định hướng l&acirc;u d&agrave;i v&agrave; x&acirc;y dựng lộ tr&igrave;nh học, thời gian học v&agrave; kiến thức học nhằm cam kết tối ưu chất lượng đ&agrave;o tạo.</p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/online%20ketpet%20avgt%20SLIDER.jpg\" style=\"height:204px; width:568px\" /></p>\r\n\r\n<p>Minh chứng cho điều n&agrave;y tất cả c&aacute;c lớp kết th&uacute;c những tuần học đầu ti&ecirc;n, gần tuyệt đối c&aacute;c em tham gia kh&oacute;a học tiếp theo, v&agrave; h&aacute;o hứng gặp gỡ thầy c&ocirc; bạn b&egrave; để giao tiếp tiếng Anh h&agrave;ng ng&agrave;y.</p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/MINT.png\" style=\"height:379px; width:380px\" /></p>\r\n\r\n<p>Với định hướng l&acirc;u d&agrave;i v&agrave; tuyệt đối về chất lượng n&ecirc;n chương tr&igrave;nh đ&agrave;o tạo được EVAS tối đa h&oacute;a cho người học như sau:</p>\r\n\r\n<p><strong><em>&nbsp; &nbsp;&sect;&nbsp;&nbsp;Kh&oacute;a học sỉ số &Iacute;T 1-6 học vi&ecirc;n;</em></strong></p>\r\n\r\n<p><strong><em>&nbsp; &nbsp;&sect;&nbsp;&nbsp;Học li&ecirc;n tục c&aacute;c ng&agrave;y trong tuần;</em></strong></p>\r\n\r\n<p><strong><em>&nbsp; &nbsp;&sect;&nbsp;&nbsp;Điều kiện c&aacute;c em giao tiếp Tiếng Anh trực tiếp với Gi&aacute;o vi&ecirc;n Việt Nam hoặc nước ngo&agrave;i MỖI NG&Agrave;Y;</em></strong></p>\r\n\r\n<p><strong><em>&nbsp; &nbsp;&sect;&nbsp;&nbsp;B&agrave;i dạy thiết kế trực quan sinh động;</em></strong></p>\r\n\r\n<p><strong><em>&nbsp; &nbsp;&sect;&nbsp;&nbsp;Tổng hợp kiến thức sau mỗi tuần học;</em></strong></p>\r\n\r\n<p>Với những tối ưu n&agrave;y c&ugrave;ng với &yacute; kiến t&iacute;ch cực từ phụ huynh học vi&ecirc;n, v&agrave; mỗi buổi học được cam kết chất lượng cho c&aacute;c em, EVAS tự tin trả lời rằng kh&oacute;a học ONLINE được đảm bảo chất lượng như kh&oacute;a học offline đ&agrave;o tạo tại EVAS.</p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/Tina.png\" style=\"height:379px; width:380px\" /></p>\r\n\r\n<p>Ph&ograve;ng chống dịch vẫn c&ograve;n tiếp tục, nhưng kh&ocirc;ng v&igrave; vậy m&agrave; gi&aacute;n đoạn khả năng ph&aacute;t triển của c&aacute;c em trong giai đoạn n&agrave;y. EVAS sẽ đồng h&agrave;nh c&ugrave;ng c&aacute;c em vững tin với tiếng Anh của m&igrave;nh v&agrave; lu&ocirc;n tin rằng Việt Nam ch&uacute;ng ta sẽ đai thắng dịch n&agrave;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em>ANH NGỮ &Acirc;U VIỆT &Uacute;C</em></strong></p>\r\n', 'Online%20course/scale.png', 1, 2, '2020-03-26 05:51:45', '2020-03-26 05:51:45');
INSERT INTO `news_items` (`id`, `title`, `code`, `description`, `detail`, `image`, `status`, `sort`, `created_at`, `updated`) VALUES
(76, '10 LÝ DO VÌ SAO PHẢI GIỎI TIẾNG ANH', '10-ly-do-vi-sao-phai-gioi-tieng-anh', 'Anh ngữ được xem là “chiếc chìa khóa vàng dẫn đến tương lai” là công cụ hữu hiệu để các bạn trẻ hội nhập vào nền giáo dục thế thới, từ đó có thể thực hiện ước mơ Du Học tại các quốc gia có nền giáo dục tiên tiến, đồng thời có thể giúp các bạn tự tin khám phá những vùng đất mới nơi có những nền văn hóa lạ lẫm, độc đáo đa sắc tộc,…', '<p><em><span style=\"color:#B22222\"><strong>1. Tiếng Anh l&agrave; ng&ocirc;n ngữ to&agrave;n cầu.</strong></span></em></p>\r\n\r\n<p>Tiếng Anh l&agrave; ng&ocirc;n ngữ được sử dụng phổ biến nhất tr&ecirc;n thế giới. Một trong năm người c&oacute; thể n&oacute;i hoặc &iacute;t nhất l&agrave; hiểu tiếng Anh!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em><span style=\"color:#B22222\"><strong>2. Với tiếng Anh, bạn c&oacute; thể học ở khắp nơi tr&ecirc;n thế giới.</strong></span></em></p>\r\n\r\n<p style=\"text-align:justify\">V&igrave; tiếng Anh được n&oacute;i ở rất nhiều quốc gia kh&aacute;c nhau, c&oacute; h&agrave;ng ng&agrave;n trường học v&agrave; đại học tr&ecirc;n khắp thế giới cung cấp c&aacute;c chương tr&igrave;nh bằng tiếng Anh. Nếu bạn n&oacute;i tiếng Anh, c&oacute; rất nhiều cơ hội để bạn t&igrave;m một trường học v&agrave; kh&oacute;a học ph&ugrave; hợp với nhu cầu học tập của m&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em><span style=\"color:#B22222\"><strong>3. Học tiếng Anh cơ hội việc l&agrave;m mở rộng.</strong></span></em></p>\r\n\r\n<p style=\"text-align:justify\">Tiếng Anh l&agrave; ng&ocirc;n ngữ của khoa học, của h&agrave;ng kh&ocirc;ng, m&aacute;y t&iacute;nh, ngoại giao v&agrave; du lịch. Biết tiếng Anh l&agrave;m tăng cơ hội c&oacute; được một c&ocirc;ng việc tốt trong một c&ocirc;ng ty đa quốc gia ở nước bạn hoặc t&igrave;m việc ở nước ngo&agrave;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em><span style=\"color:#B22222\"><strong>4. Học tiếng Anh c&oacute; thể gi&uacute;p bạn gặp gỡ những người mới.</strong></span></em></p>\r\n\r\n<p style=\"text-align:justify\">Tiếng Anh l&agrave; ng&ocirc;n ngữ ch&iacute;nh thức của 53 quốc gia v&agrave; được sử dụng như một ng&ocirc;n ngữ chung (ng&ocirc;n ngữ được biết đến với nhau) bởi mọi người từ khắp nơi tr&ecirc;n thế giới. Điều n&agrave;y c&oacute; nghĩa l&agrave; t&ocirc;i đang l&agrave;m việc ở Việt Nam hay đi du lịch ở Brazil, tiếng Anh c&oacute; thể gi&uacute;p t&ocirc;i c&oacute; một cuộc tr&ograve; chuyện với mọi người từ khắp nơi tr&ecirc;n thế giới.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em><span style=\"color:#B22222\"><strong>5. Tiếng Anh l&agrave; ng&ocirc;n ngữ của ng&agrave;nh truyền th&ocirc;ng.</strong></span></em></p>\r\n\r\n<p style=\"text-align:justify\">V&igrave; sự nổi tiếng của Hollywood tr&ecirc;n c&aacute;c phương tiện truyền th&ocirc;ng to&agrave;n cầu, một lượng lớn phim, chương tr&igrave;nh truyền h&igrave;nh v&agrave; c&aacute;c b&agrave;i h&aacute;t nổi tiếng được viết bằng tiếng Anh. Nếu bạn n&oacute;i tiếng Anh, bạn sẽ kh&ocirc;ng cần phải dựa v&agrave;o bản dịch v&agrave; phụ đề nữa để thưởng thức những cuốn s&aacute;ch, b&agrave;i h&aacute;t, bộ phim v&agrave; chương tr&igrave;nh TV y&ecirc;u th&iacute;ch của bạn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em><span style=\"color:#B22222\"><strong>6. Tiếng Anh l&agrave; ng&ocirc;n ngữ của Internet.</strong></span></em></p>\r\n\r\n<p style=\"text-align:justify\">Tiếng Anh l&agrave; một ng&ocirc;n ngữ đặc biệt quan trọng trực tuyến với hơn một nửa nội dung tr&ecirc;n internet được viết bằng tiếng Anh. Cũng như vậy, một số c&ocirc;ng ty c&ocirc;ng nghệ lớn nhất thế giới c&oacute; trụ sở tại c&aacute;c quốc gia n&oacute;i tiếng Anh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em><span style=\"color:#B22222\"><strong>7. Đi du lịch dễ d&agrave;ng hơn rất nhiều với kiến thức tiếng Anh tốt.</strong></span></em></p>\r\n\r\n<p style=\"text-align:justify\">H&atilde;y tưởng tượng bạn người Việt Nam đi du lịch ở Bồ Đ&agrave;o Nha, trong khi nh&acirc;n vi&ecirc;n lễ t&acirc;n kh&aacute;ch sạn của bạn c&oacute; thể kh&ocirc;ng trả lời được c&acirc;u hỏi của bạn bằng tiếng Việt hoặc bạn kh&ocirc;ng biết tiếng Bồ Đ&agrave;o Nha, nhất định bạn sẽ phải giao tiếp bằng tiếng Anh trong ngữ cảnh n&agrave;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em><span style=\"color:#B22222\"><strong>8. Tiếng Anh l&agrave; một trong những ng&ocirc;n ngữ quan trọng nhất đối với doanh nghiệp.</strong></span></em></p>\r\n\r\n<p style=\"text-align:justify\">Cho d&ugrave; một chủ doanh nghiệp, sinh vi&ecirc;n hay nh&acirc;n vi&ecirc;n, tiếng Anh l&agrave; v&ocirc; c&ugrave;ng quan trọng trong thế giới kinh doanh. Tiếng Anh được coi l&agrave; một trong những ng&ocirc;n ngữ kinh doanh quan trọng nhất do l&agrave; ng&ocirc;n ngữ thực tế của Hoa Kỳ v&agrave; ng&ocirc;n ngữ ch&iacute;nh thức của Vương quốc Anh, Canada, Ấn Độ v&agrave; Nam Phi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#B22222\"><em><strong>9. Nhiều b&agrave;i b&aacute;o khoa học được viết bằng tiếng Anh.</strong></em></span></p>\r\n\r\n<p style=\"text-align:justify\">Trong thế kỷ trước, số lượng b&agrave;i b&aacute;o khoa học viết bằng tiếng Anh đ&atilde; bắt đầu nhiều hơn số lượng b&agrave;i b&aacute;o viết bằng ng&ocirc;n ngữ bản địa của nh&agrave; nghi&ecirc;n cứu. V&igrave; l&yacute; do n&agrave;y, c&oacute; kiến thức về tiếng Anh l&agrave; v&ocirc; c&ugrave;ng quan trọng đối với những người l&agrave;m việc v&agrave; học tập trong lĩnh vực khoa học.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em><span style=\"color:#B22222\"><strong>10. Tiếng Anh cho ph&eacute;p bạn tiếp cận nhiều nền văn h&oacute;a.</strong></span></em></p>\r\n\r\n<p style=\"text-align:justify\">Một kiến thức tốt về tiếng Anh sẽ cho ph&eacute;p bạn truy cập c&aacute;c bộ phim, &acirc;m nhạc v&agrave; văn học từ h&agrave;ng trăm quốc gia tr&ecirc;n to&agrave;n cầu. Kh&ocirc;ng đề cập đến thực tế l&agrave; nhiều cuốn s&aacute;ch từ khắp nơi tr&ecirc;n thế giới được dịch sang tiếng Anh. V&agrave;i kinh nghiệm sẽ khiến bạn ph&aacute;t triển như một người hơn l&agrave; học c&aacute;c gi&aacute; trị, th&oacute;i quen v&agrave; c&aacute;ch sống trong một nền văn h&oacute;a kh&aacute;c biệt với bạn.</p>\r\n\r\n<p style=\"text-align:justify\"><em>(Dịch từ British Study School)</em></p>\r\n\r\n<p><strong><em>ANH NGỮ &Acirc;U VIỆT &Uacute;C </em></strong></p>\r\n', 'Online%20course/Learning-English-Online.png', 1, 4, '2020-03-28 02:29:12', '2020-03-28 02:29:12'),
(77, 'THÔNG BÁO NGHỈ LỄ GIỖ TỔ HÙNG VƯƠNG', 'thong-bao-nghi-le-gio-to-hung-vuong', 'Trung tâm Ngoại ngữ Quốc tế Âu Việt Úc xin thông báo lịch nghỉ Lễ Giỗ Tổ Hùng Vương năm 2020', '<p>Trung t&acirc;m Ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c xin th&ocirc;ng b&aacute;o đến Qu&yacute; Phụ huynh, Gi&aacute;o vi&ecirc;n v&agrave; Học vi&ecirc;n về ng&agrave;y nghỉ lễ:</p>\r\n\r\n<p><strong><em>Nghỉ 1 ng&agrave;y 02/4 Giỗ tổ H&ugrave;ng Vương.</em></strong></p>\r\n\r\n<p><strong><em><img alt=\"\" src=\"/upload/images/tin-tuc/Thongbao-1.png\" style=\"height:304px; width:380px\" /></em></strong></p>\r\n\r\n<p>Trung t&acirc;m sẽ trở lại hoạt động v&agrave;o Thứ S&aacute;u ng&agrave;y 03/4/2020.</p>\r\n\r\n<p>Trung t&acirc;m ch&uacute;c thầy c&ocirc;, phụ huynh, anh chị v&agrave; c&aacute;c em học vi&ecirc;n ng&agrave;y nghỉ thật vui vẻ! Mọi người giữ sức khỏe, nhớ mang khẩu trang v&agrave; rửa tay thường xuy&ecirc;n nh&eacute;!</p>\r\n\r\n<p><strong><em>ANH NGỮ &Acirc;U VIỆT &Uacute;C</em></strong></p>\r\n', 'tin-tuc/Thongbao-1.png', 1, 5, '2020-04-01 10:22:57', '2020-04-01 10:22:57'),
(78, 'ENGLISH TALK - ANH VĂN GIAO TIẾP', 'english-talk-anh-van-giao-tiep', 'Bạn đã sẵn sàng Giao tiếp Tiếng Anh cùng Giáo viên Bản xứ mỗi ngày chưa???', '<p><span style=\"color:#B22222\"><strong><em>Bạn sẽ l&agrave;m g&igrave; trong khoảng thời gian tr&aacute;nh dịch tại nh&agrave;?</em></strong></span></p>\r\n\r\n<p style=\"text-align:justify\">C&aacute;c bạn c&oacute; biết kh&ocirc;ng, Một trong những b&iacute; quyết chinh phục th&agrave;nh c&ocirc;ng tiếng Anh l&agrave; thời gian đấy. C&aacute;c bạn c&oacute; bao giờ tự hỏi những ng&agrave;y c&aacute;ch ly để chung tay bảo vệ đất nước an to&agrave;n v&agrave; th&agrave;nh c&ocirc;ng trong cuộc chiến dịch corona.</p>\r\n\r\n<p style=\"text-align:justify\">Bạn c&oacute; nghĩ l&agrave; ch&uacute;ng ta tận dụng thời gian n&agrave;y để cải thiện kỹ năng giao tiếp tiếng Anh của m&igrave;nh kh&ocirc;ng? Đ&atilde; nhiều lần l&ecirc;n kế hoạch học tiếng Anh nhưng lại kh&ocirc;ng c&oacute; thời gian, quyết t&acirc;m n&acirc;ng cao tiếng Anh nhưng chưa t&igrave;m được thầy c&ocirc; t&acirc;m huyết tự tin để d&igrave;u dắt bạn, mong muốn c&oacute; người c&ugrave;ng m&igrave;nh n&oacute;i tiếng Anh h&agrave;ng ng&agrave;y đ&uacute;ng kh&ocirc;ng n&agrave;o?...</p>\r\n\r\n<p style=\"text-align:justify\">Với t&acirc;m huyết c&ugrave;ng c&aacute;c bạn chinh phục tiếng Anh một c&aacute;ch hiệu quả nhất, EVAS cũng thực hiện phương ch&acirc;m &quot;Tạm dừng đến trường, kh&ocirc;ng dừng học&quot; của Bộ GD&amp;ĐT, Trung T&acirc;m Ngoại Ngữ Quốc Tế &Acirc;u Việt &Uacute;c tự h&agrave;o triển khai kh&oacute;a học ri&ecirc;ng biệt d&agrave;nh cho c&aacute;c bạn thiếu ni&ecirc;n, người đi l&agrave;m c&oacute; mong muốn trao dồi kỹ năng Anh ngữ đặc biệt l&agrave; kỹ năng GIAO TIẾP. Đặc biệt c&aacute;c bạn ở xa ho&agrave;n to&agrave;n tự tin tham gia kh&oacute;a học n&agrave;y.</p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/Online%20course/avgt.png\" style=\"height:233px; width:380px\" /></p>\r\n\r\n<p>Chương tr&igrave;nh học thiết kế đặc biệt cho c&aacute;c bạn chinh phục tiếng Anh thời gian li&ecirc;n tục, mang lại hiệu quả tối đa với những ti&ecirc;u ch&iacute;:</p>\r\n\r\n<p><strong><em>+ Thời gian Giao tiếp tiếng Anh li&ecirc;n tục mỗi ng&agrave;y;</em></strong></p>\r\n\r\n<p><strong><em>+ Sỉ số lớp 1 gi&aacute;o vi&ecirc;n: 1 học vi&ecirc;n hoặc 1 gi&aacute;o vi&ecirc;n: 6 học vi&ecirc;n;</em></strong></p>\r\n\r\n<p><strong><em>+ Kh&oacute;a học thiết kế 100% Gi&aacute;o vi&ecirc;n bản xứ hoặc Gi&aacute;o vi&ecirc;n Việt Nam;</em></strong></p>\r\n\r\n<p><strong><em>+ Hiệu quả phương ph&aacute;p truyền đạt v&agrave; tương t&aacute;c với học vi&ecirc;n;</em></strong></p>\r\n\r\n<p><strong><em>+ T&agrave;i liệu học bi&ecirc;n soạn chất lượng hiệu quả được cam kết;</em></strong></p>\r\n\r\n<p><strong><em>+ Đặc biệt cấp chứng nhận cho c&aacute;c bạn bổ sung hồ sơ du học nước ngo&agrave;i.</em></strong></p>\r\n\r\n<p><strong><em><img alt=\"\" src=\"/upload/images/Online%20course/Untitled-1.png\" style=\"height:300px; width:380px\" /></em></strong></p>\r\n\r\n<p style=\"text-align:justify\">Bạn c&oacute; thể đến hoặc gọi đến bất kỳ cơ sở &Acirc;u Việt &Uacute;c n&agrave;o để được c&aacute;c Thầy C&ocirc; tư vấn cụ thể về kh&oacute;a học cũng như c&aacute;c thức đăng k&yacute; tham gia kh&oacute;a học v&agrave; thời gian bắt đầu học cụ thể. V&agrave; khi đăng k&yacute; học tại Trung T&acirc;m, c&aacute;c bạn c&oacute; thể mang theo m&aacute;y t&iacute;nh c&aacute; nh&acirc;n của m&igrave;nh hoặc mở m&aacute;y tại nh&agrave; c&aacute;c Thầy c&ocirc; sẽ c&agrave;i đặt chương tr&igrave;nh học v&agrave; được hướng dẫn cụ thể hơn về c&aacute;ch sử dụng nh&eacute;!</p>\r\n\r\n<p style=\"text-align:justify\">Thầy c&ocirc; EVAS c&ugrave;ng đồng h&agrave;nh c&aacute;c em chinh phục tiếng Anh th&agrave;nh c&ocirc;ng!!! V&agrave; c&aacute;c em đang theo học kh&oacute;a học EVAS Online Learning tham gia đầy đủ c&aacute;c buổi học tại để t&iacute;ch lũy v&agrave; n&acirc;ng cao Tiếng Anh hằng ng&agrave;y nh&eacute;!</p>\r\n\r\n<p style=\"text-align:justify\">C&ugrave;ng giữ sức khỏe v&agrave; sử dụng thời gian hiệu quả trong những ng&agrave;y nghỉ tr&aacute;nh dịch!</p>\r\n\r\n<p><strong><em>ANH NGỮ &Acirc;U VIỆT &Uacute;C</em></strong></p>\r\n', 'Online%20course/avgt.png', 1, 2, '2020-04-03 08:14:31', '2020-04-03 08:14:31'),
(79, 'HỌC TIẾNG ANH ONLINE GIAO TIẾP TẠI EVAS', 'hoc-tieng-anh-online-giao-tiep-tai-evas', 'Bạn kỳ vọng điều gì khi tham gia khóa học Tiếng Anh tại EVAS?', '<p><strong><em>Bạn kỳ vọng điều g&igrave; khi tham gia kh&oacute;a học Tiếng Anh tại EVAS?</em></strong></p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/tin-tuc/english%20talk%201.png\" style=\"height:380px; width:380px\" /></p>\r\n\r\n<p><span style=\"color:#B22222\"><span style=\"font-size:16px\"><em><strong>CHỌN EVAS ĐỂ VIỆC HỌC TIẾNG ANH CỦA BẠN TRỞ N&Ecirc;N DỄ D&Agrave;NG HƠN</strong></em></span></span></p>\r\n\r\n<p><em><strong>100% M&Ocirc;I TRƯỜNG TIẾNG ANH</strong></em></p>\r\n\r\n<ul>\r\n	<li>&nbsp;M&ocirc;i trường học tập tại trung t&acirc;m v&agrave; đặc biệt tại &Acirc;u Việt &Uacute;c ho&agrave;n to&agrave;n 100% bằng tiếng Anh, v&igrave; thế đ&acirc;y sẽ l&agrave; cơ hội để c&aacute;c bạn được trải nghiệm m&ocirc;i trường học tập v&agrave; thực h&agrave;nh bằng tiếng Anh.</li>\r\n	<li>&nbsp;Đội ngũ Gi&aacute;o vi&ecirc;n người Việt v&agrave; người bản xứ tr&igrave;nh độ cao, t&acirc;m huyết v&agrave; cộng th&ecirc;m kinh nghiệm từ qu&aacute; tr&igrave;nh tự học của ch&iacute;nh họ v&agrave; cả về qu&aacute; tr&igrave;nh giảng dạy của họ. Ch&iacute;nh v&igrave; thế m&agrave; họ c&oacute; thể ho&agrave;n to&agrave;n hướng dẫn, k&egrave;m cặp bạn nhanh ch&oacute;ng tiến bộ v&agrave; theo đ&uacute;ng tiến độ.</li>\r\n	<li>Gi&aacute;o vi&ecirc;n trẻ, năng động, sẽ trở th&agrave;nh bạn đồng h&agrave;nh c&ugrave;ng bạn chinh phục tiếng Anh th&agrave;nh c&ocirc;ng.</li>\r\n</ul>\r\n\r\n<p><strong><em>GI&Aacute;O TR&Igrave;NH CHUY&Ecirc;N BIỆT LINH HOẠT V&Agrave; HIỆU QUẢ</em></strong></p>\r\n\r\n<ul>\r\n	<li>&nbsp;Gi&aacute;o tr&igrave;nh của học vi&ecirc;n được thiết kế ri&ecirc;ng biệt dựa tr&ecirc;n c&aacute;c ti&ecirc;u ch&iacute; đ&uacute;ng cấp độ khả năng ng&ocirc;n ngữ v&agrave; lĩnh vực học vi&ecirc;n muốn theo học. V&agrave; phải kể đến chương tr&igrave;nh Anh văn Giao tiếp online tại EVAS c&ograve;n khai th&aacute;c s&acirc;u v&agrave;o c&aacute;c chủ đề giao tiếp h&agrave;ng ng&agrave;y v&agrave; trong c&ocirc;ng việc&hellip;</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>C&aacute;c chủ đề tiếp cận một c&aacute;ch đơn giản, đi thẳng v&agrave;o thực tế ứng dụng khiến tăng việc hứng th&uacute; cho học vi&ecirc;n, dễ d&agrave;ng tiếp thu - dễ d&agrave;ng thực h&agrave;nh, ứng dụng.</li>\r\n</ul>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/tin-tuc/Untitled-1.png\" style=\"height:380px; width:380px\" /></p>\r\n\r\n<p><strong><em><span style=\"color:#B22222\">CHƯƠNG TR&Igrave;NH ĐẠO TẠO TIẾNG ANH ONLINE GIAO TIẾP H&Agrave;NG NG&Agrave;Y CỦA EVAS</span></em></strong></p>\r\n\r\n<p><strong><em>CẤP ĐỘ BASIC (12 CHỦ ĐỀ, 9 TUẦN HỌC CHUY&Ecirc;N S&Acirc;U)</em></strong></p>\r\n\r\n<p>&nbsp; Unit 1: Greetings</p>\r\n\r\n<p>&nbsp; Unit 2: Personal Information</p>\r\n\r\n<p>&nbsp; Unit 3: Family and Friends</p>\r\n\r\n<p>&nbsp; Unit 4: Possessions</p>\r\n\r\n<p>&nbsp; Unit 5: Interests</p>\r\n\r\n<p>&nbsp; Unit 6: Lifestyles</p>\r\n\r\n<p>&nbsp; &hellip;</p>\r\n\r\n<p><em><strong>CẤP ĐỘ ELEMENTARY (12 CHỦ ĐỀ, 9 TUẦN HỌC CHUY&Ecirc;N S&Acirc;U)</strong></em></p>\r\n\r\n<p>&nbsp; Unit 1: Introductions</p>\r\n\r\n<p>&nbsp; Unit 2: What a life</p>\r\n\r\n<p>&nbsp; Unit 3: Free time</p>\r\n\r\n<p>&nbsp; Unit 4: Places</p>\r\n\r\n<p>&nbsp; Unit 5: Getting around</p>\r\n\r\n<p>&nbsp; Unit 6: Shopping</p>\r\n\r\n<p>&nbsp; &hellip;</p>\r\n\r\n<p><strong><em>CẤP ĐỘ PRE-INTERMEDIATE (12 CHỦ ĐỀ, 9 TUẦN HỌC CHUY&Ecirc;N S&Acirc;U)- GI&Aacute;O VI&Ecirc;N BẢN NGỮ</em></strong></p>\r\n\r\n<p>&nbsp; Unit 1: Lifestyles</p>\r\n\r\n<p>&nbsp; Unit 2: Leisure</p>\r\n\r\n<p>&nbsp; Unit 3: Getting along</p>\r\n\r\n<p>&nbsp; Unit 4: Interests</p>\r\n\r\n<p>&nbsp; Unit 5: Telling a story</p>\r\n\r\n<p>&nbsp; Unit 6: Celebrations</p>\r\n\r\n<p>&nbsp; Unit 7: Foods and drinks</p>\r\n\r\n<p>&nbsp; &hellip;</p>\r\n\r\n<p><em>Chương tr&igrave;nh học tiếng Anh giao tiếp Online H&Agrave;NG NG&Agrave;Y tại EVAS chắc chắn sẽ mang đến cho bạn những hiệu quả như bạn mong đợi!</em></p>\r\n\r\n<p>H&atilde;y c&ugrave;ng sẵn s&agrave;ng giao tiếp tiếng Anh mỗi ng&agrave;y c&ugrave;ng EVAS!</p>\r\n\r\n<p><strong><em>ANH NGỮ &Acirc;U VIỆT &Uacute;C</em></strong></p>\r\n', 'abc%20gjk.png', 1, 2, '2020-04-04 04:26:45', '2020-04-04 04:26:45'),
(80, 'PHƯƠNG PHÁP ĐƠN GIẢN GIÚP BÉ LUYỆN NÓI TIẾNG ANH HIỆU QUẢ TẠI NHÀ', 'phuong-phap-don-gian-giup-be-luyen-noi-tieng-anh-hieu-qua-tai-nha', 'Những phương pháp ôn luyện tiếng Anh tại nhà giúp các bé vượt qua khoảng thời gian buồn chán mùa dịch...', '<p><img alt=\"\" src=\"/upload/images/Library/Campaign%20okay.jpg\" style=\"height:388px; width:300px\" /></p>\r\n\r\n<p><span style=\"font-size:16px\"><em><strong>PHƯƠNG PH&Aacute;P ĐƠN GIẢN GI&Uacute;P B&Eacute; LUYỆN N&Oacute;I TIẾNG ANH HIỆU QUẢ TẠI NH&Agrave;</strong></em></span></p>\r\n\r\n<p><span style=\"color:#B22222\"><span style=\"font-size:14px\"><strong><em>Học c&ugrave;ng b&eacute;</em></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\">H&atilde;y d&ugrave;ng &acirc;m nhạc! Anh chị phụ huynh c&oacute; thể mở những đoạn nhạc v&agrave; h&aacute;t c&ugrave;ng b&eacute;, bởi &acirc;m nhạc lu&ocirc;n l&agrave; phương ph&aacute;p hữu hiệu để ph&aacute;t triển khả năng ng&ocirc;n ngữ v&agrave; c&aacute;ch ph&aacute;t &acirc;m. Hiện tại những b&agrave;i h&aacute;t d&agrave;nh cho b&eacute; lu&ocirc;n được x&acirc;y dựng c&oacute; sự lồng gh&eacute;p cảm x&uacute;c theo từng lời h&aacute;t; v&igrave; thế, đ&acirc;y c&oacute; thể xem l&agrave; c&aacute;c học hiệu quả trong phản xạ v&agrave; c&aacute;ch biểu lộ cảm x&uacute;c trong ng&ocirc;n ngữ n&oacute;i của b&eacute;.</p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/Library/mom-son.jpg\" style=\"width:300px\" /></p>\r\n\r\n<p><em>(C&oacute; phải thời gian n&agrave;y lại l&agrave; dịp để cả gia đ&igrave;nh quay quần b&ecirc;n nhau? Vừa học vừa chơi...)</em></p>\r\n\r\n<p style=\"text-align:justify\">Việc cả nh&agrave; quay quần b&ecirc;n nhau c&ugrave;ng họ theo một nhịp điệu sẽ kh&ocirc;ng những tạo được sự th&iacute;ch th&uacute; khi học cho b&eacute; m&agrave; c&ograve;n l&agrave; những kỹ niệm gi&uacute;p b&eacute; in s&acirc;u hơn, ghi nhớ r&otilde; hơn về ng&ocirc;n ngữ n&agrave;y trong tiềm thức.</p>\r\n\r\n<p><span style=\"color:#B22222\"><span style=\"font-size:14px\"><strong><em>Đọc truyện mỗi tối </em></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\">Những mẫu truyện ngắn c&ugrave;ng h&igrave;nh ảnh, anh chị phụ huynh c&oacute; thể đọc v&agrave;o buổi tối thay cho c&acirc;u ch&uacute;c ngủ ngon, ngo&agrave;i việc tạo được những kỷ niệm đẹp trong khoảng thời gian tuổi thơ của b&eacute;, th&igrave; đ&acirc;y cũng sẽ l&agrave; c&aacute;ch học tiếng Anh lồng gh&eacute;p rất hiệu quả. Bởi n&atilde;o bộ kh&ocirc;ng ri&ecirc;ng g&igrave; trẻ con, tiếp nhận th&ocirc;ng tin gắn liền với h&igrave;nh ảnh v&agrave; hơn nữa với những kỷ niệm, cảm x&uacute;c hạnh ph&uacute;c, vui tươi sẽ gi&uacute;p ghi nhớ rất s&acirc;u v&agrave; rất d&agrave;i&hellip;</p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/Library/gettyimages-1148491229-640x640.jpg\" style=\"height:186px; width:330px\" /></p>\r\n\r\n<p><em>(H&atilde;y học c&ugrave;ng con v&agrave; c&ugrave;ng con ghi lại những kỷ niệm đẹp)</em></p>\r\n\r\n<p style=\"text-align:justify\">V&agrave; hiện tại Trung t&acirc;m đang hỗ trợ s&aacute;ch truyện tranh bằng tiếng Anh cho c&aacute;c b&eacute;, anh chị vui l&ograve;ng li&ecirc;n hệ v&agrave; gh&eacute; ngang Trung t&acirc;m k&yacute; mượn truyện hoặc inbox email EVAS sẽ gửi s&aacute;ch ebook. H&atilde;y gi&uacute;p c&aacute;c b&eacute; chuyển đổi những ng&agrave;y nghỉ dịch corona buồn ch&aacute;n th&agrave;nh những ng&agrave;y hiệu quả n&acirc;ng cao ng&ocirc;n ngữ v&agrave; h&igrave;nh th&agrave;nh th&oacute;i quen đọc s&aacute;ch cho c&aacute;c b&eacute;.&nbsp;</p>\r\n\r\n<p><strong><em>Chọn t&agrave;i liệu tự học sao cho ph&ugrave; hợp?</em></strong></p>\r\n\r\n<p style=\"text-align:justify\">Nếu &aacute;p dụng những phương ph&aacute;p tr&ecirc;n anh chị sẽ thấy được sự hiệu quả sau v&agrave;i tuần &ldquo;&ocirc;n b&agrave;i&rdquo; c&ugrave;ng b&eacute;, tuy nhi&ecirc;n để đạt được những hiệu quả mong muốn, khả năng tiếng Anh của c&aacute;c con cần được x&aacute;c định để hỗ trợ t&agrave;i liệu học hiệu quả v&agrave; tạo hứng th&uacute; cho c&aacute;c con.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"/upload/images/Library/good.jpg\" style=\"height:461px; width:330px\" /></p>\r\n\r\n<p><em>(Để lại b&igrave;nh luận dưới b&agrave;i hoặc inbox tại trang, EVAS sẽ chuẩn bị s&aacute;ch gửi đến Anh chị Phụ huynh)</em></p>\r\n\r\n<p><em>Hay đăng k&yacute; tại:&nbsp;<a href=\"https://forms.gle/2knJhXHHknjS85jc6\">https://forms.gle/2knJhXHHknjS85jc6</a></em></p>\r\n\r\n<p><strong><em>EVAS hỗ trợ kiểm tra tr&igrave;nh độ cho c&aacute;c b&eacute; chưa tham gia kh&oacute;a học của Trung t&acirc;m qua chương tr&igrave;nh &ldquo;KIỂM TRA TỪ XA TR&Aacute;NH CORONA&rdquo; tại EVAS.</em></strong></p>\r\n\r\n<p style=\"text-align:justify\">Tại nh&agrave;, b&eacute; c&oacute; thể được tham gia buổi kiểm tra dễ d&agrave;ng, tương tự như h&igrave;nh thức học tiếng Anh online c&ugrave;ng giao tiếp tiếng Anh mỗi ng&agrave;y c&ugrave;ng Gi&aacute;o vi&ecirc;n bản xứ, gi&aacute;o vi&ecirc;n Việt Nam v&agrave; bạn b&eacute;. V&agrave; Qu&yacute; Phụ Huynh cũng c&oacute; thể quan s&aacute;t, theo d&otilde;i được cả qu&aacute; tr&igrave;nh trao đổi, tương t&aacute;c giữa c&aacute;c thầy c&ocirc; với với b&eacute;.</p>\r\n\r\n<p style=\"text-align:justify\">Đặc biệt, sau khi kiểm tra tại EVAS, b&eacute; c&ograve;n c&oacute; thể được trải nghiệm th&ecirc;m 30 ph&uacute;t học tiếng Anh tr&ecirc;n ứng dụng Online. Chỉ cần đăng k&yacute; th&ocirc;ng tin, Trung t&acirc;m sẽ li&ecirc;n hệ đến phụ huynh trong thời gian nhanh nhất!</p>\r\n\r\n<p>H&atilde;y đăng k&yacute; cho con kiểm tra tr&igrave;nh độ tiếng Anh ngay h&ocirc;m nay!</p>\r\n\r\n<p><span style=\"color:#B22222\"><span style=\"font-size:18px\"><strong><em>ANH NGỮ QUỐC TẾ &Acirc;U VIỆT &Uacute;C</em></strong></span></span></p>\r\n', 'Library/iStock-693471394_wide.jpg', 1, 2, '2020-04-06 06:04:43', '2020-04-06 06:04:43'),
(81, 'HÃY LUÔN LÀ NGƯỜI ĐI TRƯỚC!', 'hay-luon-la-nguoi-di-truoc!', 'Hãy hình dung một viễn cảnh sau khi đại dịch qua đi, nhân sự chưa có việc làm khá nhiều và nhu cầu của doanh nghiệp cần nhân sự để phục hồi kinh doanh cũng khá lớn. Chính vì thế doanh nghiệp sẽ có khá nhiều sự chọn lọc trong việc tuyển chọn nhân sự...', '<p style=\"text-align:justify\"><strong><em><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">H&atilde;y h&igrave;nh dung một viễn cảnh sau khi đại dịch qua đi, nh&acirc;n sự chưa c&oacute; việc l&agrave;m kh&aacute; nhiều v&agrave; nhu cầu của doanh nghiệp cần nh&acirc;n sự để phục hồi kinh doanh cũng kh&aacute; lớn. Ch&iacute;nh v&igrave; thế doanh nghiệp sẽ c&oacute; kh&aacute; nhiều sự chọn lọc trong việc tuyển chọn nh&acirc;n sự.</span></span></em></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#B22222\"><strong><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">H&atilde;y lu&ocirc;n l&agrave; người đi trước! Sự lựa chọn&nbsp;-&nbsp;tương lai nằm trong tay bạn!</span></span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"/upload/images/Online%20course/2020_04_17_14_53_IMG_9579.JPG\" style=\"height:220px; width:330px\" /></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Nếu bạn đang nằm trong danh s&aacute;ch bị &ldquo;cắt giảm nh&acirc;n sự&rdquo;, bạn cần n&acirc;ng cao khả năng của ch&iacute;nh m&igrave;nh để vươn l&ecirc;n vị tr&iacute; cao hơn trong c&ocirc;ng ty,&nbsp;hoặc muốn chuẩn bị c&aacute;c kỹ năng cần thiết ho&agrave;n hảo bản th&acirc;n m&igrave;nh...&nbsp;vậy th&igrave; thời gian nghỉ tr&aacute;nh dịch tại nh&agrave; sẽ l&agrave; thời điểm l&yacute; tưởng để bạn tập trung đầu tư v&agrave;o ch&iacute;nh con người m&igrave;nh. </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Đọc s&aacute;ch, nghi&ecirc;n cứu một lĩnh vực kh&aacute;c hay bắt đầu những bước đầu ti&ecirc;n để chinh phục ng&ocirc;n ngữ kh&aacute; th&ocirc;ng dụng tr&ecirc;n Thế giới - Anh ngữ. Đ&acirc;y sẽ l&agrave; điểm nhấn gi&uacute;p bạn ghi điểm với nh&agrave; tuyển dụng, v&agrave; l&agrave; chiếc ch&igrave;a kh&oacute;a mở ra c&aacute;nh cửa cơ hội cho con đường ph&aacute;t triển sự nghiệp của ch&iacute;nh bạn.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"/upload/images/Online%20course/leader.png\" style=\"height:209px; width:330px\" /></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">V&agrave; nếu bạn băn khoăn kh&ocirc;ng biết phải n&ecirc;n bắt đầu từ đ&acirc;u, bạn c&oacute; thể đăng k&yacute; tham gia chương tr&igrave;nh &ldquo;HỖ TRỢ KIỂM TRA TỪ XA - TR&Aacute;NH CORONA&rdquo; tại EVAS.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Tại nh&agrave;, bạn c&oacute; thể được tham gia buổi kiểm tra dễ d&agrave;ng, tương tự như h&igrave;nh thức học tiếng Anh online giao tiếp tiếng Anh mỗi ng&agrave;y c&ugrave;ng Gi&aacute;o vi&ecirc;n bản xứ, gi&aacute;o vi&ecirc;n Việt Nam. </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Chỉ cần đăng k&yacute; th&ocirc;ng tin, Trung t&acirc;m sẽ hỗ trợ giải đ&aacute;p v&agrave; th&ocirc;ng tin chi tiết c&aacute;c thắc mắc của bạn.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Đăng k&yacute; tại đ&acirc;y:&nbsp;</span></span><a href=\"https://forms.gle/eTP6yZDjoW8BBRYeA\">https://forms.gle/eTP6yZDjoW8BBRYeA​</a>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Hoặc v&agrave;o website: auvietuc.edu.vn</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><em><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">EVAS đồng h&agrave;nh c&ugrave;ng học vi&ecirc;n chinh phục Tiếng Anh th&agrave;nh c&ocirc;ng&nbsp;trong mọi ho&agrave;n cảnh!&nbsp;</span></span></em></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"color:#B22222\"><strong><em><span style=\"font-family:arial,helvetica,sans-serif\">ANH NGỮ &Acirc;U VIỆT &Uacute;C</span></em></strong></span></span></p>\r\n', 'Online%20course/leader%201.jpg', 1, 2, '2020-04-17 10:14:09', '2020-04-17 10:14:09'),
(82, 'VÌ SAO NÊN CÓ CHỨNG CHỈ TOEIC?', 'vi-sao-nen-co-chung-chi-toeic', 'Để có được một tấm vé bước vào công ty thương mại quốc tế hay để đạt được sự thăng tiến nhất định, ngoài yêu cầu cơ bản về những bằng cấp về chuyên môn thì đó là một tấm bằng TOEIC - năng lực tiếng Anh của chính bạn.', '<p style=\"text-align:justify\"><span style=\"color:#B22222\"><em><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong><strong>1. Chứng chỉ TOEIC l&agrave; thước đo đ&aacute;nh gi&aacute; năng lực tiếng Anh của bạn</strong></strong></span></span></em></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Chứng chỉ TOEIC được tin cậy&nbsp;v&agrave; được sử dụng rộng r&atilde;i&nbsp;tr&ecirc;n to&agrave;n Thế giới với những quốc gia sử dụng ng&ocirc;n ngữ tiếng Anh trong giao dịch thương mại v&agrave; việc l&agrave;m, chương tr&igrave;nh TOEIC cung cấp chứng chỉ đ&aacute;ng tin cậy m&agrave; bạn cần để chứng minh c&aacute;c kỹ năng tiếng Anh của m&igrave;nh cho c&aacute;c nh&agrave; tuyển dụng tiềm năng tr&ecirc;n to&agrave;n thế giới. Bằng c&aacute;ch đ&aacute;nh gi&aacute; bốn kỹ năng tiếng Anh ch&iacute;nh l&agrave; nghe, n&oacute;i, đọc&nbsp;v&agrave; viết cho nơi l&agrave;m việc, c&aacute;c b&agrave;i kiểm tra TOEIC đ&aacute;nh gi&aacute; to&agrave;n bộ c&aacute;c kỹ năng giao tiếp của bạn.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><em><span style=\"color:#B22222\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong><strong>2. Nổi bật so với đối thủ</strong></strong><strong><strong>, tăng cơ hội tiềm kiếm việc l&agrave;m</strong></strong></span></span></span></em></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Thị trường việc l&agrave;m hội nhập&nbsp;quốc tế ng&agrave;y nay với mức độ cạnh tranh&nbsp;kh&aacute; cao. V&agrave; với một nền kinh tế c&ugrave;ng&nbsp;văn h&oacute;a l&agrave;m việc ng&agrave;y c&agrave;ng to&agrave;n cầu h&oacute;a,&nbsp;mức độ quan trọng của kiến thức về ngoại ngữ của bạn c&oacute; thể được xem&nbsp;như ch&iacute;nh bằng cấp v&agrave; kinh nghiệm chuy&ecirc;n m&ocirc;n của bạn. V&igrave; thế, c&aacute;c b&agrave;i kiểm tra TOEIC,&nbsp;ch&iacute;nh x&aacute;c l&agrave; những g&igrave; c&aacute;c nh&agrave; tuyển dụng đang t&igrave;m kiếm, đặc biệt l&agrave; đối với&nbsp;nhiều tổ chức v&agrave; c&ocirc;ng ty c&oacute; m&ocirc;i trường l&agrave;m việc li&ecirc;n quan đến thương mại quốc tế. </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">C&oacute; trong tay một chứng chỉ tiếng Anh&nbsp;trong hồ sơ của bạn kh&ocirc;ng chỉ chứng minh tr&igrave;nh độ tiếng Anh của bạn, m&agrave; c&ograve;n cho nh&agrave; tuyển dụng thấy rằng bạn sẵn s&agrave;ng d&agrave;nh thời gian để cải thiện tiếng Anh v&agrave; kỹ năng chuy&ecirc;n m&ocirc;n của bạn. Giữa x&atilde; hội với đa phần t&acirc;m l&yacute; của&nbsp;hầu hết c&aacute;c sinh vi&ecirc;n cố tr&aacute;nh l&agrave;m c&aacute;c b&agrave;i kiểm tra c&agrave;ng nhiều c&agrave;ng tốt, việc bạn gi&agrave;nh thời gian chuẩn bị v&agrave; l&agrave;m b&agrave;i kiểm tra TOEIC &nbsp;chứng r&otilde; r&agrave;ng cho thấy bạn quyết t&acirc;m v&agrave; d&aacute;m đi trước.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><em><span style=\"color:#B22222\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong><strong>3. Chứng chỉ TOEIC cần thiết để ra trường cho c&aacute;c bạn sinh vi&ecirc;n </strong></strong></span></span></span></em></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Hơn thế nữa, <span style=\"color:#000000\">nhiều trường Đại học, Cao đẳng&nbsp;hiện tại&nbsp;đ&atilde; đưa TOEIC v&agrave;o chương tr&igrave;nh giảng dạy chuẩn v&agrave; lựa chọn b&agrave;i thi TOEIC l&agrave;m kim chỉ nam cho việc&nbsp;theo d&otilde;i&nbsp;sự tiến bộ trong việc học tiếng Anh&nbsp;đối với sinh vi&ecirc;n theo từng học kỳ, năm học hoặc sử dụng l&agrave;m chuẩn đầu ra tiếng Anh cho sinh vi&ecirc;n tốt nghiệp. Đ&oacute; l&agrave; những l&yacute; do khiến&nbsp;việc&nbsp;học TOEIC,&nbsp;luyện thi TOEIC&nbsp;v&agrave; tham dự&nbsp;kỳ thi TOEIC&nbsp;đ&oacute;ng vai tr&ograve; quan trọng trong việc chuẩn bị h&agrave;nh trang kiến thức&nbsp;với nhiều sinh vi&ecirc;n.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><em><span style=\"color:#B22222\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong><strong>4. Vượt qua TOEIC để tham &nbsp;gia MBA hoặc Thạc sĩ chuy&ecirc;n ng&agrave;nh</strong></strong></span></span></span></em></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">TOEIC kh&ocirc;ng chỉ đơn thuần hướng tới c&aacute;c sinh vi&ecirc;n muốn tham gia cuộc sống chuy&ecirc;n nghiệp sau khi kết th&uacute;c việc học. Bạn c&oacute; thể đ&atilde; nghe n&oacute;i về c&aacute;c Master cung cấp một đội h&igrave;nh quản l&yacute; cao cấp, được gọi l&agrave; MBA (Master of Business quản trị). C&aacute;c kh&oacute;a học n&agrave;y d&agrave;nh cho những người đ&atilde; c&oacute; một v&agrave;i năm kinh nghiệm trong lĩnh vực của họ v&agrave; muốn n&acirc;ng cao n&oacute; để c&oacute; được một chương tr&igrave;nh khuyến m&atilde;i trong c&ocirc;ng ty. </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">V&agrave; y&ecirc;u cầu cơ bản để c&oacute; được một tấm v&eacute; tham gia kh&oacute;a học n&agrave;y hoặc để đảm bảo việc bạn c&oacute; thể tiếp thu tối đa những kiến thức hiệu quả nhất của kh&oacute;a học, đ&oacute; l&agrave; một tấm bằng TOEIC - năng lực tiếng Anh của ch&iacute;nh bạn.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><em><span style=\"color:#B22222\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong><strong>5. Bước đệm ho&agrave;n hảo dẫn đến những chứng chỉ tiếp theo</strong></strong></span></span></span></em></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">TOEIC ph&ugrave; hợp hơn với những người mới bắt đầu. Bước đầu ti&ecirc;n để chinh phục được mức độ cao hơn trong nấc thang bằng cấp đ&aacute;nh gi&aacute; khả năng tiếng Anh th&igrave; chương tr&igrave;nh TOEIC được đ&aacute;nh gi&aacute; kh&aacute; cao về việc cung cấp một nền tảng kiến thức kh&aacute; vững v&agrave;ng d&agrave;nh cho người học. Bởi sau khi tham gia thi TOEIC th&igrave; khả năng ng&ocirc;n ngữ tiếng anh của bạn sẽ được tiếng bộ r&otilde; rệt, hầu như to&agrave;n diện về 4 kỹ năng cơ bản. V&agrave; đ&acirc;y l&agrave; bước đệm ho&agrave;n hảo để bạn c&oacute; thể chinh phục những chứng chỉ tiếng anh quốc tế kh&aacute;c như IELTS, TOEFL&hellip;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#B22222\"><em><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong><strong>6. Để tăng mức độ động lực </strong></strong><strong><strong>học tiếng anh </strong></strong><strong><strong>của bạn</strong></strong></span></span></em></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Động lực đ&oacute;ng một vai tr&ograve; quan trọng trong qu&aacute; tr&igrave;nh học<em> </em>một ng&ocirc;n ngữ mới. C&aacute;c nghi&ecirc;n cứu được thực hiện bởi Mantasiah R. v&agrave; Yusri cho thấy c&oacute; mối quan hệ trực tiếp giữa việc học ng&ocirc;n ngữ v&agrave; động lực. V&agrave; kết quả cho thấy rằng&nbsp;việc học ng&ocirc;n ngữ c&oacute; thể l&agrave;m tăng động lực học tập v&agrave; khả năng tự học của sinh vi&ecirc;n. L&agrave;m một b&agrave;i kiểm tra v&agrave; cải thiện tr&igrave;nh độ tiếng Anh của bạn c&oacute; thể nh&igrave;n thấy, sẽ th&uacute;c đẩy động lực của bạn để l&agrave;m tốt hơn v&agrave; tiếp tục học v&agrave; sử dụng ng&ocirc;n ngữ&nbsp;tiếng anh.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><em><span style=\"font-size:16px\"><span style=\"color:#B22222\"><span style=\"font-family:arial,helvetica,sans-serif\">ANH NGỮ &Acirc;U VIỆT &Uacute;C</span></span></span></em></strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n', 'goc-hoc-tap/TOEIC.jpg', 0, 2, '2020-04-19 12:59:33', '2020-04-19 12:59:33'),
(83, 'THÔNG BÁO: NGÀY TRỞ LẠI TRƯỜNG!', 'thong-bao:-ngay-tro-lai-truong!', 'Về thời gian chính thức đi học trở lại vào Thứ Hai tuần sau, ngày 04/05/2020', '<p><img alt=\"\" src=\"/upload/images/tin-tuc/Back%20to%20school.jpg\" style=\"width:300px\" /></p>\r\n\r\n<p>Sau một kỳ nghỉ d&agrave;i kỷ lục, Trung t&acirc;m xin th&ocirc;ng tin đến c&aacute;c Qu&yacute; Phụ Huynh v&agrave; to&agrave;n thể em học sinh c&aacute;c cấp độ về thời gian ch&iacute;nh thức đi học trở lại v&agrave;o <strong><em>Thứ Hai tuần sau, ng&agrave;y 04/05/2020.</em></strong></p>\r\n\r\n<p>Xin gửi lời cảm ơn ch&acirc;n th&agrave;nh đến Qu&yacute; Phụ Huynh v&agrave; c&aacute;c em hoc vi&ecirc;n đ&atilde; lu&ocirc;n tin tưởng v&agrave; đồng h&agrave;nh c&ugrave;ng EVAS trong khoản thời gian qua.</p>\r\n\r\n<p>N&agrave;o c&ugrave;ng khởi động lại với lớp học tiếng Anh hiệu quả tại EVAS, c&ugrave;ng thầy c&ocirc; Gi&aacute;o vi&ecirc;n Nước Ngo&agrave;i th&acirc;n thiện, tận t&igrave;nh, cho c&aacute;c bạn cảm gi&aacute;c tự tin thoải m&aacute;i khi giao tiếp, c&ugrave;ng thầy c&ocirc; Việt Nam lu&ocirc;n theo s&aacute;t động vi&ecirc;n phụ đạo khi c&aacute;c bạn cần sự hỗ trợ.</p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#B22222\"><strong><em>ANH NGỮ &Acirc;U VIỆT &Uacute;C</em></strong></span></span></p>\r\n', 'tin-tuc/Back%20to%20school%20zalo.jpg', 1, 5, '2020-04-28 01:32:39', '2020-04-28 01:32:39'),
(84, '\"MY THREE WORDS\" - VÀ NHỮNG HÌNH ẢNH NGÀY TRỞ LẠI TRƯỜNG', 'my-three-words-va-nhung-hinh-anh-ngay-tro-lai-truong', 'Hãy cùng điểm lại những hình ảnh đáng nhớ của những \\\"buổi đầu đi học lại\\\" và cùng các bé chia sẻ cảm nhận về kỳ nghỉ Tết \\\"dài nhất lịch sử\\\" qua chương trình My Three Words...', '<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"color:#B22222\"><strong>MY THREE WORDS</strong></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"color:#B22222\"><strong>WHAT ARE YOURS?</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"/upload/images/Newone/three%20words.png\" style=\"width:300px\" /></p>\r\n\r\n<p style=\"text-align:justify\">Một hoạt động&nbsp;nằm trong chương tr&igrave;nh &quot;LET&#39;S CHALLENGE&quot; được tổ chức h&agrave;ng tuần&nbsp;tại EVAS với mục đ&iacute;ch tạo ra một s&acirc;n chơi, kh&ocirc;ng chỉ d&agrave;nh ri&ecirc;ng cho c&aacute;c b&eacute;, c&aacute;c&nbsp;bạn học vi&ecirc;n đang theo c&aacute;c kh&oacute;a học tại EVAS, m&agrave; c&ograve;n l&agrave; nơi để c&aacute;c bạn đam m&ecirc; chinh phục tiếng Anh c&ugrave;ng củng cố kiến thức.</p>\r\n\r\n<p>V&agrave; sau một kỳ nghỉ rất d&agrave;i, khởi đầu cho những buổi học &ldquo;đầu năm&rdquo;, v&agrave; &quot;l&agrave;m n&oacute;ng&quot; trước khi tiếp tục con đường chinh phục tiếng Anh, c&aacute;c bạn đ&atilde; c&ugrave;ng thầy c&ocirc; viết l&ecirc;n cảm nghĩ v&agrave; trải nghiệm của c&aacute;c bạn những th&aacute;ng ng&agrave;y nghỉ qua hoạt động Warm-up: MY THREE WORDS.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Newone/three%20words.png', 1, 4, '2020-05-06 13:59:22', '2020-05-06 13:59:22'),
(85, 'CHƯƠNG TRÌNH \"CÙNG HỌC CÙNG TIẾN\" - PEER LEARNING', 'chuong-trinh-cung-hoc-cung-tien-peer-learning', 'Peer Learing là phương pháp học, người học cùng chia sẽ kiến thức học hỏi cùng nhau để cùng nhau phát triển...', '<p><span style=\"font-size:14px\"><strong><em>Peer Learing l&agrave; phương ph&aacute;p học, người học c&ugrave;ng chia sẽ kiến thức học hỏi c&ugrave;ng nhau để c&ugrave;ng nhau ph&aacute;t triển.</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Dựa v&agrave;o m&ocirc; h&igrave;nh &ldquo;Innovative Learning Cells&rdquo; - nh&oacute;m học tập s&aacute;ng tạo, EVAS x&acirc;y dựng th&agrave;nh c&ocirc;ng chương tr&igrave;nh hỗ trợ cho c&aacute;c bạn học vi&ecirc;n c&ugrave;ng bạn đồng h&agrave;nh c&ugrave;ng tiến l&acirc;u d&agrave;i v&agrave; chinh phục tiếng Anh th&agrave;nh c&ocirc;ng, c&ugrave;ng với sự hướng dẫn nhiệt t&igrave;nh Gi&aacute;o vi&ecirc;n nước ngo&agrave;i, sự hỗ trợ gần gũi Gi&aacute;o vi&ecirc;n Việt Nam.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Tối ưu chương tr&igrave;nh khi c&aacute;c bạn đăng k&yacute; Peer Learing:</strong></p>\r\n\r\n<ul>\r\n	<li>&nbsp;Hỗ trợ học ph&iacute; l&ecirc;n đến 30%;</li>\r\n	<li>&nbsp;Giao tiếp v&agrave; phụ đạo ri&ecirc;ng c&ugrave;ng Gi&aacute;o vi&ecirc;n nước ngo&agrave;i;</li>\r\n	<li>&nbsp;Được hướng dẫn chi tiết lộ tr&igrave;nh tự học hiệu quả;</li>\r\n	<li>&nbsp;Hỗ trợ bộ t&agrave;i liệu tự học v&ocirc; gi&aacute;;</li>\r\n	<li>Cơ hội n&oacute;i tiếng Anh h&agrave;ng ng&agrave;y với người c&ugrave;ng ch&iacute; hướng với m&igrave;nh;</li>\r\n</ul>\r\n\r\n<p>C&ugrave;ng với gi&aacute;o vi&ecirc;n đầy nhiệt huyết v&agrave; chương tr&igrave;nh đ&agrave;o tạo độc quyền, Trung t&acirc;m tự tin mang đến chất lượng hơn cả người học mong đợi.</p>\r\n\r\n<p><em><span style=\"font-size:16px\"><span style=\"color:#B22222\"><strong>ANH NGỮ QUỐC TẾ &Acirc;U VIỆT &Uacute;C</strong></span></span></em></p>\r\n', 'Online%20course/Peer%20learning%20zalo.jpg', 1, 2, '2020-05-13 01:11:13', '2020-05-13 01:11:13'),
(86, 'KỲ THI THỬ CAMBRIDGE 2020 ', 'ky-thi-thu-cambridge-2020', 'Thứ 6 ngày 19/6/2020 vừa qua, Trung tâm Ngoại ngữ Quốc tế Âu Việt Úc liên kết tổ chức thi thử chứng chỉ Anh ngữ quốc tế Cambridge cho các em học sinh trường Tiểu học trong tỉnh...', '<p><strong>K</strong><strong>Ỳ THI THỬ ANH NGỮ QUỐC TẾ CAMBRIDGE]</strong></p>\r\n\r\n<p>Thứ 6 ng&agrave;y 19/6/2020 vừa qua, Trung t&acirc;m Ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c li&ecirc;n kết tổ chức thi thử chứng chỉ Anh ngữ quốc tế Cambridge cho c&aacute;c em học sinh trường<strong><em>&nbsp;Tiểu học CH&Acirc;U VĂN LI&Ecirc;M</em></strong> v&agrave; trường <strong><em>Tiểu học CHU VĂN AN</em></strong> tại th&agrave;nh phố Long Xuy&ecirc;n. Qua kỳ thi n&agrave;y, gi&uacute;p c&aacute;c em l&agrave;m quen với định dạng đề thi quốc tế Cambridge v&agrave; vững tin với kiến thức cho trải nghiệm kỳ thi thật sắp tới th&aacute;ng 8/2020.</p>\r\n\r\n<p>Chứng chỉ Anh ngữ quốc tế Cambridge quan trọng như thế n&agrave;o c&aacute;c em học sinh cấp tiểu học:</p>\r\n\r\n<ol>\r\n	<li><em><strong>Thước đo đ&aacute;nh gi&aacute; tiếng Anh chuẩn quốc tế của c&aacute;c con;</strong></em></li>\r\n	<li><em><strong>Cơ hội v&agrave;o trường chuy&ecirc;n v&agrave; Lớp tiếng Anh tăng cường: C&aacute;c con đạt Chứng chỉ Cambridge Flyers 10/15 được xem x&eacute;t tuyển thẳng v&agrave;o trường chuy&ecirc;n - lớp chọn tiếng Anh cấp THCS;</strong></em></li>\r\n	<li><em><strong>V&agrave; cũng l&agrave; chứng chỉ để quyết định h&agrave;nh tr&igrave;nh du học c&aacute;c cấp THCS, THPT &hellip; của c&aacute;c con trong tương lai.</strong></em></li>\r\n	<li><em><strong>Quan trọng nhất l&agrave; sự tự tin của c&aacute;c con qua trải nghiệm kỳ thi quốc tế, khơi nguồn hứng th&uacute;, động lực, ph&aacute;t triển đam m&ecirc; ng&ocirc;n ngữ tiếng Anh;</strong></em></li>\r\n</ol>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/tin-tuc/104948679_1356834874516546_3280296872378355954_o.jpg\" style=\"height:248px; width:330px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/tin-tuc/105420446_1356834997849867_151254387981452929_o.jpg\" style=\"height:248px; width:330px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/tin-tuc/103765041_1356835004516533_4627354825024947340_o.jpg\" style=\"height:248px; width:330px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/tin-tuc/104866700_1356835077849859_2533730972110987020_o.jpg\" style=\"height:248px; width:330px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/tin-tuc/105385940_1356834954516538_5205261921034396293_o.jpg\" style=\"height:248px; width:330px\" /></p>\r\n\r\n<p>&nbsp;<em>EVAS mong muốn đồng h&agrave;nh v&agrave; hỗ trợ phụ huynh, c&aacute;c em chinh phục th&agrave;nh c&ocirc;ng c&aacute;c chứng chỉ tiếng Anh quốc tế. Để tham gia lớp &ocirc;n luyện v&agrave; kỳ thi, qu&yacute; phụ huynh vui l&ograve;ng li&ecirc;n hệ trực tiếp văn ph&ograve;ng để c&aacute;c em được hướng dẫn chi tiết, hiệu quả nhất.</em></p>\r\n\r\n<p><span style=\"color:#B22222\"><span style=\"font-size:18px\"><strong><em>ANH NGỮ QUỐC TẾ &Acirc;U VIỆT &Uacute;C</em></strong></span></span></p>\r\n', 'tin-tuc/Standee.jpg', 1, 3, '2020-06-24 07:19:51', '2020-06-24 07:19:51'),
(87, 'ĐĂNG KÝ BỔ SUNG HỒ SƠ THI CAMBRIDGE 2020', 'dang-ky-bo-sung-ho-so-thi-cambridge-2020', 'EVAS nhận đăng ký bổ sung các thí sinh chưa hoàn thành thủ tục kỳ thi chứng chỉ Anh ngữ Quốc tế Cambridge...', '<p>EVAS nhận đăng k&yacute; bổ sung c&aacute;c th&iacute; sinh chưa ho&agrave;n th&agrave;nh thủ tục kỳ thi chứng chỉ Anh ngữ Quốc tế Cambridge.</p>\r\n\r\n<ol>\r\n	<li><em><strong><span style=\"color:#000080\">Ng&agrave;y thi:</span></strong></em> <strong>Thứ bảy</strong> ng&agrave;y <strong><em>8/8/2020</em></strong> cấp độ YLE (Starters, Movers, Flyers);</li>\r\n	<li><strong>Chủ nhật ng&agrave;y<em>&nbsp;9/8/2020</em></strong> cấp độ KET (A2) &ndash; PET (B1-B2)</li>\r\n	<li>Tại: <strong><em>Trung t&acirc;m ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c</em></strong></li>\r\n	<li>Đăng k&yacute; trước ng&agrave;y: Thứ 7, 28/6/2020.</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><em>Như th&ocirc;ng tin đến phụ huynh: Chứng chỉ Cambridge Starters, Movers, Flyers l&agrave; ba chứng chỉ tiếng Anh quốc tế d&agrave;nh cho trẻ em từ 7 đến 12 tuổi, đ&aacute;nh gi&aacute; tiếng Anh theo chuẩn quốc tế.</em></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><em>Ngo&agrave;i ra, theo&nbsp;quy định mới&nbsp;của bộ Gi&aacute;o dục &amp; Đ&agrave;o tạo, chứng chỉ tiếng Anh Cambridge đ&atilde; trở th&agrave;nh điều kiện x&eacute;t v&agrave;o lớp tiếng Anh tăng cường của nhiều trường tiểu học.</em></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><em>Theo đ&oacute;, hết lớp 3, b&eacute; phải c&oacute; chứng chỉ Starters, ho&agrave;n th&agrave;nh lớp 4 phải c&oacute; chứng chỉ Movers, ho&agrave;n th&agrave;nh lớp 5 phải c&oacute; chứng chỉ Flyers để tiếp theo lớp học tiếng Anh tăng cường bậc THCS.</em></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><em>Nhằm hỗ trợ học vi&ecirc;n v&agrave; phụ huynh sẵn s&agrave;ng c&aacute;c chứng chỉ v&agrave; vững tin bước v&agrave;o lớp tiếng Anh tăng cường. EVAS triển khai tiếp tục nhận hồ sơ dự thi hết ng&agrave;y:</em>\r\n\r\n	<ul>\r\n		<li><strong><em>Cấp độ KET - PET: 24/6/2020.</em></strong></li>\r\n		<li><em><strong>Cấp độ Young Learners (Starters-Movers-Flyers): 28/6/2020</strong>.</em></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#B22222\"><em><strong>* Lưu &yacute; chuẩn bị:</strong></em></span></p>\r\n\r\n<ol>\r\n	<li>H&igrave;nh thẻ 3x4 chụp kh&ocirc;ng qu&aacute; 6 th&aacute;ng gần nhất.</li>\r\n	<li>Đối với th&iacute; sinh dưới 17 tuổi chưa c&oacute; giấy tờ t&ugrave;y th&acirc;n c&oacute; ảnh khi tham gia c&aacute;c kỳ thi KET/PET/FCE cần bổ sung thẻ học sinh của trường học hoặc thẻ bảo hiểm y tế.</li>\r\n</ol>\r\n\r\n<p><em><span style=\"color:#B22222\"><strong>* Lệ ph&iacute; thi v&agrave; ph&iacute; &ocirc;n luyện</strong></span></em>&nbsp;được hỗ trợ kh&aacute;c nhau học vi&ecirc;n Trung t&acirc;m v&agrave; học vi&ecirc;n chưa tham gia kh&oacute;a học Trung t&acirc;m. Vui l&ograve;ng li&ecirc;n hệ trực tiếp văn ph&ograve;ng được th&ocirc;ng tin chi tiết.</p>\r\n\r\n<p><span style=\"color:#B22222\"><span style=\"font-size:18px\"><strong><em>ANH NGỮ QUỐC TẾ &Acirc;U VIỆT &Uacute;C</em></strong></span></span></p>\r\n', 'tin-tuc/thi%20Cambridge.png', 1, 2, '2020-06-24 07:42:23', '2020-06-24 07:42:23'),
(88, 'MỪNG KHAI TRƯƠNG TRUNG TÂM ANH NGỮ ÂU VIỆT ÚC THỨ 3', 'mung-khai-truong-trung-tam-anh-ngu-au-viet-uc-thu-3', 'Hòa chung niềm vui cùng chào đón thành viên mới của hệ thống Đào tạo Anh ngữ Quốc Tế EVAS...', '<p><img alt=\"\" src=\"/upload/images/tin-tuc/Banner%2028x6.jpg\" style=\"width:400px\" /></p>\r\n\r\n<p><span style=\"font-size:14px\">C&aacute;c hoạt động diễn ra Duy nhất ng&agrave;y khai trương<em><strong> Chủ Nhật 28/6/2020</strong></em>.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đăng k&yacute; tham gia miễn ph&iacute; ng&agrave;y trải nghiệm tiếng Anh SI&Ecirc;U TH&Uacute; VỊ v&agrave; nhận lấy ƯU Đ&Atilde;I SI&Ecirc;U HẤP DẪN trong sự kiện lớn n&agrave;y:</span></p>\r\n\r\n<ul style=\"list-style-type:circle\">\r\n	<li><span style=\"font-size:14px\"><span style=\"color:#000080\"><strong><em>D&agrave;nh cho phụ huynh:</em></strong>&nbsp;</span>Kh&aacute;ch mời tham dự MAY MẮN <strong>học bổng 50%</strong> học ph&iacute;;</span></li>\r\n	<li><span style=\"font-size:14px\"><span style=\"color:#000080\"><em><strong>D&agrave;nh học sinh, sinh vi&ecirc;n:</strong></em>&nbsp;</span>Trải nghiệm lớp học tiếng Anh CHẤT LƯỢNG CAO.</span></li>\r\n	<li><span style=\"font-size:14px\"><span style=\"color:#000080\"><em><strong>D&agrave;nh cho c&aacute;c b&eacute; thiếu nhi:</strong></em></span>&nbsp;Hoạt động ph&aacute;t triển kỹ năng tư duy s&aacute;ng tạo, kh&aacute;m ph&aacute; trở th&agrave;nh Nh&agrave; Nh&agrave; Khoa học, Phi h&agrave;nh gia, Nghi&ecirc;n cứu biển, Họa sỹ, v&agrave; trải nghiệm h&agrave;ng trăm truyện tranh tiếng Anh.</span></li>\r\n</ul>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:14px\">&nbsp;<em><strong>Giảm NGAY 2,000,000vnd</strong></em> học ph&iacute; kh&oacute;a học;</span></li>\r\n	<li><span style=\"font-size:14px\">&nbsp;G&oacute;i <em><strong>Qu&agrave; tặng MAY MẮN</strong></em>: balo thời trang, &aacute;o ph&ocirc;ng năng động, sổ tay tiện &iacute;ch v&agrave; truyện tranh điện tử tiếng Anh;</span></li>\r\n	<li><span style=\"font-size:14px\">&nbsp;<em><strong>MIỄN PH&Iacute;</strong></em> Kiểm tra tr&igrave;nh độ tiếng Anh v&agrave; tư vấn lộ tr&igrave;nh học ph&ugrave; hợp;</span></li>\r\n	<li><span style=\"font-size:14px\">&nbsp;Đặc biệt: Tặng <strong><em>20 bộ truyện tranh</em></strong> cho 20 phụ huynh v&agrave; b&eacute; đến tham dự sự kiện.</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:14px\">KH&Ocirc;NG LỠ CƠ HỘI ưu đ&atilde;i hấp dẫn, trải nghiệm v&agrave; học tiếng Anh hiệu quả c&ugrave;ng EVAS...</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"color:#B22222\"><strong><em>ANH NGỮ QUỐC TẾ &Acirc;U VIỆT &Uacute;C</em></strong></span></span></p>\r\n', 'tin-tuc/Banner%2028x6.jpg', 1, 2, '2020-06-24 12:14:26', '2020-06-24 12:14:26');
INSERT INTO `news_items` (`id`, `title`, `code`, `description`, `detail`, `image`, `status`, `sort`, `created_at`, `updated`) VALUES
(89, 'KHUNG GIỜ VÀNG - ƯU ĐÃI HỌC PHÍ DUY NHẤT THÁNG HÈ 2020', 'khung-gio-vang-uu-dai-hoc-phi-duy-nhat-thang-he-2020', 'Những ưu đãi duy nhất dành cho các cấp độ Anh văn thiếu nhi, thiếu niên và trung học cơ sở...', '<p><img alt=\"\" src=\"/upload/images/Online%20course/Golden%20hours%201.jpg\" style=\"width:330px\" /></p>\r\n\r\n<p>Nếu học ph&iacute; l&agrave; r&agrave;o cản cho c&aacute;c em học sinh chưa thể tham gia lớp tiếng Anh chất lượng cao tại EVAS, th&igrave; kh&oacute;a học KHUNG GIỜ V&Agrave;NG sẽ hỗ trợ phụ huynh học vi&ecirc;n học ph&iacute; l&ecirc;n đến <span style=\"color:#B22222\"><strong>50%</strong></span></p>\r\n\r\n<p><em><strong>V&agrave; sỉ số lớp học hiệu quả chỉ 6 -12 bạn học vi&ecirc;n cho một lớp gi&uacute;p hiệu quả học tập cao nhất.</strong></em></p>\r\n\r\n<p>Khi đăng k&yacute; trọn kh&oacute;a c&aacute;c kh&oacute;a học d&agrave;nh cho cấp độ Thiếu Nhi, Thiếu Ni&ecirc;n v&agrave;o khung giờ từ 7:30 - 9:00 c&aacute;c ng&agrave;y trong tuần thứ 3 5 7 sẽ nhận được:</p>\r\n\r\n<ul>\r\n	<li>Ưu đ&atilde;i học ph&iacute; l&ecirc;n đến 50% cho th&aacute;ng học đầu ti&ecirc;n.</li>\r\n	<li>Bộ Gi&aacute;o tr&igrave;nh đầu ti&ecirc;n.</li>\r\n	<li>Bộ phần qu&agrave; balo - &aacute;o đồng phục EVAS.</li>\r\n</ul>\r\n\r\n<p>V&agrave; sỉ số lớp học hiệu quả từ 6-12 học vi&ecirc;n/ một lớp gi&uacute;p n&acirc;ng cao hiệu quả học tập của c&aacute;c bạn.</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"color:#B22222\"><strong><em>ANH NGỮ QUỐC TẾ &Acirc;U VIỆT &Uacute;C</em></strong></span></span></p>\r\n', 'Online%20course/Golden%20hours%201.jpg', 1, 2, '2020-07-01 04:38:10', '2020-07-01 04:38:10'),
(90, 'SIÊU TỐC TIẾNG ANH MÙA HÈ 2020', 'sieu-toc-tieng-anh-mua-he-2020', 'Một mùa hè chất lượng tại Âu Việt Úc...', '<p>Th&ocirc;ng b&aacute;o qu&yacute; phụ huynh, c&aacute;c em học vi&ecirc;n th&acirc;n mến, c&aacute;c kh&oacute;a học h&egrave; 2020 tại EVAS đ&atilde; sẵn s&agrave;ng v&agrave; tổng khai giảng trong th&aacute;ng 7 n&agrave;y. Lớp học đa dạng, giờ học linh hoạt c&aacute;c buổi s&aacute;ng, chiều, tối. Xe đưa rước cho nh&oacute;m học vi&ecirc;n.</p>\r\n\r\n<p>Đa dạng tổng khai giảng c&aacute;c lớp h&egrave;:</p>\r\n\r\n<ul>\r\n	<li>&nbsp;Lớp mẫu gi&aacute;o 4.0-6.0 tuổi;</li>\r\n	<li>&nbsp;Lớp thiếu nhi chuẩn quốc tế Cambridge cho em học sinh tiểu học;</li>\r\n	<li>&nbsp;Lớp thiếu ni&ecirc;n bậc A2-B1-B2 c&aacute;c em học sinh THCS, THPT;</li>\r\n	<li>&nbsp;Lớp giao tiếp cấp tốc 100% Gi&aacute;o vi&ecirc;n bản ngữ;</li>\r\n	<li>&nbsp;Lớp lấy chứng chỉ quốc tế IELTS &ndash; TOEIC;</li>\r\n</ul>\r\n\r\n<p>Đến kh&oacute;a h&egrave; tại EVAS, chắc chắn c&aacute;c em sẽ sử dụng một m&ugrave;a h&egrave; hiệu quả tuyết đối, kh&ocirc;ng chỉ l&agrave; khả năng tiếng Anh giao tiếp mỗi ng&agrave;y trong m&ocirc;i trường <span style=\"color:#FF0000\"><span style=\"font-size:16px\"><strong><span style=\"font-family:comic sans ms,cursive\">100% tiếng Anh</span></strong></span></span> với rất nhiều hoạt động hấp dẫn, trải nghiệm th&uacute; vị, c&acirc;n bằng giữa học v&agrave; chơi sẵn s&agrave;ng ch&agrave;o đ&oacute;n c&aacute;c học vi&ecirc;n c&aacute;c hoạt động vui h&egrave; s&ocirc;i động, hội trại h&egrave;, du lịch h&egrave;, d&atilde; ngoại,&nbsp;&hellip; gi&uacute;p c&aacute;c em đam m&ecirc; m&ocirc;n học n&agrave;y v&agrave; khai ph&oacute;ng t&agrave;i năng của c&aacute;c con qua kỹ năng: thuyết tr&igrave;nh, hoạt động nh&oacute;m, s&aacute;ng tạo v&agrave; tư duy phản biện&hellip; V&agrave; kết quả sẽ l&agrave; mong đợi của từng phụ huynh v&agrave; mục ti&ecirc;u của nhiều bạn muốn tăng tốc tiếng Anh vững v&agrave;ng kiến thức cho kế hoạch sẵn s&agrave;ng ph&iacute;a trước.</p>\r\n\r\n<p>Đặc biệt, khi đăng k&yacute; tham gia sớm, học vi&ecirc;n c&ograve;n được nhận bộ qu&agrave; tặng độc quyền v&ocirc; c&ugrave;ng đ&aacute;ng y&ecirc;u từ EVAS. ️</p>\r\n\r\n<p>Đăng k&yacute; h&ocirc;m nay để c&oacute; m&ugrave;a h&egrave; tiếng Anh chất lượng c&aacute;c bạn nh&eacute;!!!</p>\r\n\r\n<p>EVAS hỗ trợ xe đưa rước học vi&ecirc;n ở ngo&agrave;i khu vực TP. Long Xuy&ecirc;n.</p>\r\n\r\n<p><em><strong><span style=\"color:#B22222\"><span style=\"font-size:16px\">ANH NGỮ QUỐC TẾ &Acirc;U VIỆT &Uacute;C</span></span></strong></em></p>\r\n', 'du-hoc/Summer%20course.png', 1, 2, '2020-07-18 04:45:23', '2020-07-18 04:45:23'),
(91, 'TƯƠNG LAI RỘNG MỞ VỚI TẤM BẰNG IELTS', 'tuong-lai-rong-mo-voi-tam-bang-ielts', 'Định hướng và vẽ bức tranh tương lai tươi sáng sẽ không bao giờ là quá sớm, chúng ta có thể làm được ngay bây giờ khi còn ngồi ghế nhà trường...', '<p><em>Định hướng v&agrave; vẽ bức tranh tương lai tươi s&aacute;ng sẽ kh&ocirc;ng bao giờ l&agrave; qu&aacute; sớm. Ch&uacute;ng m&igrave;nh c&oacute; thể l&agrave;m được ngay b&acirc;y giờ khi c&ograve;n ngồi ghế nh&agrave; trường đấy! C&aacute;c em biết kh&ocirc;ng sở hữu chứng chỉ IELTS, đ&atilde; minh chứng cho những năm th&aacute;ng học tập chăm chỉ v&agrave; khả năng &ldquo;ngầu&rdquo; tiếng Anh của m&igrave;nh để sẵn s&agrave;ng face challenges head-on đấy!</em></p>\r\n\r\n<p><strong>C&ugrave;ng EVAS liệt k&ecirc; quyền lực khi sở hữu tấm bằng IELTS n&agrave;o:</strong></p>\r\n\r\n<p><span style=\"color:#B22222\"><strong><em>Tối ưu kết quả học tiếng Anh tr&ecirc;n lớp:</em></strong></span><br />\r\nChắc chắn vượt qua kỳ thi c&ugrave;ng số điểm tr&ograve;n trịa. V&igrave; với kiến thức chuy&ecirc;n s&acirc;u 4 kỹ năng Nghe - N&oacute;i - Đọc - Viết từ chương tr&igrave;nh học IELTS sẽ khiến những b&agrave;i học Tiếng Anh tr&ecirc;n trường trở n&ecirc;n dễ d&agrave;ng hơn với c&aacute;c em.<br />\r\n<br />\r\n<em><strong><span style=\"color:#B22222\">Miễn thi m&ocirc;n tiếng Anh tốt nghiệp THPT</span></strong></em><br />\r\nSở hữu chứng chỉ IELTS 4.0 trở l&ecirc;n sẽ được miễn thi m&ocirc;n tiếng Anh tương ứng với điểm 10 trong kỳ thi THPT quốc gia. V&igrave; vậy, việc học v&agrave; thi IELTS từ sớm sẽ cho c&aacute;c em th&ecirc;m nhiều thời gian hơn để tập trung &ocirc;n tập c&aacute;c m&ocirc;n kh&aacute;c.<br />\r\n<br />\r\n<span style=\"color:#B22222\"><strong><em>Cơ hội theo học c&aacute;c trường THPT chuy&ecirc;n; Đại học y&ecirc;u th&iacute;ch.</em></strong></span><br />\r\nKiến thức chuy&ecirc;n s&acirc;u kỹ năng Nghe-N&oacute;i-Đọc- Viết, sẽ gi&uacute;p c&aacute;c em ho&agrave;n to&agrave;n tự tin th&agrave;nh c&ocirc;ng b&agrave;i thi tuyển lọc ở chuyển cấp. V&agrave; đặc biệt, c&aacute;c em chuẩn bị bước tiếp v&agrave;o giảng đường Đại học, th&igrave; chứng chỉ IELTS sẽ l&agrave; trợ thủ đắc lực trong h&agrave;nh trang của em nh&eacute;, v&igrave; nhiều trường Đại học Việt Nam cũng đ&atilde; quy định x&eacute;t tuyển đầu v&agrave;o, v&agrave; y&ecirc;u cầu chuẩn đầu ra với khả năng tiếng Anh l&agrave; chứng chỉ IELTS đấy.<br />\r\n<br />\r\n<span style=\"color:#B22222\"><strong><em>Săn học bổng du học</em></strong></span><br />\r\nAi cũng biết để đi du học điều kiện đầu ti&ecirc;n của hồ sơ nhập học l&agrave; khả năng tiếng Anh, chứng chỉ IELTS l&agrave; tấm v&eacute; th&ocirc;ng h&agrave;nh bắt buộc nếu em muốn du học tại một số quốc gia như Anh, Mỹ, &Uacute;c, New zealand, Canada, &hellip; Hơn thế nữa, sở hữu chứng chỉ IELTS cao, EVAS sẽ đồng h&agrave;nh c&ugrave;ng em chinh phục c&aacute;c học bổng du học đấy!<br />\r\n<br />\r\n<span style=\"color:#B22222\"><strong><em>Cơ hội việc l&agrave;m chuy&ecirc;n nghiệp</em></strong></span><br />\r\nC&aacute;c em tin kh&ocirc;ng, rất nhiều em ho&agrave;n th&agrave;nh chương tr&igrave;nh học IELTS ở EVAS, bước tiếp v&agrave;o trường Đại Học, tự tin khả năng tiếng Anh của m&igrave;nh, c&aacute;c em c&oacute; thể kiếm th&ecirc;m thu nhập h&agrave;ng th&aacute;ng bằng việc l&agrave;m gi&aacute;o vi&ecirc;n hoặc trợ giảng ở c&aacute;c trung t&acirc;m tiếng Anh đấy. Nếu c&aacute;c em kh&ocirc;ng nghĩ m&igrave;nh b&eacute;n duy&ecirc;n với nghề gi&aacute;o th&igrave; h&atilde;y thử những lĩnh vực c&aacute;c em đam m&ecirc; v&igrave; tiếng Anh giỏi th&igrave; l&agrave;m g&igrave; cũng dễ m&agrave;. Quan trọng l&agrave; sống v&agrave; học tập những th&aacute;ng năm ở Đại học thật trải nghiệm, độc lập v&agrave; tự chủ.<br />\r\n--------------------</p>\r\n\r\n<p><br />\r\n<span style=\"color:#B22222\"><strong><em>TỔNG KHAI GIẢNG IELTS CAM KẾT ĐẦU RA 4.5 - 6.5+</em></strong></span><br />\r\nIELTS 4.5 14/09/2020<br />\r\nIELTS 5.0 15/09/2020<br />\r\nIELTS 5.5 16/09/2020<br />\r\nIELTS 6.0+ 16/09/2020</p>\r\n\r\n<p><br />\r\nĐể cam kết lớp học đ&uacute;ng khả năng, đ&uacute;ng điểm đầu ra, c&aacute;c em vui l&ograve;ng li&ecirc;n hệ văn ph&ograve;ng để thực hiện b&agrave;i đ&aacute;nh gi&aacute; đầu v&agrave;o nh&eacute;. EVAS h&acirc;n hạnh đồng h&agrave;nh c&aacute;c em chinh phục IELTS kết quả cao!</p>\r\n\r\n<p><strong><em><span style=\"font-size:16px\"><span style=\"color:#B22222\">ANH NGỮ QUỐC TẾ &Acirc;U VIỆT &Uacute;C</span></span></em></strong></p>\r\n', 'Newone/IELTS.png', 1, 1, '2020-09-08 03:00:04', '2020-09-08 03:00:04'),
(92, 'BE A STAR 2020', 'be-a-star-2020', 'Tại EVAS, chúng tôi luôn tin tưởng vào tiềm năng của từng em học viên, sẽ tỏa sáng trong tương lai và thay đổi thế giới bằng tài năng và tầm nhìn của mình...', '<p><img alt=\"\" src=\"/upload/images/Newone/Be%20a%20star%20poster.jpg\" style=\"height:475px; width:336px\" /></p>\r\n\r\n<p><span style=\"color:#B22222\"><em><strong>What is the contest all about?</strong></em></span><br />\r\nAt EVAS, we strongly believe in the potential of individuals who will be bright in the future and change the world with their talent and vision. Let&rsquo;s maximize their hidden talents by providing them opportunities and being our STAR in 2020!<br />\r\n<br />\r\n<span style=\"color:#B22222\"><em><strong>Deadline and result announcement:</strong></em></span><br />\r\n- Submit the video by August 31st, 2020 via email: auvietucBEASTAR@gmail.com<br />\r\n- The winner will be announced on the morning of September 7th, 2020 at EVAS campuses, website and fanpage.</p>\r\n\r\n<p>-----------------------------------</p>\r\n\r\n<p><span style=\"color:#B22222\"><em><strong>Mục đ&iacute;ch tổ chức cuộc thi:</strong></em></span><br />\r\nTại EVAS, ch&uacute;ng t&ocirc;i lu&ocirc;n tin tưởng v&agrave;o tiềm năng của từng em học vi&ecirc;n, sẽ tỏa s&aacute;ng trong tương lai v&agrave; thay đổi thế giới bằng t&agrave;i năng v&agrave; tầm nh&igrave;n của m&igrave;nh. Để tối đa h&oacute;a t&agrave;i năng tiềm ẩn, EVAS tổ chức cuộc thi cho c&aacute;c em cơ hội v&agrave; trở th&agrave;nh NG&Ocirc;I SAO EVAS năm 2020!<br />\r\n<br />\r\n<span style=\"color:#B22222\"><strong><em>Thời gian dự thi v&agrave; c&ocirc;ng bố kết quả:</em></strong></span><br />\r\n- Hạn ch&oacute;t đăng k&yacute;: 23 giờ 59 ph&uacute;t ng&agrave;y 31/8/2020 qua email: auvietucBEASTAR@gmail.com.<br />\r\n- Kết quả được c&ocirc;ng bố: s&aacute;ng ng&agrave;y 07/9/2020 tr&ecirc;n tất cả cơ sở, website v&agrave; fanpage EVAS.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Newone/Be%20a%20star%20poster.jpg', 1, 2, '2020-09-08 03:10:40', '2020-09-08 03:10:40'),
(93, 'CÔNG BỐ KẾT QUẢ CUỘC THI BE A STAR 2020', 'cong-bo-ket-qua-cuoc-thi-be-a-star-2020', 'Tại Âu Việt Úc, chúng tôi luôn đăt niềm tin vào tiềm năng của từng em học viên, sẽ tỏa sáng và thay đổi cả thế giới bằng tài năng và tâm nhìn của chính mình...', '<p><span style=\"color:#B22222\"><em><strong>NG&Ocirc;I SAO EVAS 2020</strong></em></span></p>\r\n\r\n<p>At EVAS, we strongly believe in the potential of individuals who will be bright in the future and change the world with their talent and vision. We are so proud that we have found a lot of talents in many fields of arts, sports and technology after the Be a Star 2020.</p>\r\n\r\n<p>And now, we would like to congratulate Tina-Ngo Nhut Bao Chau who has just become the Star of EVAS in 2020 with her excellent singing performance.</p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/tin-tuc/award-trophy.png\" style=\"height:333px; width:500px\" /></p>\r\n\r\n<p>EVAS hope that you will keep this passion to pursuit your dream.</p>\r\n\r\n<p><strong><em>&quot;THINK BIG, DREAM BIG, BELIEVE BIG AND THE RESULTS WILL BE BIG&quot;</em></strong></p>\r\n\r\n<p>_________________</p>\r\n\r\n<p>Tại &Acirc;u Việt &Uacute;c, ch&uacute;ng t&ocirc;i lu&ocirc;n đăt niềm tin v&agrave;o tiềm năng của từng em học vi&ecirc;n, sẽ tỏa s&aacute;ng v&agrave; thay đổi cả thế giới bằng t&agrave;i năng v&agrave; t&acirc;m nh&igrave;n của ch&iacute;nh m&igrave;nh. Sau cuộc thi BE A STAR 2020, ch&uacute;ng t&ocirc;i tự h&agrave;o đ&atilde; t&igrave;m được rất nhiều t&agrave;i năng trong nhiều lĩnh vực như nghệ thuật, thể thao v&agrave; c&ocirc;ng nghệ. V&agrave; việc t&igrave;m ra một t&agrave;i năng nổi bật nhất trong tất cả c&aacute;c t&agrave;i năng m&agrave; c&aacute;c bạn đ&atilde; thể hiện, kh&ocirc;ng phải l&agrave; một điều dễ d&agrave;ng.</p>\r\n\r\n<p>V&agrave; b&acirc;y giờ, ch&uacute;ng t&ocirc;i v&ocirc; c&ugrave;ng tự h&agrave;o c&ocirc;ng bố NG&Ocirc;I SAO T&Agrave;I NĂNG 2020 thuộc về bạn Tina - NG&Ocirc; NHỰT BẢO CH&Acirc;U (KET 24) đ&atilde; xuất sắc trong phần thể hiện t&agrave;i năng ca h&aacute;t.</p>\r\n\r\n<p>Thầy c&ocirc; EVAS hy vọng c&aacute;c con sẽ lu&ocirc;n giữ được ngọn lửa đam m&ecirc; n&agrave;y để tiếp tục ước mơ của ch&iacute;nh m&igrave;nh...</p>\r\n\r\n<p><strong><em>&quot;THINK BIG, DREAM BIG, BELIEVE BIG AND THE RESULTS WILL BE BIG&quot;</em></strong></p>\r\n\r\n<p><span style=\"color:#B22222\"><em><strong>KHOẢNH KHẮC Đ&Aacute;NG Y&Ecirc;U C&Ugrave;NG BE A STAR MEETING</strong></em></span></p>\r\n\r\n<p>Buổi họp mặt trước ng&agrave;y c&ocirc;ng bố kết quả Ng&ocirc;i Sao EVAS 2020 diễn ra v&agrave;o ng&agrave;y 6/9/2020, đ&atilde; mang lại kh&ocirc;ng những những tiếng cười, những điểm nhấn v&agrave; cả sự c&ocirc;ng nhận với những t&agrave;i năng m&agrave; c&aacute;c con đ&atilde; thể hiện.</p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/tin-tuc/exif_temp_image%20(1).jpg\" style=\"height:375px; width:500px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/tin-tuc/exif_temp_image%20(13).jpg\" style=\"height:375px; width:500px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/tin-tuc/exif_temp_image%20(7).jpg\" style=\"height:667px; width:500px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/tin-tuc/exif_temp_image%20(15).jpg\" style=\"height:375px; width:500px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/tin-tuc/exif_temp_image%20(5).jpg\" style=\"height:667px; width:500px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/tin-tuc/exif_temp_image%20(16).jpg\" style=\"height:270px; width:500px\" /></p>\r\n\r\n<p>C&aacute;c bạn đ&atilde; c&ugrave;ng nhau xem lại những khoảnh khắc đặc sắc trong từng video v&agrave; đ&oacute; thật l&agrave; những khoảnh khắc đ&aacute;ng y&ecirc;u, kh&ocirc;ng những được đầu tư về nội dung, c&aacute;ch quay m&agrave; cả về diễn suất v&agrave; kỹ sảo, &acirc;m nhạc được lồng gh&eacute;p trong video. Đ&acirc;y quả l&agrave; những kỷ niệm đ&aacute;ng nhớ kh&ocirc;ng chỉ đối với cuộc thi, m&agrave; l&agrave; điểm nhấn trong khoảng thời gian tuổi thơ, c&ugrave;ng gia đ&igrave;nh c&ugrave;ng thầy c&ocirc;,&nbsp;c&aacute;c bạn vẫn c&ograve;n tr&ecirc;n ghế nh&agrave; trường EVAS.</p>\r\n\r\n<p>V&agrave; việc thể hiện ch&iacute;nh bản th&acirc;n m&igrave;nh trước ống k&iacute;nh, cũng đ&atilde; l&agrave; th&agrave;nh c&ocirc;ng d&agrave;nh cho ch&iacute;nh bản th&acirc;n c&aacute;c con rồi, h&atilde;y lu&ocirc;n tin tưởng v&agrave;o ch&iacute;nh m&igrave;nh, th&agrave;nh c&ocirc;ng sẽ lu&ocirc;n b&ecirc;n c&aacute;c con nh&eacute;!</p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#B22222\"><em><strong>ANH NGỮ QUỐC TẾ &Acirc;U VIỆT &Uacute;C</strong></em></span></span></p>\r\n', 'tin-tuc/award-trophy.png', 1, 2, '2020-09-08 04:13:58', '2020-09-08 04:13:58'),
(94, 'LIGHT UP THE MOON - CUỘC THI LÀM LỒNG ĐÈN TRUNG THU', 'light-up-the-moon-cuoc-thi-lam-long-den-trung-thu', 'Nhằm giúp các con thể hiện sự sáng tạo và khéo léo của mình qua những chiếc lồng đèn dự thi, tự tin thuyết trình về tác phẩm mình thiết kế bằng tiếng Anh, cùng với những trải nghiệm thiết kế lồng đèn qua vật tư thủ công, giúp các con thêm kiến thức về Tết Trung thu của thế hệ trước...', '<p>LIGHT UP THE MOON - CUỘC THI L&Agrave;M LỒNG Đ&Egrave;N TRUNG THU</p>\r\n\r\n<p>=========</p>\r\n\r\n<p>C&ugrave;ng t&acirc;m huyết đồng h&agrave;nh với c&aacute;c con chinh phục tiếng Anh th&agrave;nh c&ocirc;ng, EVAS kh&ocirc;ng ngừng tạo ra m&ocirc;i trường học năng động v&agrave; s&aacute;ng tạo, để tối đa h&oacute;a t&agrave;i năng v&agrave; khả năng ng&ocirc;n ngữ của con. Nh&acirc;n dịp Tết Trung Thu 2020 với chủ đề THẮP S&Aacute;NG &Aacute;NH TRĂNG, EVAS ph&aacute;t động cu&ocirc;c thi l&agrave;m LỒNG Đ&Egrave;N TRUNG THU, nhằm gi&uacute;p c&aacute;c con thể hiện sự s&aacute;ng tạo v&agrave; kh&eacute;o l&eacute;o của m&igrave;nh qua những chiếc lồng đ&egrave;n dự thi, tự tin thuyết tr&igrave;nh về t&aacute;c phẩm m&igrave;nh thiết kế bằng tiếng Anh, c&ugrave;ng với những trải nghiệm thiết kế lồng đ&egrave;n qua vật tư thủ c&ocirc;ng, gi&uacute;p c&aacute;c con th&ecirc;m kiến thức về Tết Trung thu của thế hệ trước.</p>\r\n\r\n<p><span style=\"color:#A52A2A\"><strong><em>Cơ cấu Giải thưởng</em></strong></span></p>\r\n\r\n<ul>\r\n	<li>Giải nhất t&aacute;c phẩm dự thi CS 1: Giỏ qu&agrave; b&aacute;nh;</li>\r\n	<li>Giải nhất t&aacute;c phẩm dự thi CS 2: Giỏ qu&agrave; b&aacute;nh;</li>\r\n	<li>Giải nhất t&aacute;c phẩm dự thi CS 3: Giỏ qu&agrave; b&aacute;nh;</li>\r\n	<li>Giấy khen c&ocirc;ng nhận s&aacute;ng tạo v&agrave; kh&eacute;o l&eacute;o;</li>\r\n	<li>Trưng b&agrave;y v&agrave; giới thiệu tại tất cả cơ sở, tr&ecirc;n trang website.</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#A52A2A\"><strong><em>Thời gian dự thi v&agrave; c&ocirc;ng bố kết quả:</em></strong></span></p>\r\n\r\n<ul>\r\n	<li>Địa điểm nộp lồng đ&egrave;n dự thi: tại cơ sở đang theo học.</li>\r\n	<li>Hạn ch&oacute;t nộp lồng đ&egrave;n: 9:00pm ng&agrave;y 29/9/2020 tại c&aacute;c cơ sở.</li>\r\n	<li>Kết quả được c&ocirc;ng bố: ng&agrave;y 01/10/2020 (15/08/2020 al) tr&ecirc;n tất cả cơ sở, website v&agrave; fanpage EVAS.</li>\r\n</ul>\r\n\r\n<p>We EVAS have devotedly accompanied our beloved students on the journey getting fluent in English. In fact, we constantly create a learning and creative environment with the purpose of maximizing their talent and language skills. The Lantern Making Competition is introduced on the occasion of the Mid-Autumn Festival 2020 with the theme: LIGHT UP THE MOON, offering our students opportunities to stimulate their creativity and crafting skills through handcrafted lanterns and learn the Vietnamese tradition in the previous generation.</p>\r\n\r\n<p><strong><em><span style=\"color:#A52A2A\">Prizes</span></em></strong></p>\r\n\r\n<ul>\r\n	<li>The winner of campus 1: a basket of snacks and lollipop</li>\r\n	<li>The winner of campus 2: a basket of snacks and lollipop</li>\r\n	<li>The winner of campus 3: a basket of snacks and lollipop</li>\r\n	<li>Certificate of Achievement</li>\r\n	<li>Display the work at the campus;</li>\r\n	<li>Publish the winner on EVAS fanpage and website.</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#B22222\"><strong><em>Deadline and result announcement</em></strong></span></p>\r\n\r\n<ul>\r\n	<li>Hand in the lantern by 29th September 2020 at your Campus.</li>\r\n	<li>All lanterns will be used to decorate around the office by a day after submission.</li>\r\n	<li>The winner will be announced by 1st October, 2020 at EVAS campuses, website and fanpage.</li>\r\n</ul>\r\n\r\n<p><strong><em><span style=\"color:#B22222\"><span style=\"font-size:16px\">ANH NGỮ QUỐC TẾ &Acirc;U VIỆT &Uacute;C</span></span></em></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Online%20course/Mid%20autumn%20poster%20Campus%201%202.jpg', 1, 2, '2020-09-27 10:51:10', '2020-09-27 10:51:10'),
(95, 'NỀN TẢNG TIẾNG ANH CHO TÂN SINH VIÊN VÀ NGƯỜI MẤT GỐC', 'nen-tang-tieng-anh-cho-tan-sinh-vien-va-nguoi-mat-goc', 'Dành cho những bạn tân sinh viên, người mất gốc và những bạn mới bắt đầu tiếp cận tiếng Anh...', '<p><strong><em>Hiểu được b&ecirc;n cạnh những kh&oacute; khăn, sự hoang man bước đầu m&agrave; bất kỳ c&aacute;c bạn t&acirc;n sinh vi&ecirc;n v&agrave; c&aacute;c bạn mất gốc hay bắt đầu tiếp cận với tiếng Anh đều gặp phải, vấn đề chi ph&iacute; sẽ lu&ocirc;n l&agrave; ti&ecirc;u ch&iacute; quan t&acirc;m lớn đối với c&aacute;c bạn, EVAS t&acirc;m huyết x&acirc;y dựng lộ tr&igrave;nh v&agrave; lu&ocirc;n cải tiến phương ph&aacute;p học gi&uacute;p c&aacute;c bạn mới bắt đầu c&oacute; thể tiếp cận tốt v&agrave; l&acirc;u d&agrave;i hơn l&agrave; chinh phục con đường &ldquo;giao tiếp lưu lo&aacute;t cơ bản bằng tiếng Anh&rdquo; với mức học ph&iacute; dễ chịu - m&agrave; vẫn đảm bảo chất lượng đầu ra.</em></strong></p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/Newone/AVGT%20OK.jpg\" style=\"height:389px; width:600px\" /></p>\r\n\r\n<p>Chương tr&igrave;nh <em><strong>&ldquo;Nền tảng tiếng Anh cho t&acirc;n sinh vi&ecirc;n v&agrave; người mất gốc&rdquo;</strong></em>:</p>\r\n\r\n<ul>\r\n	<li><strong><span style=\"color:#B22222\">Kh&oacute;a Song song GVVN-GVNN v&agrave; kh&oacute;a 100% GVNN</span></strong></li>\r\n	<li><strong><span style=\"color:#B22222\">Hỗ trợ học ph&iacute; l&ecirc;n đến 30%**</span></strong></li>\r\n	<li><strong><span style=\"color:#B22222\">Sỉ số &iacute;t 10 -15: đảm bảo chất lượng học</span></strong></li>\r\n	<li><strong><span style=\"color:#B22222\">Chủ đề n&oacute;i xuy&ecirc;n suốt, gi&uacute;p cải thiện 60% khả năng chỉ trong 4 th&aacute;ng học.</span></strong></li>\r\n</ul>\r\n\r\n<p>**Với c&aacute;c bạn đăng k&yacute; theo nh&oacute;m từ 5 học vi&ecirc;n sẽ được ưu đ&atilde;i học ph&iacute; được hỗ trợ cho nh&oacute;m l&ecirc;n đến 30% cho mỗi cấp độ học.</p>\r\n\r\n<p>&nbsp; Tham gia c&aacute;c kh&oacute;a học tiếng Anh l&agrave; bạn đang tự nguyện đưa m&igrave;nh v&agrave;o khu&ocirc;n khổ tiếng Anh c&oacute; &iacute;ch. Những lợi &iacute;ch m&agrave; bạn nhận được gấp nhiều lần chi ph&iacute; m&agrave; bạn bỏ ra cho mỗi kh&oacute;a học như thế. Chẳng những vậy, những nh&acirc;n vi&ecirc;n v&agrave; gi&aacute;o vi&ecirc;n đầy t&acirc;m huyết của EVAS lu&ocirc;n cố gắng để gi&uacute;p học vi&ecirc;n ở mọi mặt, từ kiến thức đến việc lu&ocirc;n giữ vững được ngọn lửa quyết t&acirc;m cho học vi&ecirc;n.</p>\r\n\r\n<p><em>C&aacute;c kh&oacute;a học tiếng Anh giao tiếp tại EVAS cam kết sẽ mang lại cho bạn những giờ học vui vẻ, thoải m&aacute;i để gi&uacute;p bạn c&oacute; thể tiếp thu một c&aacute;ch thật tốt v&agrave; bạn sẽ thấy th&agrave;nh quả tiếng Anh của m&igrave;nh cải thiện r&otilde; rệt sau những kh&oacute;a học tại trung t&acirc;m.</em></p>\r\n\r\n<p><span style=\"color:#B22222\"><span style=\"font-size:16px\"><em><strong>ANH NGỮ QUỐC TẾ &Acirc;U VIỆT &Uacute;C</strong></em></span></span></p>\r\n', 'Newone/AVGT%20OK.jpg', 1, 2, '2020-10-27 01:32:03', '2020-10-27 01:32:03'),
(96, 'ĐÊM SỰ KIỆN HALLOWEEN ĐẶC BIỆT TẠI EVAS', 'dem-su-kien-halloween-dac-biet-tai-evas', 'Với mục đích cho các em EVAS được trải nghiệm phong tục phương Tây và hòa vào không khí đúng nghĩa ngày Halloween, EVAS tổ chức các hoạt động vui nhộn cơ hội các em tích lũy tiếng Anh về lễ hội...', '<p><strong><em>Halloween (hay c&ograve;n gọi l&agrave; lễ hội H&oacute;a Lộ Quỉ) l&agrave; một lễ hội thường ni&ecirc;n được tổ chức 31/10 hằng năm. Ở c&aacute;c nước phương t&acirc;y, đ&acirc;y được coi như một ng&agrave;y tết, một lễ hội vui nhất trong năm với những tr&ograve; chơi v&ocirc; c&ugrave;ng kỳ b&iacute; v&agrave; hấp dẫn.</em></strong></p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/Newone/Halloweem.png\" style=\"height:803px; width:600px\" /></p>\r\n\r\n<p>Với mục đ&iacute;ch cho c&aacute;c em EVAS được trải nghiệm phong tục phương T&acirc;y v&agrave; h&ograve;a v&agrave;o kh&ocirc;ng kh&iacute; đ&uacute;ng nghĩa ng&agrave;y Halloween, EVAS tổ chức c&aacute;c hoạt động vui nhộn cơ hội c&aacute;c em t&iacute;ch lũy tiếng Anh về lễ hội cũng như văn h&oacute;a phương t&acirc;y, c&ugrave;ng với những &ldquo;treats&rdquo; thầy c&ocirc; EVAS sẽ mang lại cho c&aacute;c em nhiều sự bất ngờ v&agrave; th&uacute; vị nh&eacute;!</p>\r\n\r\n<p>Thời gian tổ chức: <strong>30 - 31/10/2020</strong></p>\r\n\r\n<ol>\r\n	<li><strong><span style=\"color:#0000CD\">T&igrave;m hiểu ng&agrave;y Halloween qua hoạt động nh&oacute;m;</span></strong></li>\r\n	<li><strong><span style=\"color:#0000CD\">Tr&ograve; chơi th&aacute;ch thức, th&uacute; vị phong c&aacute;ch Halloween;</span></strong></li>\r\n	<li><strong><span style=\"color:#0000CD\">Buổi diễn thời gian phong c&aacute;ch Halloween;</span></strong></li>\r\n	<li><strong><span style=\"color:#0000CD\">S&aacute;ng tạo ảnh dự thi c&ugrave;ng thầy c&ocirc; v&agrave; bạn b&egrave;.</span></strong></li>\r\n</ol>\r\n\r\n<p>Shhhhh!!! Nhiều giải thưởng đậm chất Halloween nh&eacute;!</p>\r\n\r\n<p>V&agrave; điều cuối c&ugrave;ng l&agrave; h&atilde;y &ldquo;quẩy&rdquo; hết m&igrave;nh với những hoạt động đặc sắc m&agrave; thầy c&ocirc; đ&atilde; chuẩn bị cho c&aacute;c con nh&eacute;!</p>\r\n\r\n<p><span style=\"color:#B22222\"><em><span style=\"font-size:16px\"><strong>ANH NGỮ QUỐC TẾ &Acirc;U VIỆT &Uacute;C</strong></span></em></span></p>\r\n', 'Newone/Halloweem.png', 1, 2, '2020-10-28 01:42:52', '2020-10-28 01:42:52'),
(97, 'LIGHT UP THE MOON - THẮP SÁNG ÁNH TRĂNG', 'light-up-the-moon-thap-sang-anh-trang', 'Môi trường ở EVAS không đơn thuần chỉ tập trung cho kiến thức Anh ngữ, mà còn là môi trường khơi nguồn cho sự sáng tạo của bé thông qua các hoạt động vào các dịp Lễ của năm...', '<p><em>68 chiếc lồng đ&egrave;n được gửi về 3 cơ sở trong những ng&agrave;y cận Trung Thu, với những b&agrave;i viết mi&ecirc;u tả c&aacute;ch l&agrave;m, hay kể về khoảng thời gian c&aacute;c bạn c&ugrave;ng l&ecirc;n &yacute; tưởng với ba mẹ, hay khoảng thời gian chuẩn bị từ những hộp b&uacute;t m&agrave;u, những mẫu giấy cho buổi l&agrave;m lồng đ&egrave;n c&ugrave;ng bạn c&ugrave;ng thầy c&ocirc; EVAS tại lớp.</em></p>\r\n\r\n<p>Điều c&aacute;c con c&oacute; được kh&ocirc;ng chỉ l&agrave; những chiếc lồng đ&egrave;n th&agrave;nh phẩm do ch&iacute;nh bản th&acirc;n c&aacute;c con tự l&agrave;m ra, m&agrave; l&agrave; những kỷ niệm, những cảm nhận từ những hoạt động s&aacute;ng tạo v&agrave; cả những chia sẻ của ch&iacute;nh ba mẹ, thầy c&ocirc; về Trung Thu của c&aacute;c thế hệ trước.</p>\r\n\r\n<p>Quả l&agrave; những kỷ niệm đ&aacute;ng gi&aacute; đ&uacute;ng kh&ocirc;ng c&aacute;c con? Đ&oacute; cũng l&agrave; &yacute; nghĩa m&agrave; c&aacute;c chương tr&igrave;nh tại EVAS d&agrave;nh cho c&aacute;c con.</p>\r\n\r\n<p>N&agrave;o!!! Thầy c&ocirc; xin gửi lời ch&uacute;c mừng đến chủ nh&acirc;n của ba chiếc lồng đ&egrave;n đ&atilde; xuất sắc đạt điểm cao nhất về 4 ti&ecirc;u ch&iacute; đ&aacute;nh gi&aacute; của cuộc thi Light up the Moon năm 2020 nh&eacute;:</p>\r\n\r\n<p>Xin ch&uacute;c mừng!</p>\r\n\r\n<p><strong><em><span style=\"color:#B22222\">Cơ sở 1: Tập thể lớp MOVERS 38</span></em></strong></p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/Newone/120295691_1444348029098563_8915095626136150429_o.jpg\" style=\"height:433px; width:500px\" /></p>\r\n\r\n<p>Cơ sở 2: Bạn Nguyễn Thị Minh Thư (Susan) Flyers 31</p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/Newone/120235423_1444348199098546_2879757189760318033_o.jpg\" style=\"height:667px; width:500px\" /></p>\r\n\r\n<p>Cơ sở 3: Bạn Từ Vĩnh Qu&yacute; (Antom) PET 17</p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/Newone/120712225_1445840868949279_3404385840298691661_o.jpg\" style=\"height:667px; width:500px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/Newone/120425746_1444350009098365_8924171220822824589_o.jpg\" style=\"height:667px; width:500px\" /></p>\r\n\r\n<p>H&atilde;y lu&ocirc;n giữ ngọn lửa s&aacute;ng tạo v&agrave; niềm đam m&ecirc; học tiếng Anh trong con con nh&eacute;! Ch&uacute;c cho ng&agrave;y Trung thu năm nay của c&aacute;c con thật nhiều niềm vui. V&agrave; h&atilde;y lu&ocirc;n nhớ rằng, niềm vui của con v&agrave; sự cố gắng mỗi ng&agrave;y trong việc học của con ch&iacute;nh l&agrave; niềm hạnh ph&uacute;c v&agrave; tự h&agrave;o của ba mẹ c&aacute;c con...</p>\r\n\r\n<p>Bằng tất cả tấm l&ograve;ng,</p>\r\n\r\n<p>Thầy c&ocirc; EVAS.</p>\r\n\r\n<p><span style=\"color:#B22222\"><span style=\"font-size:16px\"><strong><em>ANH NGỮ QUỐC TẾ &Acirc;U VIỆT &Uacute;C</em></strong></span></span></p>\r\n', 'Newone/exif_temp_image%20(1).jpg', 1, 4, '2020-11-19 04:32:27', '2020-11-19 04:32:27'),
(98, 'ĐÊM \"HỘI CHỢ\" GIÁNG SINH ĐẶC BIỆT TẠI EVAS', 'dem-hoi-cho-giang-sinh-dac-biet-tai-evas', 'Một lễ hội có thể được coi là một buổi lễ của trẻ em. Đêm Giáng sinh là một đêm kỳ diệu mà mọi ước nguyện của những đứa trẻ sẽ trở thành sự thật...', '<p>Gi&aacute;ng Sinh l&agrave; lễ hội thường ni&ecirc;n được tổ chức 24-25/12 hằng năm. Ở c&aacute;c nước phương T&acirc;y, đ&acirc;y được coi như một ng&agrave;y Tết, một lễ hội mang &yacute; nghĩa &quot;sum họp gia đ&igrave;nh&quot; v&agrave; cũng c&oacute; thể được coi l&agrave; một buổi lễ của trẻ em. Đ&ecirc;m Gi&aacute;ng sinh l&agrave; một đ&ecirc;m kỳ diệu m&agrave; mọi ước nguyện của những đứa trẻ sẽ trở th&agrave;nh sự thật.</p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/Christmas%202020/xmas.png\" style=\"height:401px; width:600px\" /></p>\r\n\r\n<p>Với mục đ&iacute;ch cho c&aacute;c em EVAS được trải nghiệm phong tục phương t&acirc;y v&agrave; &yacute; nghĩa về gia đ&igrave;nh m&agrave; ng&agrave;y Gi&aacute;ng Sinh mang lại, EVAS tổ chức c&aacute;c hoạt động vui nhộn cơ hội c&aacute;c em t&iacute;ch lũy tiếng Anh về lễ hội cũng như văn h&oacute;a phương T&acirc;y sẽ mang lại cho c&aacute;c em nhiều sự bất ngờ v&agrave; th&uacute; vị nh&eacute;!</p>\r\n\r\n<ul>\r\n	<li>Thời gian tổ chức: 20 - 23 - 24/12/2020</li>\r\n	<li>Gian h&agrave;ng với c&aacute;c hoạt đ&ocirc;ng t&igrave;m hiểu &yacute; nghĩa ng&agrave;y Gi&aacute;ng Sinh;</li>\r\n	<li>Tr&ograve; chơi thử th&aacute;ch từ vựng ng&agrave;y Gi&aacute;ng Sinh;</li>\r\n	<li>S&aacute;ng tạo ảnh c&ugrave;ng thầy c&ocirc; v&agrave; bạn b&egrave; tại g&oacute;c check-in.</li>\r\n</ul>\r\n\r\n<p>Đ&acirc;y sẽ l&agrave; một trong những cơ hội trải nghiệm văn h&oacute;a d&agrave;nh cho c&aacute;c bạn nhỏ theo học tại EVAS...</p>\r\n\r\n<p><span style=\"color:#B22222\"><span style=\"font-size:16px\"><em><strong>ANH NGỮ QUỐC TẾ &Acirc;U VIỆT &Uacute;C</strong></em></span></span></p>\r\n', 'Christmas%202020/xmas.png', 1, 2, '2020-12-19 05:01:07', '2020-12-19 05:01:07'),
(99, 'DU HỌC ANH! VÌ SAO NÊN CHỌN?', 'du-hoc-anh!-vi-sao-nen-chon', 'Việc chọn du học tại Anh cũng cho bạn nhiều ưu thế về tài chính, văn hóa và xã hội. Một cấu trúc chương trình ngắn hơn, chuyên sâu hơn đồng nghĩa với việc bạn sẽ có thể theo đuổi sự nghiệp nhanh chóng hơn và cắt giảm tối đa các chi phí sinh hoạt của mình trong lúc bạn đi học...', '<p><strong>V&Igrave; SAO N&Ecirc;N CHỌN DU HỌC ANH?</strong></p>\r\n\r\n<p><strong><em>Việc chọn du học tại một trường đại học của Vương quốc Anh cũng cho bạn nhiều ưu thế về t&agrave;i ch&iacute;nh, văn h&oacute;a v&agrave; x&atilde; hội. Một cấu tr&uacute;c chương tr&igrave;nh ngắn hơn, chuy&ecirc;n s&acirc;u hơn đồng nghĩa với việc bạn sẽ c&oacute; thể theo đuổi sự nghiệp nhanh ch&oacute;ng hơn v&agrave; cắt giảm tối đa c&aacute;c chi ph&iacute; sinh hoạt của m&igrave;nh trong l&uacute;c bạn đi học. </em></strong></p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/du-hoc/DU%20HOC%20ANH%20QUOC.png\" style=\"height:582px; width:600px\" /></p>\r\n\r\n<p><strong><em>Vương quốc Anh c&ograve;n c&oacute; một loạt c&aacute;c chương tr&igrave;nh được thiết kế nhằm cung cấp cho sinh vi&ecirc;n c&aacute;c khoản giảm gi&aacute; v&agrave; cơ hội tiếp cận dịch vụ vận chuyển c&ocirc;ng cộng rẻ v&agrave; những dụng cụ học tập cần thiết.</em></strong></p>\r\n\r\n<p>Đ&acirc;y l&agrave; năm l&yacute; do ch&iacute;nh để c&aacute;c sinh vi&ecirc;n c&acirc;n nhắc lựa chọn Vương quốc Anh.</p>\r\n\r\n<p><em><strong>1. Anh quốc l&agrave; c&aacute;i n&ocirc;i tiếng Anh của Thế giới</strong></em></p>\r\n\r\n<p>Anh quốc l&agrave; qu&ecirc; hương của tiếng Anh, l&agrave; nơi m&agrave; từ đ&acirc;y những người Anh đem tiếng Anh c&ugrave;ng nền văn h&oacute;a, khoa học&hellip; đến nhiều nước kh&aacute;c để lập nghiệp. Tại c&aacute;c quốc gia n&agrave;y, ng&agrave;y nay, tiếng Anh trở th&agrave;nh quốc ngữ v&agrave; c&ugrave;ng với người Anh th&igrave; người Mỹ, &Uacute;c, New Zealand, Canada trở th&agrave;nh những người bản xứ n&oacute;i tiếng Anh.</p>\r\n\r\n<p>V&agrave; khi tiếng Anh trở th&agrave;nh ng&ocirc;n ngữ quốc tế th&igrave; kh&ocirc;ng g&igrave; tốt hơn l&agrave; đến đất nước được coi l&agrave; c&aacute;i n&ocirc;i của tiếng Anh n&agrave;y để học tập v&agrave; trải nghi&ecirc;̣m trong m&ocirc;i trường tiếng Anh chuẩn mực nhất.</p>\r\n\r\n<p><em><strong>2. Chất lượng Gi&aacute;o dục h&agrave;ng đầu Thế giới</strong></em></p>\r\n\r\n<p>Vương quốc Anh thỏa m&atilde;n những yếu tố ti&ecirc;n quyết trong c&aacute;c nhu cầu của nhiều sinh vi&ecirc;n v&agrave; sở hữu một v&agrave;i trong số những trường đại học tốt nhất thế giới, bao gồm Cambridge v&agrave; Oxford. Chất lượng đ&agrave;o tạo được kiểm s&aacute;t chặt chẽ bởi Ofsted nơi chuy&ecirc;n đ&aacute;nh gi&aacute;, xếp loại một c&aacute;ch to&agrave;n diện c&aacute;c đơn vị đ&agrave;o tạo.</p>\r\n\r\n<p>Bằng cấp của Anh lu&ocirc;n được c&ocirc;ng nhận v&agrave; đ&aacute;nh gi&aacute; cao tr&ecirc;n to&agrave;n cầu. Với tấm bằng đại học tại Anh Quốc, du học sinh c&oacute; thể học th&ecirc;m l&ecirc;n hoặc xin việc l&agrave;m tại bất cứ nước n&agrave;o tr&ecirc;n thế giới. Những cựu du học sinh Anh quốc cũng l&agrave; những người được giới tuyển dụng ưa chuộng, do họ c&oacute; kiến thức cơ bản tốt, kỷ luật l&agrave;m việc cao v&agrave; kĩ năng mềm đảm bảo cần thiết để hỗ trợ cho c&ocirc;ng việc th&agrave;nh c&ocirc;ng.</p>\r\n\r\n<p><strong><em>3. Thời gian học tại Anh ngắn</em></strong></p>\r\n\r\n<p>So với nhiều nước kh&aacute;c, thời gian học tại Anh được coi l&agrave; ngắn: bậc cử nh&acirc;n được đ&agrave;o tạo trong 3 năm v&agrave; bậc thạc sỹ được đ&agrave;o tạo trong 01 năm. Điều n&agrave;y gi&uacute;p c&aacute;c du học sinh tiết kiệm thời gian, tiền bạc, ra trường sớm v&agrave; sớm tiếp cận c&aacute;c cơ hội việc l&agrave;m, thu nhập cao.</p>\r\n\r\n<p><strong><em>4. Văn h&oacute;a</em></strong></p>\r\n\r\n<p>Kh&ocirc;ng nhiều nơi c&oacute; một nền văn h&oacute;a đa dạng như Vương quốc Anh. Với sự pha trộn của nhiều cộng đồng, nhiều chủng tộc v&agrave; một nền lịch sử l&acirc;u đời v&agrave; phong ph&uacute; chắc chắn đ&acirc;y sẽ l&agrave; một nơi tuyệt vời để bạn kh&aacute;m ph&aacute;.</p>\r\n\r\n<p><em><strong>5. Vương quốc Anh c&oacute; vị tr&iacute; địa l&yacute; l&yacute; tưởng</strong></em></p>\r\n\r\n<p>Vương quốc Anh nằm ở vị tr&iacute; l&yacute; tưởng cho việc thực hiện những chuyến du lịch ngắn đến c&aacute;c quốc gia kh&aacute;c. Từ London, bạn c&oacute; thể đi đến nhiều địa điểm kh&aacute;c ở ch&acirc;u &Acirc;u m&agrave; chỉ mất v&agrave;i giờ, một l&yacute; do khiến th&agrave;nh phố n&agrave;y trở th&agrave;nh một trung t&acirc;m du lịch tuyệt vời.</p>\r\n\r\n<p><strong><em>6. Sinh vi&ecirc;n được ở lại 02 năm l&agrave;m việc sau khi tốt nghiệp</em></strong></p>\r\n\r\n<p>(Link tham khảo:&nbsp;<a href=\"https://l.facebook.com/l.php?u=http%3A%2F%2Fwww.ukcisa.org.uk%2FInformation%E2%80%93Advice%2FWorking%2FWorking-after-studies%3Ffbclid%3DIwAR1WWYs06SLdJ5eSnh1a3VWuxJkGgomXbMtl4hm8UPHeFjKohlAhI9e2bjg&amp;h=AT22dLsutKYhacscclvfA519TXzaFIIP1O3wHgoXQM3IPxu6BZc8iDTngt-suwbCktvbUTlm8EZ__pMfZmE-9DWqFMvrc-IYj62gkAqmZNzFn5baumK_L6d-1StO36FBa4h_&amp;__tn__=-UK-R&amp;c[0]=AT1YvRSc3W40sLPuCWQIIE3b7D5qTCOYEio7xFT54f1_nJexRM-CImWON6ZEjCZ2w36QIwvPG4Uji_ajDKv0EnVbHv5DjJvofHfQ_iM6IiLiMoaw-ZhY_v55WxYhjbQ0t_WAiA0dZ-dqk0X0iel9ZHs9BbEJ6KuqtkhUh7leNF1q6cbTnFUjhzq_vmbGVG_k-A-L\" rel=\"nofollow noopener\" tabindex=\"0\" target=\"_blank\">www.ukcisa.org.uk/Information&ndash;Advice/Working/Working-after-studies</a>)</p>\r\n\r\n<p>Ch&iacute;nh s&aacute;ch Visa Anh mới được Thủ tướng c&ocirc;ng bố cho ph&eacute;p du học sinh ở lại quốc gia n&agrave;y trong v&ograve;ng 2 năm. Cụ thể, từ năm 2021, sinh vi&ecirc;n quốc tế khi theo học bậc đại học, sau đại học hoặc tiến sĩ ở Anh sẽ cho ph&eacute;p sinh vi&ecirc;n đủ điều kiện l&agrave;m việc hoặc t&igrave;m kiếm việc l&agrave;m, trong bất cứ ng&agrave;nh nghề hoặc vị tr&iacute; n&agrave;o m&agrave; sinh vi&ecirc;n chọn trong v&ograve;ng 2 năm sau khi ho&agrave;n th&agrave;nh kh&oacute;a học. Chiến lược n&agrave;y được cho rằng nhằm th&uacute;c đẩy sự thu h&uacute;t của du học Anh, coi xứ sở sương m&ugrave; l&agrave; điểm đến du học tuyệt vời.</p>\r\n\r\n<p>Sinh vi&ecirc;n c&oacute; 2 năm để t&igrave;m việc l&agrave;m, nếu sở hữu được c&ocirc;ng việc tốt v&agrave; nhận được lời mời từ nh&agrave; tuyển dụng, sinh vi&ecirc;n sẽ dễ d&agrave;ng hơn rất nhiều để c&oacute; working visa v&agrave; cơ hội l&agrave;m việc l&acirc;u d&agrave;i tại Anh.</p>\r\n\r\n<p><em>(Tham khảo nguồn IDP)</em></p>\r\n\r\n<p><strong><em>ANH NGỮ QUỐC TẾ &Acirc;U VIỆT &Uacute;C</em></strong></p>\r\n\r\n<p><strong><em>&Acirc;U VIỆT &Uacute;C - IDP IELTS - DU HỌC ANH&nbsp;</em></strong></p>\r\n\r\n<p><a href=\"https://www.facebook.com/auvietucieltsduhoc\"><strong><em>https://www.facebook.com/auvietucieltsduhoc</em></strong></a></p>\r\n', 'du-hoc/DU%20HOC%20ANH%20QUOC.png', 1, 1, '2021-02-19 11:28:29', '2021-02-19 11:28:29'),
(100, 'IELTS: ĐỐI TÁC CHÍNH THỨC CỦA IDP IELTS', 'ielts:-doi-tac-chinh-thuc-cua-idp-ielts', 'Chương trình học cam kết việc đạt chứng chỉ IELTS miễn thi tốt nghiệp THPT, và dẫn dắt nhiều em học viên đạt kết quả từ 6.5 IELTS, cũng như đào tạo học viên nguyện vọng đạt kết quả đủ điều kiện du học hoặc việc làm thời gian ngắn chinh phục IELTS thành công...', '<p><span style=\"color:#B22222\"><span style=\"font-size:18px\"><em><strong>CHƯƠNG TR&Igrave;NH Đ&Agrave;O TẠO IELTS</strong></em></span></span></p>\r\n\r\n<p><strong><em>L&agrave; đối t&aacute;c của IDP IELTS, Trung t&acirc;m Ngoại ngữ quốc tế &Acirc;u Việt &Uacute;c hỗ trợ tư vấn thủ tục v&agrave; đăng k&yacute; thi IELTS tại hội đồng thi IDP miễn ph&iacute;, đặc biệt hỗ trợ % lệ ph&iacute; thi IELTS cho học vi&ecirc;n Trung t&acirc;m.</em></strong></p>\r\n\r\n<p>Với đội ngũ giảng vi&ecirc;n chuy&ecirc;n m&ocirc;n cao 8.0 IELTS đầy nhiệt huyết, c&ugrave;ng với lộ tr&igrave;nh v&agrave; chương tr&igrave;nh đ&agrave;o tạo chất lượng hiệu quả từ IELTS cho người mới bắt đầu đến n&acirc;ng cao. Trung t&acirc;m cam kết nhiều em đạt chứng chỉ IELTS miễn thi tốt nghiệp THPT, v&agrave; dẫn dắt nhiều em học vi&ecirc;n đạt kết quả từ 6.5 IELTS, cũng như đ&agrave;o tạo học vi&ecirc;n nguyện vọng đạt kết quả đủ điều kiện du học hoặc việc l&agrave;m thời gian ngắn chinh phục IELTS th&agrave;nh c&ocirc;ng.</p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#006400\"><em><strong>Ti&ecirc;u ch&iacute; đ&agrave;o tạo:</strong></em></span></span></p>\r\n\r\n<ul>\r\n	<li>Nắm vững cấu tr&uacute;c đề thi;</li>\r\n	<li>Ph&aacute;t triển chiến thuật l&agrave;m b&agrave;i thi;</li>\r\n	<li>Tập trung r&egrave;n luyện to&agrave;n diện bốn kỹ năng Nghe - N&oacute;i - Đọc- Viết;</li>\r\n	<li>Lập chiến lược l&agrave;m b&agrave;i hợp l&yacute; đối với c&aacute;c dạng c&acirc;u hỏi kh&aacute;c nhau của b&agrave;i thi Nghe v&agrave; Đọc.</li>\r\n	<li>Ph&aacute;t triển khả năng suy nghĩ độc lập, đưa ra nhiều &yacute; kiến trong b&agrave;i thi N&oacute;i v&agrave; Viết.</li>\r\n	<li>Phụ đạo v&agrave; hỗ trợ (1 thầy: 1 tr&ograve;) kỹ năng N&oacute;i v&agrave; Viết suốt kh&oacute;a học;</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#006400\"><strong><em>Lộ tr&igrave;nh học v&agrave; điểm cam kết đầu ra:</em></strong></span></span></p>\r\n\r\n<p>o <strong>IELTS L1</strong>: Cam kết đầu ra 4.0 IELTS</p>\r\n\r\n<p>o <strong>IELTS L2</strong>: Cam kết đầu ra 4.5</p>\r\n\r\n<p>o <strong>IELTS L3</strong>: Cam kết đầu ra 5.0</p>\r\n\r\n<p>o <strong>IELTS L4</strong>: Cam kết đầu ra 5.5</p>\r\n\r\n<p>o <strong>IELTS L5</strong>: Cam kết đầu ra 6.0+</p>\r\n\r\n<p>o<strong> IELTS Skill Set</strong></p>\r\n\r\n<p>Th&ocirc;ng tin chi tiết chương tr&igrave;nh đ&agrave;o tạo IELTS tại Trung t&acirc;m ngoại ngữ &Acirc;u Việt &Uacute;c hoặc hỗ trợ thủ tục thi IELTS miễn ph&iacute; hội đồng thi IDP. Vui l&ograve;ng li&ecirc;n hệ:</p>\r\n\r\n<p><span style=\"color:#B22222\"><span style=\"font-size:18px\"><strong><em>ANH NGỮ QUỐC TẾ &Acirc;U VIỆT &Uacute;C</em></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"color:#B22222\"><strong><em>&Acirc;U VIỆT &Uacute;C - IDP IELTS - DU HỌC ANH</em></strong></span></span></p>\r\n\r\n<p><a href=\"https://www.facebook.com/auvietucieltsduhoc\"><strong><em>https://www.facebook.com/auvietucieltsduhoc</em></strong></a></p>\r\n', 'du-hoc/IDP%20IELTS.png', 1, 1, '2021-02-19 11:48:29', '2021-02-19 11:48:29'),
(101, 'HỘI THẢO DU HỌC TRỰC TUYẾN', 'hoi-thao-du-hoc-truc-tuyen', 'Với EVAS, chúng tôi không chỉ chuyên về đào tạo tiếng Anh mà việc định hướng nghề nghiệp, đích đến cho con đường học vấn của các con luôn là sứ mệnh hàng đầu cả đội ngũ tâm huyết xây dựng.', '<p><span style=\"font-size:18px\"><span style=\"color:#800000\"><strong>HỘI THẢO DU HỌC ANH V&Agrave; HỌC BỔNG 30%- TO&Agrave;N PHẦN</strong></span></span></p>\r\n\r\n<p><strong><em>K&iacute;nh gửi Qu&yacute; phụ huynh v&agrave; c&aacute;c bạn học sinh,</em></strong></p>\r\n\r\n<p>Đầu năm 2021, Trung t&acirc;m Ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c tại An Giang ch&iacute;nh thức k&yacute; hơp t&aacute;c tư vấn du học c&ugrave;ng IDP Education v&agrave; trường đại học Huddersfield nhằm hỗ trợ c&aacute;c em học sinh trong việc chọn lựa kh&oacute;a học tốt nhất, nộp đơn v&agrave;o trường đại học quan t&acirc;m hay những hướng dẫn nộp hồ sơ visa du học nếu đang ấp ủ dự định du học &Uacute;c, Canada, Anh, Mỹ, New Zealand v&agrave; Ireland.</p>\r\n\r\n<p>Tr&acirc;n trọng k&iacute;nh mời Qu&yacute; phụ huynh v&agrave; c&aacute;c bạn học sinh tham gia Hội thảo trực tuyến: Chương tr&igrave;nh hợp t&aacute;c tư vấn du học c&aacute;c nước do &Acirc;u Việt &Uacute;c tổ chức với sự chia sẻ từ c&aacute;c đối t&aacute;c IDP Education v&agrave; trường đại học Huddersfield để biến mục ti&ecirc;u du học th&agrave;nh bệ ph&oacute;ng vững chắc đến sự th&agrave;nh c&ocirc;ng tương lai.</p>\r\n\r\n<p>&bull;<em><span style=\"color:#800000\"> Ms. Xu&acirc;n L&ecirc; &ndash; <strong>Đại diện tuyển sinh</strong></span></em></p>\r\n\r\n<p><span style=\"color:#800000\">&bull; <em>Mr. Chương Nguyễn &ndash; <strong>Gi&aacute;m đốc văn ph&ograve;ng IDP Q7 v&agrave; Cần Thơ</strong></em></span></p>\r\n\r\n<p><span style=\"color:#0000CD\">Thời gian:</span><span style=\"color:#800080\"> </span>Chủ nhật 28/02/2021 | 9:00 &ndash; 10:30</p>\r\n\r\n<p><span style=\"color:#0000CD\">Đăng k&yacute; tham gia ngay tại đ&acirc;y:</span> <a href=\"http://bit.ly/AuVietUc-28Feb\" rel=\"nofollow noopener\" tabindex=\"0\" target=\"_blank\">http://bit.ly/AuVietUc-28Feb</a> để nhận hướng dẫn tham gia trực tuyến</p>\r\n\r\n<p>----------------</p>\r\n\r\n<p><span style=\"color:#006400\"><span style=\"font-size:16px\"><strong><em>Giới thiệu về IDP Education:</em></strong></span></span></p>\r\n\r\n<p>&bull; L&agrave; tổ chức gi&aacute;o dục h&agrave;ng đầu thế giới do 38 trường c&ocirc;ng học Đại học &Uacute;c th&agrave;nh lập năm 1969 v&agrave; hiện c&oacute; tr&ecirc;n 100 văn ph&ograve;ng tại 32 quốc gia.</p>\r\n\r\n<p>&bull; C&oacute; mặt tại Việt Nam từ năm 1996, IDP Education Việt Nam lu&ocirc;n giữ vị tr&iacute; dẫn đầu trong việc cung cấp c&aacute;c dịch vụ tư vấn du học v&agrave; ph&aacute;t triển gi&aacute;o dục tại Việt Nam.</p>\r\n\r\n<p>C&aacute;c dịch vụ hỗ trợ miễn ph&iacute; từ IDP bao gồm:</p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#0000CD\"><strong><em>Tư vấn du học miễn ph&iacute;, giải ph&aacute;p tiết kiệm</em></strong></span></li>\r\n	<li><span style=\"color:#0000CD\"><strong><em>Tư vấn chọn trường &amp; kh&oacute;a học ph&ugrave; hợp</em></strong></span></li>\r\n	<li><span style=\"color:#0000CD\"><strong><em>Giới thiệu học bổng 30-100%</em></strong></span></li>\r\n	<li><span style=\"color:#0000CD\"><strong><em>Hỗ trợ nộp đơn xin nhập học, theo d&otilde;i tiến độ v&agrave; nhận thư x&aacute;c nhận từ trường</em></strong></span></li>\r\n	<li><span style=\"color:#0000CD\"><strong><em>Thực h&agrave;nh phỏng vấn, ho&agrave;n tất hồ sơ xin visa du học miễn ph&iacute; với tỉ đậu 95%</em></strong></span></li>\r\n	<li><span style=\"color:#0000CD\"><strong><em>Tư vấn chỗ ở, kế hoạch t&agrave;i ch&iacute;nh, đặt v&eacute; m&aacute;y bay, trang bị kỹ năng trước khi l&ecirc;n đường</em></strong></span></li>\r\n</ul>\r\n\r\n<p>----------------</p>\r\n\r\n<p><span style=\"color:#006400\"><strong><em><span style=\"font-size:16px\">Giới thiệu về đại học Huddersfield, Anh Quốc:</span></em></strong></span></p>\r\n\r\n<p>&bull; Đại học Huddersfield được th&agrave;nh lập năm 1825 với tư c&aacute;ch l&agrave; Học viện Cơ kh&iacute; v&agrave; Khoa học Huddersfield. Trường nằm tại th&agrave;nh phố Huddersfield, T&acirc;y Yorkshire, nước Anh v&agrave; hiện c&oacute; khoảng 22.000 sinh vi&ecirc;n đăng k&yacute;.</p>\r\n\r\n<p>&bull; Trường hoạt động trong c&aacute;c cơ sở ở Barnsley v&agrave; Oldham. C&aacute;c chương tr&igrave;nh học được tổ chức với h&agrave;ng loạt m&ocirc;n học như Khoa học ứng dụng, Ngệ thuật, Thiết kế v&agrave; Kiến tr&uacute;c, M&aacute;y t&iacute;nh v&agrave; Cơ kh&iacute;, Gi&aacute;o dục v&agrave; Ph&aacute;t triển nghề nghiệp, Khoa học con người v&agrave; Khoa học sức khỏe, &Acirc;m nhạc, Nh&acirc;n văn v&agrave; Truyền th&ocirc;ng.</p>\r\n\r\n<p>Hỗ trợ hồ sơ du học c&aacute;c cấp bậc tại Anh Quốc xin vui l&ograve;ng li&ecirc;n hệ:</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"color:#B22222\"><strong><em>ANH NGỮ QUỐC TẾ &Acirc;U VIỆT &Uacute;C</em></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"color:#B22222\"><strong><em>&Acirc;U VIỆT &Uacute;C - IDP IELTS - DU HỌC ANH&nbsp;</em></strong></span></span></p>\r\n\r\n<p><a href=\"https://www.facebook.com/auvietucieltsduhoc\"><strong><em>https://www.facebook.com/auvietucieltsduhoc</em></strong></a></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'du-hoc/DU%20HOC.jpg', 1, 1, '2021-02-20 01:39:10', '2021-02-20 01:39:10'),
(102, 'HỌC BỔNG COACHING AND LEADERSHIP TOÀN PHẦN', 'hoc-bong-coaching-and-leadership-toan-phan', 'Thông tin học bổng cơ bản cho khóa đào tạo Lãnh đạo...', '<p>HỌC BỔNG L&Atilde;NH ĐẠO TƯƠNG LAI</p>\r\n\r\n<p>--------------</p>\r\n\r\n<p>UK-Vietnam Institute of Education Development (UKVIED) &amp; CIRCL cấp 10 suất học bổng về kh&oacute;a đ&agrave;o tạo Coaching and Leadership. Th&ocirc;ng tin cụ thể như trong file.</p>\r\n\r\n<p>Chi tiết c&aacute;c bạn li&ecirc;n hệ trực tiếp Trung t&acirc;m &Acirc;u Việt &Uacute;c hoặc đăng nhập v&agrave;o Website: <a href=\"https://ukvied.co.uk/futureleaders/?fbclid=IwAR0Kd2yfg-LPuJ0hskIfIb23WwM9GvPNfQqkBt3WU8pjaviRR0oNB_N8JXE\" rel=\"nofollow noopener\" tabindex=\"0\" target=\"_blank\">https://ukvied.co.uk/futureleaders/</a></p>\r\n\r\n<p>Email: enquiries@ukvied.co.uk</p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/du-hoc/hoc%20bong%20Coaching%20and%20Leadership.jpg\" /></p>\r\n\r\n<p>----------------------</p>\r\n\r\n<p>UKVIED in partnership with CIRCL offer 10 Full Scholarships in Coaching and Leadership.</p>\r\n\r\n<p>Please look into the detail information and apply.</p>\r\n\r\n<p>For more information, please contact EVAS or visit:</p>\r\n\r\n<p>Website: <a href=\"https://ukvied.co.uk/futureleaders/?fbclid=IwAR1I9mDTbeM_FgteZABHB8R7QcSdHe_N3h7rbyaKgwiXSb53ShpdOgi_j7w\" rel=\"nofollow noopener\" tabindex=\"0\" target=\"_blank\">https://ukvied.co.uk/futureleaders/</a></p>\r\n\r\n<p>Email: enquiries@ukvied.co.uk</p>\r\n', 'du-hoc/hoc%20bong%20Coaching.jpg', 1, 1, '2021-02-22 14:04:54', '2021-02-22 14:04:54');
INSERT INTO `news_items` (`id`, `title`, `code`, `description`, `detail`, `image`, `status`, `sort`, `created_at`, `updated`) VALUES
(103, 'BÀI THI IELTS TRÊN MÁY TÍNH CÓ KHÁC VỚI BÀI THI GIẤY TRUYỀN THỐNG?', 'bai-thi-ielts-tren-may-tinh-co-khac-voi-bai-thi-giay-truyen-thong', 'Giải đáp mọi thắc mắc cho những câu hỏi thường gặp khi đứng trước sự lựa chọn phương thức tham dự kỳ thi IELTS...', '<p><em><strong>Với IELTS, bạn c&oacute; được sự lựa chọn.</strong></em></p>\r\n\r\n<p><em>Để x&aacute;c định được h&igrave;nh thức b&agrave;i test n&agrave;o ph&ugrave; hợp với bạn, điều n&agrave;y c&ograve;n phụ thuộc v&agrave;o &yacute; muốn v&agrave; sở th&iacute;ch ri&ecirc;ng của từng c&aacute; nh&acirc;n. C&oacute; th&iacute; sinh sẽ ưu ti&ecirc;n thi tr&ecirc;n giấy bởi cảm gi&aacute;c quen thuộc như đang l&agrave;m b&agrave;i kiểm tra ở trường. Tuy nhi&ecirc;n, sẽ c&oacute; những th&iacute; sinh lựa chọn thi tr&ecirc;n m&aacute;y t&iacute;nh v&igrave; sự thoải m&aacute;i khi đ&aacute;nh m&aacute;y.</em></p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/du-hoc/ielts_test_guide.jpg\" style=\"height:276px; width:930px\" /></p>\r\n\r\n<p>H&atilde;y c&ugrave;ng t&igrave;m hiểu về sự kh&aacute;c biệt cơ bản nhất giữa cả 2 h&igrave;nh thức thi IELTS tr&ecirc;n giấy v&agrave; IELTS tr&ecirc;n m&aacute;y t&iacute;nh:</p>\r\n\r\n<p><span style=\"color:#B22222\"><strong><em>1. Nội dung thi tr&ecirc;n giấy c&oacute; kh&aacute;c hay kh&oacute; hơn so với nội dung b&agrave;i thi tr&ecirc;n m&aacute;y t&iacute;nh kh&ocirc;ng?</em></strong></span></p>\r\n\r\n<p>Kh&ocirc;ng. B&agrave;i thi tr&ecirc;n m&aacute;y t&iacute;nh cũng sẽ bao gồm phần thi Nghe, Đọc v&agrave; Viết. H&igrave;nh thức thi n&agrave;y sẽ kh&ocirc;ng kh&aacute;c g&igrave; h&igrave;nh thức thi tr&ecirc;n giấy về mặt nội dung, c&aacute;ch chấm điểm IELTS, độ kh&oacute; v&agrave; c&aacute;c dạng c&acirc;u hỏi.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, phần thi N&oacute;i sẽ được giữ nguy&ecirc;n h&igrave;nh thức thi trực tiếp với một Gi&aacute;m khảo IELTS gi&agrave;u kinh nghiệm v&agrave; đ&atilde; qua đ&agrave;o tạo.</p>\r\n\r\n<p><span style=\"color:#B22222\"><strong><em>2. B&agrave;i thi IELTS tr&ecirc;n m&aacute;y t&iacute;nh c&oacute; g&igrave; kh&aacute;c biệt so với c&aacute;c văn bằng thi tiếng Anh tr&ecirc;n m&aacute;y t&iacute;nh kh&aacute;c?</em></strong></span></p>\r\n\r\n<p>Với IELTS, th&iacute; sinh lu&ocirc;n cảm thấy tự tin rằng họ đang tham dự một b&agrave;i thi kiểm tra năng lực tiếng Anh phổ biến v&agrave; uy t&iacute;n nhất tr&ecirc;n thế giới d&agrave;nh cho việc học tập, l&agrave;m việc cũng như di cư.</p>\r\n\r\n<p>IELTS được tin tưởng bởi 10,000 tổ chức tr&ecirc;n thế giới bởi t&iacute;nh c&ocirc;ng bằng, độ tin cậy cũng như ti&ecirc;u chuẩn chất lượng cao. Điều n&agrave;y đ&uacute;ng với cả h&igrave;nh thức thi tr&ecirc;n m&aacute;y t&iacute;nh lẫn tr&ecirc;n giấy.</p>\r\n\r\n<p><span style=\"color:#B22222\"><strong><em>3. Thi IELTS tr&ecirc;n m&aacute;y t&iacute;nh c&oacute; đồng nghĩa với việc sẽ nhận được kết quả nhanh hơn?</em></strong></span></p>\r\n\r\n<p>Nếu bạn thực hiện b&agrave;i thi IELTS tr&ecirc;n m&aacute;y t&iacute;nh bạn sẽ được th&ocirc;ng b&aacute;o kết quả trong v&ograve;ng từ 3 đến 5 ng&agrave;y kể từ ng&agrave;y bạn kết th&uacute;c b&agrave;i thi của m&igrave;nh. Trong khi kết quả thi IELTS tr&ecirc;n giấy sẽ cần đến 13 ng&agrave;y.</p>\r\n\r\n<p><span style=\"color:#B22222\"><em><strong>4. H&igrave;nh thức thi n&agrave;o sẽ tốn k&eacute;m hơn?</strong></em></span></p>\r\n\r\n<p>IELTS lu&ocirc;n cung cấp c&aacute;c trải nghiệm l&agrave;m b&agrave;i thi tương đương nhau bất kể bạn thi theo h&igrave;nh thức n&agrave;o. V&igrave; vậy, h&atilde;y đọc kỹ c&aacute;c quy định về lệ ph&iacute; thi IELTS khi lựa chọn h&igrave;nh thức thi, trước khi bạn ho&agrave;n th&agrave;nh thủ tục đăng k&yacute; thi IELTS.</p>\r\n\r\n<p>(Tham khảo nguồn IDP IELTS)</p>\r\n\r\n<p><span style=\"color:#B22222\"><span style=\"font-size:18px\"><strong><em>ANH NGỮ QUỐC TẾ &Acirc;U VIỆT &Uacute;C</em></strong></span></span></p>\r\n\r\n<p>Th&ocirc;ng tin chi tiết chương tr&igrave;nh đ&agrave;o tạo IELTS tại Trung t&acirc;m ngoại ngữ &Acirc;u Việt &Uacute;c hoặc hỗ trợ thủ tục thi IELTS miễn ph&iacute; hội đồng thi IDP. Vui l&ograve;ng li&ecirc;n hệ:</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"color:#B22222\"><strong><em>&Acirc;U VIỆT &Uacute;C - IDP IELTS - DU HỌC ANH&nbsp;</em></strong></span></span></p>\r\n\r\n<p><a href=\"http://auvietuc.edu.vn/%22https://www.facebook.com/auvietucieltsduhoc/%22\"><strong><em>https://www.facebook.com/auvietucieltsduhoc</em></strong></a></p>\r\n', 'du-hoc/IELT%20test.png', 1, 1, '2021-02-24 03:28:39', '2021-02-24 03:28:39'),
(104, 'TẠI SAO NÊN DU HỌC ANH BẬC CỬ NHÂN?', 'tai-sao-nen-du-hoc-anh-bac-cu-nhan', 'Tiến xa hơn trên con đường học vấn tại Vương Quốc Anh...', '<p><span style=\"color:#B22222\"><strong><em>Tiến xa hơn tr&ecirc;n con đường học vấn tại Vương Quốc Anh...</em></strong></span></p>\r\n\r\n<p><span style=\"color:#B22222\"><strong><em><img alt=\"\" src=\"/upload/images/du-hoc/Huddersfield-campus.jpg\" style=\"height:400px; width:400px\" /></em></strong></span></p>\r\n\r\n<p>Nhận bằng tại Vương quốc Anh (Anh) l&agrave; một c&aacute;ch tuyệt vời để củng cố v&agrave; ph&aacute;t triển chuy&ecirc;n m&ocirc;n của bạn v&agrave; tạo n&ecirc;n những kết nối l&acirc;u d&agrave;i trong một m&ocirc;i trường năng động v&agrave; đa văn h&oacute;a. C&oacute; một loạt c&aacute;c th&agrave;nh phố, thị trấn v&agrave; l&agrave;ng qu&ecirc; quốc tế ở c&aacute;c v&ugrave;ng của Vương quốc Anh, cụ thể l&agrave; Anh, Scotland, Bắc Ireland v&agrave; xứ Wales, v&agrave; hơn 50.000 kh&oacute;a học trong hơn 25 m&ocirc;n học c&oacute; sẵn.</p>\r\n\r\n<p>Du học tại một trường đại học tại Vương quốc Anh cũng mang lại nhiều lợi thế như cấu tr&uacute;c kh&oacute;a học ngắn hơn v&agrave; chuy&ecirc;n s&acirc;u hơn cho ph&eacute;p sinh vi&ecirc;n theo d&otilde;i v&agrave; ph&aacute;t triển sự nghiệp một c&aacute;ch nhanh hơn v&agrave; giảm thiểu chi ph&iacute; sinh hoạt trong giai đoạn học tập. Theo truyền thống, Vương quốc Anh cung cấp chương tr&igrave;nh đại học 3 năm v&agrave; chương tr&igrave;nh thạc sĩ 1 năm tại c&aacute;c trường đại học. Cơ hội học tập sau đại học chất lượng cao cũng c&oacute; sẵn ở phần lớn c&aacute;c trường đại học tại Vương quốc Anh, với một số trường đại học cung cấp t&agrave;i trợ để gia hạn visa cấp 4.</p>\r\n\r\n<p>Vương quốc Anh cũng c&oacute; sự đa dạng phong ph&uacute; về văn h&oacute;a, ng&ocirc;n ngữ v&agrave; t&iacute;n ngưỡng. C&aacute;c trường đại học c&oacute; một loạt c&aacute;c c&acirc;u lạc bộ v&agrave; x&atilde; hội đảm bảo một cuộc sống x&atilde; hội s&ocirc;i động cho sinh vi&ecirc;n, v&agrave; bạn sẽ được trải nghiệm nhiều nền ẩm thực v&ocirc; c&ugrave;ng đa dạng, hệ thống giao th&ocirc;ng mạnh mẽ, c&aacute;c lễ hội &acirc;m nhạc nổi tiếng v&agrave; c&aacute;c giải v&ocirc; địch thể thao quốc tế trong suốt những năm sinh vi&ecirc;n của bạn.</p>\r\n\r\n<p><strong>Tại sao n&ecirc;n du học Anh bậc Cử nh&acirc;n?</strong></p>\r\n\r\n<ul>\r\n	<li><strong><span style=\"color:#006400\">Nền gi&aacute;o dục được quốc tế c&ocirc;ng nhận</span>:&nbsp;</strong>Vương quốc Anh l&agrave; nơi c&oacute; một số trường đại học tốt nhất tr&ecirc;n thế giới, bao gồm Cambridge v&agrave; Oxford.</li>\r\n	<li><span style=\"color:#006400\"><strong>Được tiếp cận c&aacute;c dịch vụ chăm s&oacute;c sức khỏe:</strong></span>&nbsp;Dịch vụ y tế quốc gia (NHS) ở Anh cung cấp cho sinh vi&ecirc;n quốc tế quyền truy cập chăm s&oacute;c sức khỏe miễn ph&iacute; tại địa phương khi thanh to&aacute;n Phụ ph&iacute; y tế quốc tế (IHS).</li>\r\n	<li><span style=\"color:#006400\"><strong>Nền kinh tế:</strong></span>&nbsp;Vương quốc Anh l&agrave; nền kinh tế lớn thứ năm tr&ecirc;n thế giới với GDP 2,62 ngh&igrave;n tỷ đ&ocirc; la.</li>\r\n	<li><span style=\"color:#006400\"><strong>Sự đa dạng về văn h&oacute;a:</strong></span>&nbsp;C&oacute; rất &iacute;t nơi c&oacute; nền văn h&oacute;a phong ph&uacute; giống như Vương quốc Anh. Với đa d&acirc;n tộc v&agrave; một lịch sử l&acirc;u d&agrave;i v&agrave; th&uacute; vị, đ&acirc;y l&agrave; một nơi hấp dẫn để bạn kh&aacute;m ph&aacute;.</li>\r\n	<li><span style=\"color:#006400\"><strong>Vị tr&iacute; địa l&yacute;:</strong></span>&nbsp;Vương quốc Anh chỉ c&aacute;ch một v&agrave;i giờ bay đến c&aacute;c điểm đến ch&acirc;u &Acirc;u kh&aacute;c nhau, l&agrave;m cho n&oacute; trở th&agrave;nh một địa điểm trung t&acirc;m du lịch l&yacute; tưởng.</li>\r\n</ul>\r\n\r\n<p><em>(Nguồn IDP)</em></p>\r\n\r\n<p>Hỗ trợ hồ sơ du học bậc Đại học tại Anh Quốc xin vui l&ograve;ng li&ecirc;n hệ:</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"color:#B22222\"><strong><em>&Acirc;U VIỆT &Uacute;C - IDP IELTS - DU HỌC ANH</em></strong></span></span></p>\r\n\r\n<p><a href=\"https://www.facebook.com/auvietucieltsduhoc\"><strong><em>https://www.facebook.com/auvietucieltsduhoc</em></strong></a></p>\r\n', 'du-hoc/uk-study.jpg', 1, 1, '2021-02-24 03:51:38', '2021-02-24 03:51:38'),
(105, 'DU HỌC BẬC THẠC SỸ TẠI ANH, VÌ SAO NÊN?', 'du-hoc-bac-thac-sy-tai-anh-vi-sao-nen', 'Tấm bằng Thạc sĩ tại Anh có giá trị cao hơn rất nhiều và thời gian học thường được kéo dài trong một năm sẽ cho phép sinh viên sau đại học bắt kịp sự nghiệp một các nhanh chóng hơn...', '<p>Bằng thạc sĩ tại Vương quốc Anh (Anh), bao gồm c&aacute;c kh&oacute;a học như MA, MSc, MRes, MBA v&agrave; LLM, thường chỉ k&eacute;o d&agrave;i trong một năm, cho ph&eacute;p sinh vi&ecirc;n sau đại học bắt kịp sự nghiệp nhanh hơn trong khoảng thời gian ngắn hơn so với c&aacute;c quốc gia kh&aacute;c. Vương quốc Anh l&agrave; nơi c&oacute; nhiều trường đại học h&agrave;ng đầu như Đại học Oxford, Đại học Cambridge, Đại học Ho&agrave;ng gia Lu&acirc;n Đ&ocirc;n, Đại học Queen Mary ở Lu&acirc;n Đ&ocirc;n v&agrave; Đại học Edinburgh.</p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/du-hoc/Education-in-the-UK-840x430.jpg\" style=\"height:307px; width:600px\" /></p>\r\n\r\n<p><em>Sự phong ph&uacute; v&agrave; đa dạng của c&aacute;c nền văn h&oacute;a l&agrave; một sức h&uacute;t rất lớn, với c&aacute;c th&agrave;nh phố quốc tế như London v&agrave; Glasgow đến c&aacute;c quận lịch sử như Warwickshire v&agrave; Yorkshire, chỉ c&aacute;ch một chuyến t&agrave;u cho d&ugrave; bạn chọn lấy bằng tại bất cứ nơi n&agrave;o.</em></p>\r\n\r\n<p>Dưới đ&acirc;y l&agrave; một số l&yacute; do khiến Vương quốc Anh l&agrave; nơi tuyệt vời để bạn lấy tấm bằng Thạc Sĩ của m&igrave;nh:</p>\r\n\r\n<ul>\r\n	<li><strong>C&aacute;c trường đại học nổi tiếng quốc tế:</strong>&nbsp;Vương quốc Anh l&agrave; một trong ba điểm đến du học h&agrave;ng đầu thế giới v&agrave; l&agrave; nơi c&oacute; nhiều trường đại học h&agrave;ng đầu như Đại học Oxford, Đại học Cambridge, Đại học Ho&agrave;ng gia Lu&acirc;n Đ&ocirc;n v&agrave; Đại học Edinburgh.</li>\r\n	<li><strong>Chất lượng gi&aacute;o dục:</strong>&nbsp;C&aacute;c chương tr&igrave;nh của Vương quốc Anh được quốc tế c&ocirc;ng nhận v&agrave; họ nổi tiếng với chất lượng giảng dạy v&agrave; nghi&ecirc;n cứu</li>\r\n	<li><strong>L&agrave;m việc v&agrave; học tập:</strong>&nbsp;C&aacute;c chương tr&igrave;nh thạc sĩ tại Vương quốc Anh bao gồm một kh&oacute;a thực tập cho sinh vi&ecirc;n để c&oacute; được c&aacute;i nh&igrave;n s&acirc;u sắc v&agrave; kết nối v&ocirc; gi&aacute; trong ng&agrave;nh. Sinh vi&ecirc;n cũng được ph&eacute;p l&agrave;m việc với thị thực sinh vi&ecirc;n, với sự linh hoạt hơn được trao cho sinh vi&ecirc;n từ EU.</li>\r\n	<li><strong>Học bổng:</strong>&nbsp;Nhiều chương tr&igrave;nh học bổng d&agrave;nh cho sinh vi&ecirc;n quốc tế từ c&aacute;c trường đại học n&oacute;i ri&ecirc;ng v&agrave; sinh vi&ecirc;n từ c&aacute;c quốc gia Khối thịnh vượng n&oacute;i chung.</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#B22222\"><em><strong>CHỌN TRƯỜNG</strong></em></span></p>\r\n\r\n<p>Dưới đ&acirc;y l&agrave; một số điều quan trọng cần ch&uacute; &yacute; khi chọn trường đại học của bạn:</p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#2F4F4F\"><strong><em>Chương tr&igrave;nh học tập xuất sắc</em></strong>&nbsp;-</span> Nghi&ecirc;n cứu về đ&aacute;nh gi&aacute; v&agrave; gi&aacute;o sư giảng dạy c&oacute; thể cung cấp c&aacute;i nh&igrave;n s&acirc;u sắc c&oacute; gi&aacute; trị về chương tr&igrave;nh giảng dạy b&ecirc;n cạnh c&aacute;c m&ocirc; tả kh&oacute;a học. Một trải nghiệm tốt c&oacute; thể vượt ra khỏi ranh giới gi&aacute;o dục truyền thống v&agrave; cung cấp hỗ trợ giảng dạy tuyệt vời cho con đường bạn muốn theo đuổi.</li>\r\n	<li><span style=\"color:#2F4F4F\"><strong><em>Địa điểm</em></strong>&nbsp;- </span>C&oacute; một loạt c&aacute;c th&agrave;nh phố, thị trấn v&agrave; l&agrave;ng qu&ecirc; tr&ecirc;n khắp l&atilde;nh thổ của Vương quốc Anh, cụ thể l&agrave; Anh, Scotland, Bắc Ireland v&agrave; xứ Wales. Loại lối sống bạn muốn sẽ l&agrave; một yếu tố trong việc lựa chọn nơi bạn muốn học tập.</li>\r\n	<li><span style=\"color:#2F4F4F\"><strong><em>Học ph&iacute; v&agrave; học bổng</em></strong>&nbsp;</span>- Sinh vi&ecirc;n quốc tế ngo&agrave;i EU v&agrave; EEA (Khu vực kinh tế ch&acirc;u &Acirc;u) đ&oacute;ng học ph&iacute; đầy đủ tại c&aacute;c trường đại học Vương quốc Anh. Kiểm tra điều kiện nhận học bổng của bạn v&agrave; xem x&eacute;t việc thu&ecirc; một cố vấn c&oacute; kinh nghiệm để hướng dẫn bạn trong qu&aacute; tr&igrave;nh nộp đơn.</li>\r\n	<li><span style=\"color:#2F4F4F\"><strong><em>Chi ph&iacute; sinh hoạt</em></strong></span>&nbsp;- Lu&acirc;n Đ&ocirc;n v&agrave; c&aacute;c th&agrave;nh phố lớn kh&aacute;c c&oacute; xu hướng đắt đỏ hơn, nhưng cũng thuận tiện hơn về mặt h&agrave;ng ng&agrave;y của bạn.</li>\r\n	<li><span style=\"color:#2F4F4F\"><strong><em>Hỗ trợ sinh vi&ecirc;n quốc tế</em></strong>&nbsp;-</span> T&igrave;m một trường đại học cung cấp hỗ trợ theo kế hoạch tốt cho sinh vi&ecirc;n quốc tế, v&igrave; họ c&oacute; thể tư vấn về c&aacute;c yếu tố cần thiết như chỗ ở, ph&uacute;c lợi sinh vi&ecirc;n v&agrave; cơ hội việc l&agrave;m sau đại học.</li>\r\n</ul>\r\n\r\n<p><em>(Nguồn IDP)</em></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"color:#B22222\"><strong><em>&Acirc;U VIỆT &Uacute;C - IDP IELTS - DU HỌC ANH</em></strong></span></span></p>\r\n\r\n<p><a href=\"https://www.facebook.com/auvietucieltsduhoc\"><strong><em>https://www.facebook.com/auvietucieltsduhoc</em></strong></a></p>\r\n', 'du-hoc/Education-in-the-UK-840x430.jpg', 1, 1, '2021-02-24 04:09:24', '2021-02-24 04:09:24'),
(106, 'DROP IN SESSION 2021 - CHƯƠNG TRÌNH GIAO LƯU CÙNG GV BẢN NGỮ', 'drop-in-session-2021-chuong-trinh-giao-luu-cung-gv-ban-ngu', 'Tích hợp những hoạt động, tạo điều kiện cho các bạn học viên có cơ hội giao tiếp với Giáo viên nước ngoài ngoài chương trình học chính thức tại Trung tâm...', '<p style=\"margin-left:42.55pt; text-align:center\"><span style=\"font-size:18px\"><span style=\"color:#B22222\"><strong>CHƯƠNG TR&Igrave;NH GIAO LƯU C&Ugrave;NG GI&Aacute;O VI&Ecirc;N NƯỚC NGO&Agrave;I</strong></span></span></p>\r\n\r\n<p style=\"margin-left:42.55pt; text-align:center\"><span style=\"font-size:18px\"><span style=\"color:#B22222\"><strong>&quot;DROP IN SESSION&quot;</strong></span></span></p>\r\n\r\n<p style=\"margin-left:42.55pt; text-align:center\"><span style=\"font-size:18px\"><span style=\"color:#B22222\"><strong><img alt=\"\" src=\"/upload/images/Progression%20day/Drop%20in%20session%201.jpg\" style=\"height:990px; width:700px\" /></strong></span></span></p>\r\n\r\n<p style=\"margin-left:42.55pt; text-align:center\"><em>(Quy</em><em>&nbsp;định Tham gia Chương tr&igrave;nh Drop-in Session</em><em>&nbsp;202</em><em>1</em><em>)</em></p>\r\n\r\n<p style=\"margin-left:42.5500pt\">K&iacute;nh&nbsp;gửi: to&agrave;n thể Gi&aacute;o vi&ecirc;n, Nh&acirc;n vi&ecirc;n, Học vi&ecirc;n v&agrave; Qu&yacute; phụ huynh.</p>\r\n\r\n<p><em>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Trải qua hơn 2 năm th&agrave;nh c&ocirc;ng với t&acirc;m huyết đồng h&agrave;nh c&ugrave;ng học vi&ecirc;n để chinh phục tiếng Anh th&agrave;nh c&ocirc;ng, Trung t&acirc;m Ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c kh&ocirc;ng ngừng tạo ra m&ocirc;i trường học tập năng động v&agrave; cơ hội thực h&agrave;nh ng&ocirc;n ngữ cho tất cả c&aacute;c em, Chương tr&igrave;nh &ldquo;Drop-in </em><em>S</em><em>es</em><em>sion&rdquo;- tr</em><em>&ograve; chuyện nhanh với Gi&aacute;o vi&ecirc;n Nước ngo&agrave;i &ndash; được mở ra nhằm gi&uacute;p cho học vi&ecirc;n c&oacute; cơ hội tiếp x&uacute;c nhiều hơn v&agrave; sử dụng tiếng Anh một c&aacute;ch linh hoạt hơn, dạn dĩ v&agrave; tự tin hơn qua mỗi buổi tr&ograve; chuyện với Gi&aacute;o vi&ecirc;n người Nước ngo&agrave;i.</em></p>\r\n\r\n<p><em>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Qua c&aacute;c phản hồi t&iacute;ch cực v&agrave; sự y&ecirc;u mến d&agrave;nh cho chương tr&igrave;nh của c&aacute;c phụ huynh, hiện tại số lượng học vi&ecirc;n đăng k&yacute; tham gia ng&agrave;y c&agrave;ng tăng, đ&acirc;y l&agrave; dấu hiệu cho thấy chương tr&igrave;nh đ&atilde; c&oacute; những th&agrave;nh c&ocirc;ng nhất định.</em></p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Nay, Trung t&acirc;m Ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c xin th&ocirc;ng b&aacute;o một số thay đổi về c&aacute;ch thức v&agrave; Quy định tham gia chương tr&igrave;nh được <strong>&aacute;p dụng từ ng&agrave;y 01/03/2021</strong>&nbsp;như sau:</p>\r\n\r\n<p><span style=\"color:#B22222\"><em><strong>I. Th&ocirc;ng tin chung:</strong></em></span></p>\r\n\r\n<ol>\r\n	<li>Thời lượng đăng k&yacute;: <strong>10-15 ph&uacute;t/ học vi&ecirc;n/ buổi</strong></li>\r\n	<li><strong>Địa điểm</strong>: cơ sở 1 đường H&agrave; Ho&agrave;ng Hổ, Phường Đ&ocirc;ng Xuy&ecirc;n, TP. Long Xuy&ecirc;n, An Giang</li>\r\n	<li><strong>Thời gian chương tr&igrave;nh</strong>: <strong>Thứ Ba đến Thứ S&aacute;u</strong>&nbsp;h&agrave;ng tuần &ndash; từ <strong>16 giờ đến 17 giờ 45 ph&uacute;t</strong></li>\r\n	<li><strong>Điện thoại đăng k&yacute;</strong>: văn ph&ograve;ng Trung t&acirc;m:<strong>&nbsp;0296.3941.790 &ndash; Hotline: 0949.949.226 (Ms. Mỹ)</strong></li>\r\n	<li>Học vi&ecirc;n tham gia chương tr&igrave;nh <strong>bắt buộc</strong>&nbsp;phải đặt lịch trước 1 tuần.</li>\r\n</ol>\r\n\r\n<p><span style=\"color:#B22222\"><em><strong>II. Đối với học vi&ecirc;n c&oacute; thẻ Th&agrave;nh vi&ecirc;n:</strong></em></span></p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; Thời lượng đăng k&yacute; <strong>tối đa 2 buổi</strong>/ tuần.</p>\r\n\r\n<p><em><span style=\"color:#B22222\"><strong>III. Đối với Học vi&ecirc;n c&oacute; thẻ VIP</strong></span></em>,</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; Thời lượng đăng k&yacute; kh&ocirc;ng giới hạn số buổi trong thời gian chương tr&igrave;nh.</p>\r\n\r\n<p><span style=\"color:#B22222\"><em><strong>IV. Chi tiết về chương tr&igrave;nh</strong>,</em></span></p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp;Qu&yacute; Phụ huynh v&agrave; Học vi&ecirc;n vui l&ograve;ng li&ecirc;n hệ Ms. Mỹ Trịnh (DĐ: 0949.949.226) &ndash; Rất mong Qu&yacute; phụ huynh v&agrave; học vi&ecirc;n tiếp tục ủng hộ chương tr&igrave;nh để Trung t&acirc;m c&oacute; thể hỗ trợ tốt hơn cho học vi&ecirc;n, tối đa h&oacute;a chất lượng cho từng học vi&ecirc;n Trung t&acirc;m.</p>\r\n\r\n<p><span style=\"color:#B22222\"><span style=\"font-size:18px\"><strong><em>ANH NGỮ QUỐC TẾ &Acirc;U VIỆT &Uacute;C</em></strong></span></span></p>\r\n', 'Progression%20day/Drop%20in%20session%201.jpg', 1, 0, '2021-03-04 04:04:55', '2021-03-04 04:04:55'),
(107, 'LỄ PHÁT BẰNG CAMBRIDGE 2021', 'le-phat-bang-cambridge-2021', 'Buổi lễ vinh danh và trao chứng chỉ Quốc tế Cambridge cho 192 học viên xuất sắc trong kỳ thi Cambridge năm 2020...', '<p><em>Trung t&acirc;m Ngoại ngữ Quốc tế &Acirc;u Việt &Uacute;c th&ocirc;ng b&aacute;o về việc tổ chức buổi lễ ph&aacute;t bằng quốc tế Cambridge cho hơn 200 em học vi&ecirc;n xuất sắc 2020 gồm c&aacute;c cấp độ Starters - Movers - Flyers - KET - PET.</em></p>\r\n\r\n<p>EVAS rất tự h&agrave;o về kết quả kỳ thi năm nay, sau đợt nghỉ dịch kh&aacute; d&agrave;i, c&aacute;c em vẫn vững tin chinh phục kỳ thi với điểm số hầu như tuyệt đối, đặc biệt ở cấp độ KET - PET, c&aacute;c em đ&atilde; xuất sắc, kh&ocirc;ng chỉ mang về kết quả l&agrave; đạt m&agrave; c&aacute;c em c&ograve;n vượt ngưỡng thang ở chuẩn B1 - B2.</p>\r\n\r\n<p>C&ugrave;ng đ&oacute;n chờ gi&acirc;y ph&uacute;t vinh danh th&agrave;nh t&iacute;ch của c&aacute;c em.</p>\r\n\r\n<p>Sau khi kết th&uacute;c buổi lễ,sẽ c&oacute; buổi giao lưu, phỏng vấn c&ugrave;ng gi&aacute;o vi&ecirc;n nước ngo&agrave;i Ms. April, Mrs. Jubail,&amp; Mr. Veren. Hy vọng Qu&yacute; Phụ huynh chia sẻ thời gian v&agrave; tạo cơ hội để c&aacute;c em tự tin tham gia giao lưu c&ugrave;ng thầy c&ocirc;.</p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/Newone/Cambridge%20ceremony1.png\" style=\"width:600px\" /></p>\r\n\r\n<p>Trung t&acirc;m xin tr&acirc;n trọng th&ocirc;ng b&aacute;o đến Qu&yacute; Phụ huynh, Qu&yacute; Thầy C&ocirc; v&agrave; học vi&ecirc;n đến tham dự buổi lễ theo thời gian v&agrave; địa điểm như sau:</p>\r\n\r\n<ul>\r\n	<li>Hội trường 300A - Trường Đại Học An Giang</li>\r\n	<li>28.03.2021 l&uacute;c 1:30-5:00pm</li>\r\n	<li>Sự tham dự của Qu&yacute; phụ huynh như lời động vi&ecirc;n, khen ngợi cho c&aacute;c b&eacute; đ&atilde; th&agrave;nh c&ocirc;ng trong kỳ thi Cambridge v&agrave; g&oacute;p phần cho buổi lễ diễn ra th&agrave;nh c&ocirc;ng.</li>\r\n</ul>\r\n\r\n<p>Trung t&acirc;m xin k&iacute;nh mời Qu&yacute; thầy c&ocirc;, Qu&yacute; phụ huynh v&agrave; học vi&ecirc;n đến tham dự buổi lễ ph&aacute;t bằng Cambridge 2021.</p>\r\n\r\n<p><span style=\"color:#800000\"><span style=\"font-size:18px\"><strong><em>ANH NGỮ QUỐC TẾ &Acirc;U VIỆT &Uacute;C</em></strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Newone/Cambridge%20ceremony1.png', 1, 0, '2021-03-26 13:23:43', '2021-03-26 13:23:43');
