INSERT INTO `opinion` (`id`, `title`, `name`, `image`, `description`, `status`, `created_at`) VALUES
(4, 'Đáng tin cậy', 'Phụ Huynh bạn Tuyết Ngân (Chương trình Cambridge - KET)', 'Untitled-2.jpg', '\"Cháu theo học tại Trung tâm cũng được hai khóa, tôi cũng theo dõi sát việc học của cháu, và so với trước kia việc nói và phát âm cháu đã tiến bộ rất nhiều, cháu nói lưu loát hơn và phát âm chuẩn hơn.\"', 1, '2020-03-19 05:03:12'),
(5, 'Chất lượng', 'Phụ Huynh bạn Bảo Phúc (Chương trình Cambridge - KET)', 'ph2.jpg', '\"Bé học ở đây cũng khoảng 6 tháng nay rồi. Và từ khi bé vào học thì bé có sự tiến bộ rất là rõ kỹ năng nói, nghe, phát âm và giao tiếp với người nước ngoài thì bé có sự tự tin nhất định, chúng tôi mong muốn bé trau dồi nhiều hơn các kỹ năng ngoại ngữ để sau này bé sẽ tiếp cận với môi trường làm việc của người nước ngoài. Tôi tin tưởng nhà trường và tôi cũng xin cảm ơn trường Âu Việt Úc đã tạo điều kiện thuận lợi để cho bé học và hoc rất là tốt.\"', 1, '2020-03-17 08:45:45'),
(6, 'Tâm huyết', 'Phụ Huynh bạn Huỳnh Chi (Chương trình Cambridge - Starters)', 'ph1.jpg', '\"Mình thấy đội ngũ giáo viên trẻ tại trường rất năng động, dạy rất nhiệt tình, phương pháp dạy rất là mới nên kích thích rất lớn đến tính tích cực của các , bé của mình lúc mới học nhút nhát lắm, nhưng mà bây giờ thì bé nó rất là tự tin, rồi bé nó dạn dĩ nhiều lắm, phát âm cũng chuẩn xác hơn. Mình thấy là chỗ học đáng tin cậy nhất!\"', 1, '2020-03-17 08:43:46'),
(7, 'Tin cậy', 'Phụ Huynh bạn Minh Trí (Pre 2 - IELTS)', 'ph3.jpg', '\"Chị có hai bé, sau khi lựa chọn các trung tâm, chị đã quyết định chọn Trung tâm Âu Việt Úc, đối với bé nhỏ khi vào Trung tâm bé chỉ mới 6 tuổi, bé còn rất nhỏ, rất là nhút nhát, tưởng đâu là bé không thể theo kịp các bạn cùng lớp, nhưng sau khi vào học tại Trung tâm, được các cô dẫn dắt và hướng dẫn rất nhiệt tình, bé rất là vui và thích học tiếng Anh cho đến tận bây giờ, đã 5 năm bé theo học liên tục, bây giờ bé sắp vào lớp 6 và chị sẽ sẽ tiếp tục cho bé luyện IELTS đến cấp độ 6.0.\"', 1, '2020-03-19 05:03:34'),
(8, 'Đạt được kỳ vọng', 'Phụ Huynh bạn Đức Anh (Chương trình EVAS Stars - Superb Stars)', 'ph4.jpg', '\"Bé tham gia khóa học chỉ mới 8 tháng nhưng chị thấy bé có nhiều tiến bộ trong học tập, bé mạnh dạn hơn, về nhà năng động và chịu nói bằng tiếng Anh với các cô chú của bé cũng là giáo viên hơn rất nhiều, bé còn nói để bạn dạy cho chị học tiếng Anh.\"', 1, '2020-03-19 05:02:33');
INSERT INTO `philosophy` (`id`, `title`, `description`, `status`, `created_at`) VALUES
(3, 'Đảm bảo chất lượng đầu ra', 'Khung chương trình đào tạo được thiết kế riêng biệt bắt đầu từ việc xây dựng kết quả học tập theo mong đợi của học viên.', 1, '2020-03-12 10:36:18'),
(4, '100% Môi trường tiếng Anh', 'Tạo ra cơ hội tiếp cận, được dạy và học bằng ngôn ngữ Anh hoàn toàn 100%.', 1, '2020-03-12 09:38:03'),
(5, 'Phát triển toàn diện', 'Giúp các bạn nhỏ phát triển kỹ năng về ngôn ngữ cơ bản đến nâng cao và cả kỹ năng mềm trong cuộc sống.', 1, '2020-03-17 04:43:38'),
(6, 'Lấy học sinh làm trung tâm', 'Phương pháp dạy học dựa trên những hoạt động thuyết trình, nghiên cứu ý tưởng giúp các bạn chủ động hơn trong việc học, tự tin hơn, có trách nhiệm hơn và khám phá tiềm năng của chính mình.', 1, '2020-03-17 04:43:59'),
(7, 'Chiếm lĩnh kiến thức', 'Kết hợp chủ đề học và phương pháp giảng dạy với mục tiêu không chỉ để các em nắm vững kiến thức nền tảng, mà còn chiếm lĩnh tri thức đa lĩnh vực.', 1, '2020-03-12 11:34:11'),
(8, 'Hội nhập', 'Với nền tảng kiến thức được củng cố và xây dựng vững chắc theo từng buổi học, sẽ là bước chạy đà cho kế hoạch hội nhập vào môi trường giao dục quốc tế trong tương lai.', 1, '2020-03-12 11:38:37');
INSERT INTO `slider` (`id`, `title`, `image`, `description`, `link`, `status`, `sort`, `created_at`) VALUES
(4, 'MỪNG KHAI TRƯƠNG CƠ SỞ 3', 'tin-tuc/Banner%2028x6.jpg', 'Chào đón thành viên mới tại Hệ thống Đào tạo Anh ngữ Quốc Tế EVAS...\r\n    \r\n    \r\n      \r\n     \r\n       ', 'http://auvietuc.edu.vn/tin-tuc/mung-khai-truong-trung-tam-anh-ngu-au-viet-uc-thu-3', 0, 0, '2021-01-06 07:16:25'),
(5, 'Lễ Tốt Nghiệp Khi Vượt Cấp Độ', 'slider/Slide2.jpg', 'thường xuyên tổ chức các chương trình ngoại khóa ngoài trời giúp các bé thoải mái tiếp thu học hỏi một cách tự nhiên, tạo môi trường tốt nhất để các bé mạnh dạng giao tiếp nhiều hơn...', 'http://auvietuc.edu.vn/chuong-trinh-dao-tao', 1, 1, '2020-03-11 10:46:35'),
(6, 'Chương Trình Ngoại Khóa Thường Xuyên', 'Slide%206.jpg', 'Thường xuyên tổ chức các chương trình ngoại khóa ngoài trời giúp các bé thoải mái tiếp thu học hỏi một cách tự nhiên, tạo môi trường tốt nhất để các bé mạnh dạng giao tiếp nhiều hơn...', 'http://auvietuc.edu.vn/chuong-trinh-dao-tao', 1, 1, '2020-03-11 10:49:05'),
(7, 'Phương Pháp Học Hiệu Quả', '2020_02_16_00_36_IMG_7947.jpg', 'Lấy học sinh làm trung tâm, vì thế những buổi học được áp dụng những hoạt động thuyết trình, nghiên cứu ý tưởng dựa trên sự khám phá các chủ đề xung quanh các em...', 'http://auvietuc.edu.vn/tin-tuc', 1, 1, '2020-03-11 11:45:54'),
