INSERT INTO `study_space` (`id`, `title`, `code`, `description`, `detail`, `image`, `status`, `created_at`) VALUES
(10, 'THƯ VIỆN eBooks', 'thu-vien-ebooks', 'Thư viện sách điện tử (eBooks) miễn phí dành cho mọi học viên tại EVAS...', '<p><strong><em><span style=\"font-size:14px\">Thư viện bao gồm 2 danh mục sách và truyện bằng tiếng Anh dành cho từng lứa tuổi đang theo học các khóa học tương ứng tại Anh Ngữ Âu Việt Úc...</span></em></strong></p>\r\n\r\n<p><span style=\"color:#B22222\"><strong><em><span style=\"font-size:14px\">1. DANH MỤC TRUYỆN SÁCH CHO BÉ</span></em></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em><strong>&nbsp;<img alt=\"\" src=\"/upload/images/Library/children\'s%20books/Tiny-Talk-1B-SB.jpg\" style=\"height:177px; width:133px\" />&nbsp;<img alt=\"\" src=\"/upload/images/Library/children\'s%20books/tiny-talk-2a-student-book-bia.jpg\" style=\"height:178px; width:123px\" />&nbsp;<img alt=\"\" src=\"/upload/images/Library/children\'s%20books/15243.jpg\" style=\"height:177px; width:123px\" /></strong></em></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em><strong>Tiny talk 1 - 2 - 3 -&nbsp;</strong>Susan Rivers</em></span></p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/Library/children\'s%20books/780f64c0cf34c168d9bcd1df72f478ce.jpg\" style=\"height:158px; width:123px\" /></p>\r\n\r\n<p><span style=\"font-size:14px\"><em><strong>&nbsp;1000 Useful Words</strong></em></span></p>\r\n\r\n<p><img alt=\"\" src=\"/upload/images/Library/children\'s%20books/51K7jt-FhML.jpg\" style=\"height:175px; width:123px\" /></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;<strong><em>Funny Phonics Amp Amp Silly Spelling</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em><strong><img alt=\"\" src=\"/upload/images/Library/children\'s%20books/word-by-word-picture-dictionary-1-638.jpg\" style=\"height:159px; width:123px\" /></strong></em></span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;<strong><em>Word by Word&nbsp;Picture Dictionary</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong><em><img alt=\"\" src=\"